import { tags } from "typia";

/**
 * Records of system backup snapshots including timestamps, backup types,
 * storage locations, sizes, and status. Supports disaster recovery, compliance
 * auditing, and operational transparency across the multi-tenant LMS platform.
 *
 * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
 */
export type IEnterpriseLmsBackupRecords = {
  /** Primary Key. */
  id: string & tags.Format<"uuid">;

  /** Timestamp when the backup was taken. */
  backup_timestamp: string & tags.Format<"date-time">;

  /** Type of backup such as full, incremental, differential. */
  backup_type: string;

  /** Storage path or URL where the backup is stored. */
  storage_location: string;

  /** Size of the backup in bytes. */
  backup_size_bytes: number & tags.Type<"int32">;

  /** Current status of the backup, e.g., completed, failed, in_progress. */
  status: string;

  /** Record creation timestamp. */
  created_at: string & tags.Format<"date-time">;

  /** Record last update timestamp. */
  updated_at: string & tags.Format<"date-time">;
};
