/// etc.). Logs actionable business incidents, API-specific error codes,
/// exception stack, user/session trace. Each record stands independently for
/// error review, postmortem analysis, and quality assurance. Helps drive SLA
/// monitoring, platform reliability, and incident response.
///
/// @namespace Integration
/// @author AutoBE - https://github.com/wrtnlabs/autobe
model storyfield_ai_external_api_failures {
  //----
  // COLUMNS
  //----
  /// Primary Key.
  id String @id @db.Uuid

  /// Optional reference to the user involved in the failed request if
  /// applicable. {@link storyfield_ai_authenticatedusers.id}.
  storyfield_ai_authenticateduser_id String? @db.Uuid

  /// Optional reference to the story related to the API failure event if any.
  /// {@link storyfield_ai_stories.id}.
  storyfield_ai_story_id String? @db.Uuid

  /// Type of external API where the failure was detected (e.g., openai, asr,
  /// tts, spring_backend, s3, payment, etc.).
  api_type String

  /// API endpoint or resource path that triggered the failure event.
  endpoint String

  /// HTTP method used for the failed request (GET, POST, PUT, DELETE, etc.).
  http_method String

  /// API/system specific error code or HTTP status associated with this
  /// failure event.
  error_code String

  /// Detailed description or exception message for analysis. Allows full-text
  /// search for troubleshooting.
  error_message String?

  /// Request payload (usually JSON) sent to the API which caused the failure.
  /// Can be omitted for privacy/security if not allowed.
  request_payload String?

  /// Response body returned with the error/failure. Can be omitted for
  /// privacy/security if not allowed.
  response_payload String?

  /// How many retries attempted for this failed operation, including the
  /// initial request.
  retry_count Int @db.Integer

  /// Optional business/session group or distributed trace for associating
  /// failures across services.
  session_id String?

  /// Timestamp of API failure event record creation.
  created_at DateTime @db.Timestamptz

  /// Timestamp of last update to this record.
  updated_at DateTime @db.Timestamptz

  /// Null unless the failure record is soft deleted.
  deleted_at DateTime? @db.Timestamptz

  //----
  // RELATIONS
  //----
  authenticatedUser storyfield_ai_authenticatedusers? @relation(fields: [storyfield_ai_authenticateduser_id], references: [id], onDelete: Cascade, map: "storyfield_ai_external_api_failures_storyfield_ai_auth_38cf65ef")
  story storyfield_ai_stories? @relation(fields: [storyfield_ai_story_id], references: [id], onDelete: Cascade)

  @@index([storyfield_ai_authenticateduser_id], map: "storyfield_ai_external_api_failures_storyfield_ai_auth_10c6fc65")
  @@index([storyfield_ai_story_id])

  @@unique([session_id, api_type, created_at], map: "storyfield_ai_external_api_failures_session_id_api_typ_e7ff2d7f")
  @@index([api_type, created_at])
  @@index([http_method, api_type])
  @@index([error_code])
  @@index([error_message(ops: raw("gin_trgm_ops"))], type: Gin)
}

schemas.schema-05-systematic.prisma:
/// Service-wide system policy registry. Stores all business, technical,
/// feature toggle, and compliance rules as policy records for the StoryField
/// AI platform. Provides versioned policy management for runtime enforcement
/// and auditability. Used by service admin for global settings and
/// operational toggles. Independent management and change history required.
///
/// @namespace Systematic
/// @author AutoBE - https://github.com/wrtnlabs/autobe
model storyfield_ai_system_policies {
  //----
  // COLUMNS
  //----
  /// Primary Key.
  id String @id @db.Uuid

  /// Unique string code representing the policy for programmatic reference by
  /// subsystems (e.g., 'INTEGRATION_TIMEOUT_LIMIT', 'MAINTENANCE_MODE').
  policy_code String

  /// Human-friendly policy name for admin UI and documentation.
  name String

  /// Full description of the policy's business intent, operational effect, or
  /// compliance requirements.
  description String

  /// The policy's current value. Can be stringified JSON for complex types or
  /// scalar for simple toggles.
  value String

  /// The policy's data type (e.g., 'boolean', 'int', 'duration', 'json'). For
  /// informational/admin validation.
  type String

  /// Whether this policy is currently enforced (true = in effect, false = not
  /// enforced; allows for toggling).
  active Boolean

  /// Creation datetime for audit trail.
  created_at DateTime @db.Timestamptz

  /// Last updated datetime for policy modification tracking.
  updated_at DateTime @db.Timestamptz

  /// Soft delete timestamp. Null unless logically removed.
  deleted_at DateTime? @db.Timestamptz

  //----
  // RELATIONS
  //----
  @@unique([policy_code])
  @@index([active])
  @@index([created_at])
  @@index([name(ops: raw("gin_trgm_ops"))], type: Gin)
  @@index([description(ops: raw("gin_trgm_ops"))], type: Gin)
}

/// Application environment settings audit log. Tracks key=value
/// configuration for all distinct runtime environments (development,
/// staging, production). Captures environment variable versioning, change
/// origin, and historical context. Provides strong traceability for
/// configuration drift, issue diagnosis, and rollback decisions.
///
/// @namespace Systematic
/// @author AutoBE - https://github.com/wrtnlabs/autobe
model storyfield_ai_env_settings {
  //----
  // COLUMNS
  //----
  /// Primary Key.
  id String @id @db.Uuid

  /// Configuration key name. Matches a single environment variable or logical
  /// config item (e.g., 'OPENAI_API_KEY').
  env_key String

  /// Configuration value (mask sensitive data in UI).
  env_value String

  /// Environment label (e.g., 'local', 'development', 'staging', 'production').
  env_name String

  /// Identifier (username, email, or adminId) representing the actor who
  /// introduced the change.
  changed_by String

  /// Human-facing description of why this change was made (e.g., key rotation,
  /// urgent bug fix).
  change_reason String

  /// Datetime when this setting entry was created; marks effective date of
  /// config.
  created_at DateTime @db.Timestamptz

  /// Datetime of last update (for correction or rollover).
  updated_at DateTime @db.Timestamptz

  /// Soft delete timestamp for audit log. Null unless deleted logically.
  deleted_at DateTime? @db.Timestamptz

  //----
  // RELATIONS
  //----
  @@unique([env_key, env_name])
  @@index([env_name])
  @@index([created_at])
  @@index([change_reason(ops: raw("gin_trgm_ops"))], type: Gin)
}

/// Deployment and rollback event log for system operations. Records all
/// release, rollback, and hotfix actions for service version control, audit
/// traceability, and incident response. Enables review of deployment
/// outcome, initiator, timing, label, and associated summary for postmortems
/// and compliance review.
///
/// @namespace Systematic
/// @author AutoBE - https://github.com/wrtnlabs/autobe
model storyfield_ai_deployment_logs {
  //----
  // COLUMNS
  //----
  /// Primary Key.
  id String @id @db.Uuid

  /// Label identifying the deployment or rollback event. Should match the tag,
  /// branch, or semantic version (e.g., 'v1.10.7', 'bugfix-hot-deploy').
  deployment_label String

  /// Type of deployment action (e.g., 'deploy', 'rollback', 'hotfix',
  /// 'config-change').
  action_type String

  /// Deployment target environment (e.g., 'production', 'staging',
  /// 'development').
  environment String

  /// Identifier for the initiator (admin handle, CI/CD user, scriptID).
  initiated_by String

  /// Outcome status (e.g., 'success', 'failed', 'in-progress', 'aborted'); for
  /// workflow tracking and recovery.
  status String

  /// Free-form summary of the deployment action and impact. Used for
  /// compliance audit and operational history.
  summary String

  /// Datetime when deployment/rollback started.
  created_at DateTime @db.Timestamptz

  /// Datetime of last modification/status update.
  updated_at DateTime @db.Timestamptz

  /// Soft delete timestamp. Null unless logically removed from report view.
  deleted_at DateTime? @db.Timestamptz

  //----
  // RELATIONS
  //----
  @@unique([deployment_label, environment])
  @@index([environment])
  @@index([status])
  @@index([created_at])
  @@index([summary(ops: raw("gin_trgm_ops"))], type: Gin)
}

/// Centralized service alert and event notification log. Stores technical
/// event alerts (error, warning, info, recovery, anomaly, quota breach,
/// etc.) for both real-time and historical monitoring. Enables systematic
/// search by type, environment, resolution status, and content for incident
/// response and analytics. Independent querying and search required for
/// system health review.
///
/// @namespace Systematic
/// @author AutoBE - https://github.com/wrtnlabs/autobe
model storyfield_ai_service_alerts {
  //----
  // COLUMNS
  //----
  /// Primary Key.
  id String @id @db.Uuid

  /// Categorical alert/event type (e.g., 'error', 'warning', 'info', 'quota',
  /// 'incident', 'recovery').
  alert_type String

  /// Optional structured alert code, for business logic and filtering (e.g.,
  /// 'AUTH_401_012', 'S3_UPLOAD_FAIL').
  alert_code String

  /// Detailed description of the alert or event for admin review and
  /// downstream action. Used for analytics and audit.
  content String

  /// Target environment for the alert/event (e.g., 'production', 'staging').
  environment String

  /// Whether the alert/event has been resolved or closed (true = closed, false
  /// = active).
  resolved Boolean

  /// Human-facing note explaining how the alert/event was resolved or closed.
  /// Null until event is resolved.
  resolution_note String?

  /// Datetime when alert/event was recorded.
  created_at DateTime @db.Timestamptz

  /// Datetime of last update (e.g., resolution).
  updated_at DateTime @db.Timestamptz

  /// Soft delete timestamp. Null unless logically removed from view.
  deleted_at DateTime? @db.Timestamptz

  //----
  // RELATIONS
  //----
  @@index([alert_type])
  @@index([environment])
  @@index([resolved])
  @@index([created_at])
  @@index([content(ops: raw("gin_trgm_ops"))], type: Gin)
}

schemas.main.prisma:
generator client {
  provider        = "prisma-client-js"
  previewFeatures = ["postgresqlExtensions", "views"]
  binaryTargets   = ["native"]
}
datasource db {
  provider   = "postgresql"
  url        = env("DATABASE_URL")
  extensions = [pg_trgm]
}
generator markdown {
  provider = "prisma-markdown"
  output   = "../../docs/ERD.md"
}

schemas.schema-01-actors.prisma:
/// Holds registered authenticated StoryField AI users that have passed
/// Spring backend verification and possess a valid external user ID. Each
/// entry maps to a verified, onboarded user authorized to generate content
/// and access their own data only. Serves as the reference basis for all
/// user-generated content, TTS, and story traceability. Maintains audit
/// fields for creation, update, and soft deletion. Stance is primary, as
/// these users require independent business management, onboarding, and
/// compliance traceability.
///
/// @namespace Actors
/// @author AutoBE - https://github.com/wrtnlabs/autobe
model storyfield_ai_authenticatedusers {
  //----
  // COLUMNS
  //----
  /// Primary Key.
  id String @id @db.Uuid

  /// External user ID issued by Spring backend; unique per authenticated user.
  /// Used as a reference key to external identity system.
  external_user_id String

  /// Unique email address of authenticated user; must match email used for
  /// Spring registration.
  email String

  /// Describes the actor type in the business domain; always
  /// "authenticatedUser" for this table.
  actor_type String

  /// Timestamp of initial record creation. Used for traceability and
  /// compliance.
  created_at DateTime @db.Timestamptz

  /// Timestamp of last update to record. For audit tracking.
  updated_at DateTime @db.Timestamptz

  /// Timestamp when user soft-deleted (deactivated or removed, but not fully
  /// purged). Null if active.
  deleted_at DateTime? @db.Timestamptz

  //----
  // RELATIONS
  //----
  storyfield_ai_stories               storyfield_ai_stories[]
  storyfield_ai_token_sessions        storyfield_ai_token_sessions[]
  storyfield_ai_token_revocations     storyfield_ai_token_revocations[]
  storyfield_ai_auth_audit_logs       storyfield_ai_auth_audit_logs[]
  storyfield_ai_integration_logs      storyfield_ai_integration_logs[]
  storyfield_ai_s3_upload_histories   storyfield_ai_s3_upload_histories[]
  storyfield_ai_external_api_failures storyfield_ai_external_api_failures[]

  @@unique([external_user_id])
  @@unique([email])
  @@index([created_at])
  @@index([updated_at])
}

/// Records StoryField AI system administrative accounts. Each entry maps to
/// a separate admin, distinct from standard authenticated users, with
/// privilege for system oversight, abuse monitoring, and administrative
/// actions. Tracks audit and compliance fields, as well as last login date
/// and optional admin notes. Stance is primary due to independent admin
/// lifecycle management and business impact.
///
/// @namespace Actors
/// @author AutoBE - https://github.com/wrtnlabs/autobe
model storyfield_ai_systemadmins {
  //----
  // COLUMNS
  //----
  /// Primary Key.
  id String @id @db.Uuid

  /// External admin ID, mapped from Spring/JWT backend. Uniquely identifies an
  /// administrator for credential mapping and traceability.
  external_admin_id String

  /// Administrative email address, unique per admin and used for
  /// login/audit/policy notification.
  email String

  /// Role descriptor for actor type; always "systemAdmin" for this table.
  actor_type String

  /// Datetime of the admin's last successful login into the system. For
  /// privileged access tracking and compliance audits.
  last_login_at DateTime? @db.Timestamptz

  /// Optional notes regarding admin privileges, actions, or incident response
  /// logs. Enables enhanced compliance and system audit.
  admin_notes String?

  /// Timestamp of initial record creation. Audit/investigation baseline.
  created_at DateTime @db.Timestamptz

  /// Timestamp of last information update or privilege change for this admin
  /// account.
  updated_at DateTime @db.Timestamptz

  /// Timestamp of logical deletion (soft delete) if admin is removed from
  /// current oversight, null if active.
  deleted_at DateTime? @db.Timestamptz

  //----
  // RELATIONS
  //----
  storyfield_ai_token_sessions    storyfield_ai_token_sessions[]
  storyfield_ai_token_revocations storyfield_ai_token_revocations[]
  storyfield_ai_auth_audit_logs   storyfield_ai_auth_audit_logs[]

  @@unique([external_admin_id])
  @@unique([email])
  @@index([created_at])
  @@index([updated_at])
}

schemas.schema-02-stories.prisma:
/// Master table for AI-generated fairy tales. Each record represents a
/// complete story created by an authenticated user, with metadata such as
/// title, main plot, language, and creation details. Each story can have
/// multiple pages ({@link storyfield_ai_story_pages}), images ({@link
/// storyfield_ai_story_images}), and TTS results ({@link
/// storyfield_ai_tts_results}). Ownership is tracked by foreign key to
/// authenticated users. Supports soft deletion and audit trails for content
/// moderation, copyright disputes, and compliance.
///
/// @namespace Stories
/// @author AutoBE - https://github.com/wrtnlabs/autobe
model storyfield_ai_stories {
  //----
  // COLUMNS
  //----
  /// Primary Key.
  id String @id @db.Uuid

  /// Owner's {@link storyfield_ai_authenticatedusers.id}. The user who created
  /// this story.
  storyfield_ai_authenticateduser_id String @db.Uuid

  /// Title of the story as provided or generated by the user.
  title String

  /// The main plot or summary provided as the core input for this story.
  main_plot String?

  /// Primary language or dialect option chosen for this story instance (e.g.,
  /// Korean, Gyeongsang dialect).
  language String

  /// Record creation timestamp. Required for audit trail and ordering.
  created_at DateTime @db.Timestamptz

  /// Record last update time. Required for modification tracking.
  updated_at DateTime @db.Timestamptz

  /// Soft deletion time for GDPR and moderation compliance.
  deleted_at DateTime? @db.Timestamptz

  //----
  // RELATIONS
  //----
  authenticatedUser storyfield_ai_authenticatedusers @relation(fields: [storyfield_ai_authenticateduser_id], references: [id], onDelete: Cascade)

  storyfield_ai_story_pages           storyfield_ai_story_pages[]
  storyfield_ai_story_images          storyfield_ai_story_images[]
  storyfield_ai_tts_results           storyfield_ai_tts_results[]
  storyfield_ai_integration_logs      storyfield_ai_integration_logs[]
  storyfield_ai_s3_upload_histories   storyfield_ai_s3_upload_histories[]
  storyfield_ai_external_api_failures storyfield_ai_external_api_failures[]

  @@unique([storyfield_ai_authenticateduser_id, title], map: "storyfield_ai_stories_storyfield_ai_authenticateduser__b6d8f312")
  @@index([created_at])
  @@index([storyfield_ai_authenticateduser_id])
  @@index([deleted_at])
  @@index([title(ops: raw("gin_trgm_ops"))], type: Gin)
}

/// Stores the textual content and ordering for each page within a story.
/// Pages are independently addressable to allow specific moderation or user
/// revision (e.g., editing a single page). Connected to its parent story
/// ({@link storyfield_ai_stories.id}) and may have multiple supporting
/// images ({@link storyfield_ai_story_images}). Page number enforces logical
/// order. Soft deletion and temporal fields for compliance, moderation, and
/// granular auditing.
///
/// @namespace Stories
/// @author AutoBE - https://github.com/wrtnlabs/autobe
model storyfield_ai_story_pages {
  //----
  // COLUMNS
  //----
  /// Primary Key.
  id String @id @db.Uuid

  /// Reference to parent story's {@link storyfield_ai_stories.id}.
  storyfield_ai_story_id String @db.Uuid

  /// The sequential page number within the story. Used for natural ordering.
  page_number Int @db.Integer

  /// Textual content for the page. AI-generated or user-edited.
  text String

  /// Timestamp when the page was created.
  created_at DateTime @db.Timestamptz

  /// Last update time for the page.
  updated_at DateTime @db.Timestamptz

  /// Soft deletion for this page, supporting recoverability and compliance.
  deleted_at DateTime? @db.Timestamptz

  //----
  // RELATIONS
  //----
  story storyfield_ai_stories @relation(fields: [storyfield_ai_story_id], references: [id], onDelete: Cascade)

  storyfield_ai_story_images storyfield_ai_story_images[]
  storyfield_ai_tts_results  storyfield_ai_tts_results[]

  @@unique([storyfield_ai_story_id, page_number], map: "storyfield_ai_story_pages_storyfield_ai_story_id_page__76bf7564")
  @@index([storyfield_ai_story_id])
  @@index([deleted_at])
  @@index([text(ops: raw("gin_trgm_ops"))], type: Gin)
}

/// Stores generated or user-provided images associated with stories or
/// specific pages. Each image can be tied to a parent story, a specific
/// page, or both. Used to display illustrations within stories or as
/// thumbnails. Images references their storage (typically S3) by URI. Only
/// supporting entity; must always belong to a parent story and optionally a
/// page. Deleted via soft delete if parent is deleted.
///
/// @namespace Stories
/// @author AutoBE - https://github.com/wrtnlabs/autobe
model storyfield_ai_story_images {
  //----
  // COLUMNS
  //----
  /// Primary Key.
  id String @id @db.Uuid

  /// Parent story's {@link storyfield_ai_stories.id}.
  storyfield_ai_story_id String @db.Uuid

  /// Specific page's {@link storyfield_ai_story_pages.id} if image is tied to
  /// a page. Nullable for story-overall images.
  storyfield_ai_story_page_id String? @db.Uuid

  /// URI (usually S3 URL) of the image storage location.
  image_uri String @db.VarChar(80000)

  /// Optional alt text or description for accessibility or search.
  description String?

  /// When image was added or generated.
  created_at DateTime @db.Timestamptz

  /// Last modification time of image record.
  updated_at DateTime @db.Timestamptz

  /// Soft deletion timestamp. Image becomes inaccessible if set.
  deleted_at DateTime? @db.Timestamptz

  //----
  // RELATIONS
  //----
  story     storyfield_ai_stories      @relation(fields: [storyfield_ai_story_id], references: [id], onDelete: Cascade)
  storyPage storyfield_ai_story_pages? @relation(fields: [storyfield_ai_story_page_id], references: [id], onDelete: Cascade)

  @@index([storyfield_ai_story_id])
  @@index([storyfield_ai_story_page_id])
  @@index([deleted_at])
  @@index([description(ops: raw("gin_trgm_ops"))], type: Gin)
}

/// Stores results of text-to-speech generation for both entire stories and
/// specific pages. Each record contains the URI of the audio file, the text
/// source, and links to the owning story and, if relevant, the specific
/// page. Used to enable playback for users, accessibility features, and
/// dialect variation support. Not directly created by users, but is returned
/// or accessed as business artifacts of story creation. Soft delete
/// supported for data removal and compliance.
///
/// @namespace Stories
/// @author AutoBE - https://github.com/wrtnlabs/autobe
model storyfield_ai_tts_results {
  //----
  // COLUMNS
  //----
  /// Primary Key.
  id String @id @db.Uuid

  /// Story's {@link storyfield_ai_stories.id} for which this TTS was produced.
  storyfield_ai_story_id String @db.Uuid

  /// Story page's {@link storyfield_ai_story_pages.id} if TTS is for a
  /// specific page. Nullable for whole-story TTS.
  storyfield_ai_story_page_id String? @db.Uuid

  /// URI of the generated TTS audio file (e.g. S3 pre-signed URL).
  tts_audio_uri String @db.VarChar(80000)

  /// The text from which TTS was generated.
  source_text String

  /// Dialect or language option used in TTS result.
  dialect String

  /// When TTS was generated or uploaded.
  created_at DateTime @db.Timestamptz

  /// When record was last modified.
  updated_at DateTime @db.Timestamptz

  /// Soft deletion for regulatory and content-management purposes.
  deleted_at DateTime? @db.Timestamptz

  //----
  // RELATIONS
  //----
  story     storyfield_ai_stories      @relation(fields: [storyfield_ai_story_id], references: [id], onDelete: Cascade)
  storyPage storyfield_ai_story_pages? @relation(fields: [storyfield_ai_story_page_id], references: [id], onDelete: Cascade)

  @@index([storyfield_ai_story_id])
  @@index([storyfield_ai_story_page_id])
  @@index([dialect])
  @@index([deleted_at])
  @@index([source_text(ops: raw("gin_trgm_ops"))], type: Gin)
}

schemas.schema-03-auth.prisma:
/// Active authentication token/session registry. Tracks each unique issued
/// token, maps it to the authenticated user (or admin), records
/// device/fingerprint for session uniqueness, and manages timestamps for
/// creation, last activity, and (optional) soft-deletion. Used to validate
/// token presence for the full duration of session's allowed lifetime. Keys
/// token value hashes (not plain value), user link, and session fingerprint.
/// Critical for session verification and security audit.
///
/// @namespace Auth
/// @author AutoBE - https://github.com/wrtnlabs/autobe
model storyfield_ai_token_sessions {
  //----
  // COLUMNS
  //----
  /// Primary Key.
  id String @id @db.Uuid

  /// Belonged authenticated user's {@link storyfield_ai_authenticatedusers.id}.
  authenticated_user_id String? @db.Uuid

  /// Belonged system admin's {@link storyfield_ai_systemadmins.id}. Can be
  /// null for end-users.
  system_admin_id String? @db.Uuid

  /// Hashed authentication token value used for session validation (never raw
  /// token).
  token_hash String

  /// Device or browser fingerprint for session uniqueness and frictioned
  /// session control.
  fingerprint String

  /// Timestamp when token was issued.
  issued_at DateTime @db.Timestamptz

  /// Timestamp when token will expire.
  expires_at DateTime @db.Timestamptz

  /// Most recent refresh timestamp for this session, or null if never
  /// refreshed.
  refreshed_at DateTime? @db.Timestamptz

  /// Timestamp of last activity/proof of life for the session (token usage
  /// event).
  last_activity_at DateTime @db.Timestamptz

  /// Creation timestamp of session record (may match issued_at).
  created_at DateTime @db.Timestamptz

  /// Last update timestamp for record lifecycle tracking.
  updated_at DateTime @db.Timestamptz

  /// Soft delete timestamp, null unless the session is explicitly
  /// invalidated/closed prior to expiration.
  deleted_at DateTime? @db.Timestamptz

  //----
  // RELATIONS
  //----
  authenticatedUser storyfield_ai_authenticatedusers? @relation(fields: [authenticated_user_id], references: [id], onDelete: Cascade)
  systemAdmin       storyfield_ai_systemadmins?       @relation(fields: [system_admin_id], references: [id], onDelete: Cascade)

  storyfield_ai_token_revocations storyfield_ai_token_revocations[]
  storyfield_ai_auth_audit_logs   storyfield_ai_auth_audit_logs[]

  @@unique([token_hash])
  @@unique([authenticated_user_id, fingerprint], map: "storyfield_ai_token_sessions_authenticated_user_id_fin_7cbe655a")
  @@index([authenticated_user_id])
  @@index([system_admin_id])
  @@index([expires_at])
  @@index([last_activity_at])
}

/// Registry of forcibly-revoked or blacklisted tokens for security response
/// and audit. Each record logs a revoked token (by hash), context, related
/// session or user where detectable, and permanent timestamps. Used to deny
/// reused/compromised/invalid in-flight tokens regardless of their session
/// status. Non-deletable, immutable audit source.
///
/// @namespace Auth
/// @author AutoBE - https://github.com/wrtnlabs/autobe
model storyfield_ai_token_revocations {
  //----
  // COLUMNS
  //----
  /// Primary Key.
  id String @id @db.Uuid

  /// Related session's {@link storyfield_ai_token_sessions.id} if known. Null
  /// for external/sessionless revocations.
  token_session_id String? @db.Uuid

  /// Authenticated user's {@link storyfield_ai_authenticatedusers.id}.
  /// Nullable for sessionless revocations.
  authenticated_user_id String? @db.Uuid

  /// System admin's {@link storyfield_ai_systemadmins.id}. Nullable for
  /// userless admin or script-level actions.
  system_admin_id String? @db.Uuid

  /// Hash of revoked token (never store plain token) for future match and
  /// deny-list operations.
  token_hash String

  /// Administrative or automated reason for revocation (compromise, logout,
  /// abuse, etc.).
  revoked_reason String

  /// Source IP address from which revocation was requested/executed.
  revoked_by_ip String?

  /// Time of revocation record creation.
  created_at DateTime @db.Timestamptz

  //----
  // RELATIONS
  //----
  tokenSession      storyfield_ai_token_sessions?     @relation(fields: [token_session_id], references: [id], onDelete: Cascade)
