import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IStoryfieldAiAuthenticatedUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiAuthenticatedUser";

/**
 * Test the StoryField AI onboarding for an authenticatedUser using valid
 * external credentials.
 *
 * This test covers both success and error cases:
 *
 * - Successful registration with unique, verified external_user_id and
 *   business-unique email
 * - Duplicate registration attempts (same external_user_id or email) are rejected
 * - Invalid credentials and injection attempts are rejected with business errors
 *   (no type error testing)
 *
 * Steps:
 *
 * 1. Register a user with unique valid credentials.
 * 2. Validate response has correct fields, audit marks, and valid JWT token.
 * 3. Attempt registration with duplicate external_user_id -> expect business rule
 *    rejection.
 * 4. Attempt registration with duplicate email -> expect business rule rejection.
 * 5. Attempt registration with suspicious/injection strings in input -> expect
 *    rejection.
 */
export async function test_api_authenticated_user_registration_with_valid_external_id(
  connection: api.IConnection,
) {
  // 1. Register new authenticated user
  const external_user_id = RandomGenerator.alphaNumeric(24);
  const email = `${RandomGenerator.alphaNumeric(8)}@businessdomain.com`;
  const createBody = {
    external_user_id,
    email,
    actor_type: "authenticatedUser",
  } satisfies IStoryfieldAiAuthenticatedUser.ICreate;
  const session = await api.functional.auth.authenticatedUser.join(connection, {
    body: createBody,
  });
  typia.assert(session);
  TestValidator.equals(
    "external_user_id preservation",
    session.external_user_id,
    external_user_id,
  );
  TestValidator.equals("email preservation", session.email, email);
  TestValidator.equals(
    "actor_type must be authenticatedUser",
    session.actor_type,
    "authenticatedUser",
  );
  TestValidator.predicate(
    "audit created_at present",
    typeof session.created_at === "string" && !!session.created_at,
  );
  TestValidator.predicate(
    "audit updated_at present",
    typeof session.updated_at === "string" && !!session.updated_at,
  );
  typia.assert<IAuthorizationToken>(session.token);

  // 2. Error: Duplicate external_user_id
  await TestValidator.error("duplicate external_user_id rejected", async () => {
    await api.functional.auth.authenticatedUser.join(connection, {
      body: {
        external_user_id,
        email: `${RandomGenerator.alphaNumeric(8)}@businessdomain.com`,
        actor_type: "authenticatedUser",
      } satisfies IStoryfieldAiAuthenticatedUser.ICreate,
    });
  });
  // 3. Error: Duplicate email
  await TestValidator.error("duplicate email rejected", async () => {
    await api.functional.auth.authenticatedUser.join(connection, {
      body: {
        external_user_id: RandomGenerator.alphaNumeric(24),
        email,
        actor_type: "authenticatedUser",
      } satisfies IStoryfieldAiAuthenticatedUser.ICreate,
    });
  });
  // 4. Error: Invalid credentials (suspicious/injection strings)
  await TestValidator.error(
    "SQL injection-string external_user_id rejected",
    async () => {
      await api.functional.auth.authenticatedUser.join(connection, {
        body: {
          external_user_id: "'1 OR 1=1;--",
          email: `${RandomGenerator.alphaNumeric(8)}@businessdomain.com`,
          actor_type: "authenticatedUser",
        } satisfies IStoryfieldAiAuthenticatedUser.ICreate,
      });
    },
  );
  await TestValidator.error("malformed/injection email rejected", async () => {
    await api.functional.auth.authenticatedUser.join(connection, {
      body: {
        external_user_id: RandomGenerator.alphaNumeric(24),
        email: "<script>alert('xss')</script>",
        actor_type: "authenticatedUser",
      } satisfies IStoryfieldAiAuthenticatedUser.ICreate,
    });
  });
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IStoryfieldAiAuthenticatedUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiAuthenticatedUser";

/**
 * Validates the login operation for an already-registered authenticated user
 * using external_user_id and email.
 *
 * 1. Registers a new authenticatedUser with unique external_user_id and email.
 * 2. Performs login with correct credentials, validates issued session token, and
 *    ensures token is bound to the user.
 * 3. Verifies business logic by attempting login with unregistered/malformed
 *    credentials (no type error tests).
 * 4. Confirms no session tokens are issued and appropriate errors are returned for
 *    invalid login attempts.
 *
 * (Soft-deleted account scenario is not implemented as no such API is
 * available.)
 */
export async function test_api_authenticated_user_login_session_issuance(
  connection: api.IConnection,
) {
  // 1. Register an authenticated user
  const external_user_id = RandomGenerator.alphaNumeric(12);
  const email = `${RandomGenerator.alphabets(8)}@business-domain.com`;
  const createBody = {
    external_user_id,
    email,
    actor_type: "authenticatedUser",
  } satisfies IStoryfieldAiAuthenticatedUser.ICreate;
  const registeredUser = await api.functional.auth.authenticatedUser.join(
    connection,
    { body: createBody },
  );
  typia.assert(registeredUser);
  TestValidator.equals(
    "registered external_user_id matches",
    registeredUser.external_user_id,
    external_user_id,
  );
  TestValidator.equals("registered email matches", registeredUser.email, email);
  TestValidator.equals(
    "registered actor_type is 'authenticatedUser'",
    registeredUser.actor_type,
    "authenticatedUser",
  );
  TestValidator.predicate(
    "token is present on registration",
    typeof registeredUser.token.access === "string" &&
      registeredUser.token.access.length > 10,
  );
  // 2. Login with the same external_user_id and email
  const loginBody = {
    external_user_id,
    email,
  } satisfies IStoryfieldAiAuthenticatedUser.ILogin;
  const loginResult = await api.functional.auth.authenticatedUser.login(
    connection,
    { body: loginBody },
  );
  typia.assert(loginResult);
  TestValidator.equals(
    "login external_user_id matches",
    loginResult.external_user_id,
    registeredUser.external_user_id,
  );
  TestValidator.equals(
    "login email matches",
    loginResult.email,
    registeredUser.email,
  );
  TestValidator.equals(
    "login actor_type is 'authenticatedUser'",
    loginResult.actor_type,
    "authenticatedUser",
  );
  TestValidator.predicate(
    "login token is present",
    typeof loginResult.token.access === "string" &&
      loginResult.token.access.length > 10,
  );
  TestValidator.notEquals(
    "login issued new access token",
    loginResult.token.access,
    registeredUser.token.access,
  );
  // 3. Attempt login with unregistered (random) credentials
  const badLoginBody = {
    external_user_id: RandomGenerator.alphaNumeric(12),
    email: `notexisting_${RandomGenerator.alphabets(6)}@biz.com`,
  } satisfies IStoryfieldAiAuthenticatedUser.ILogin;
  await TestValidator.error("login with unregistered user fails", async () => {
    await api.functional.auth.authenticatedUser.login(connection, {
      body: badLoginBody,
    });
  });
  // Note: Type error/missing-field tests are intentionally omitted because those scenarios are not permitted by TypeScript and are forbidden.
  // Soft-deletion business error not implemented due to lack of such API.
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IStoryfieldAiAuthenticatedUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiAuthenticatedUser";

/**
 * Test the session refresh lifecycle for an authenticatedUser, ensuring that a
 * valid session and non-revoked refresh token are required to obtain a new
 * token. Scenario begins with user signup and login to obtain the necessary
 * session. Validate that the refresh endpoint returns a new session with
 * updated claims and tokens, and enforces that only active, non-revoked
 * sessions tied to active users (not soft deleted) are eligible. Simulate token
 * expiration, explicit revocation, or user deletion to validate negative/error
 * responses for refresh under these edge cases. Confirm audit trail for
 * successful and failed refresh attempts where possible within API limits.
 *
 * 1. Register a new authenticated user via join endpoint
 * 2. Login as that user to obtain session/access/refresh tokens
 * 3. Call refresh endpoint using that refresh token to get new tokens/session
 * 4. Validate that returned session is correct (claims, tokens)
 * 5. Simulate reusing old/consumed refresh token and expect error (if supported)
 * 6. Simulate explicit revocation, expiration, or user deletion to confirm failure
 *    (if supported)
 * 7. Confirm audit trail for refresh attempts if auditing is exposed (not
 *    currently testable)
 */
export async function test_api_authenticated_user_token_refresh_lifecycle(
  connection: api.IConnection,
) {
  // 1. Register a new authenticated user
  const externalUserId: string = RandomGenerator.alphaNumeric(12);
  const userEmail: string = `${RandomGenerator.alphabets(8)}@test-refresh.com`;
  const join: IStoryfieldAiAuthenticatedUser.IAuthorized =
    await api.functional.auth.authenticatedUser.join(connection, {
      body: {
        external_user_id: externalUserId,
        email: userEmail,
        actor_type: "authenticatedUser",
      } satisfies IStoryfieldAiAuthenticatedUser.ICreate,
    });
  typia.assert(join);

  // 2. Login with the just registered user
  const login: IStoryfieldAiAuthenticatedUser.IAuthorized =
    await api.functional.auth.authenticatedUser.login(connection, {
      body: {
        external_user_id: externalUserId,
        email: userEmail,
      } satisfies IStoryfieldAiAuthenticatedUser.ILogin,
    });
  typia.assert(login);

  // Validate token/claims between join and login
  TestValidator.equals(
    "Join and login issued for same user",
    join.id,
    login.id,
  );
  TestValidator.equals("Join and login email match", join.email, login.email);
  TestValidator.equals(
    "Join and login external IDs match",
    join.external_user_id,
    login.external_user_id,
  );

  // 3. Call refresh endpoint with the refresh token from login
  // The API SDK sets connection.headers.Authorization automatically, so prior call's token is active session
  const refresh1: IStoryfieldAiAuthenticatedUser.IAuthorized =
    await api.functional.auth.authenticatedUser.refresh(connection, {
      body: {},
    });
  typia.assert(refresh1);

  // Validate new session returned, proper structure and token update
  TestValidator.equals("Refresh user id matches login", refresh1.id, login.id);
  TestValidator.equals(
    "Refresh user email matches login",
    refresh1.email,
    login.email,
  );
  TestValidator.notEquals(
    "Access token after refresh differs from original login",
    refresh1.token.access,
    login.token.access,
  );
  TestValidator.notEquals(
    "Refresh token after refresh differs from original login",
    refresh1.token.refresh,
    login.token.refresh,
  );

  // 4. Attempt to call refresh again immediately (simulate fast replay, if backend allows)
  const refresh2: IStoryfieldAiAuthenticatedUser.IAuthorized =
    await api.functional.auth.authenticatedUser.refresh(connection, {
      body: {},
    });
  typia.assert(refresh2);
  TestValidator.notEquals(
    "Multiple sequential refreshes yield new tokens",
    refresh2.token.access,
    refresh1.token.access,
  );
  TestValidator.notEquals(
    "Multiple sequential refreshes yield new refresh tokens",
    refresh2.token.refresh,
    refresh1.token.refresh,
  );

  // 5. Attempt to refresh after disconnecting session (simulate invalidation). No explicit API to revoke so cannot test true revocation, but can create a new connection without token and expect error
  const unauthConn: api.IConnection = { ...connection, headers: {} };
  await TestValidator.error(
    "Cannot refresh without Authorization header (unauthenticated attempt)",
    async () => {
      await api.functional.auth.authenticatedUser.refresh(unauthConn, {
        body: {},
      });
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IStoryfieldAiSystemAdmin } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiSystemAdmin";

/**
 * End-to-end onboarding of a StoryField AI system admin using externally
 * validated credentials.
 *
 * Steps:
 *
 * 1. Register a new system admin with a unique external_admin_id and valid
 *    business email.
 * 2. Ensure the system returns required identity fields (uuid, email,
 *    external_admin_id, actor_type), valid token, timestamps (created_at,
 *    updated_at as ISO8601).
 * 3. Confirm optional fields (last_login_at, admin_notes, deleted_at) are
 *    nullable/optional.
 * 4. Attempt to re-register with the same external_admin_id and/or email and
 *    expect API rejection (uniqueness enforced).
 * 5. Register with minimum required fields (actor_type is optional) and confirm
 *    default is 'systemAdmin' in the response.
 */
export async function test_api_system_admin_registration_with_valid_external_admin_id(
  connection: api.IConnection,
) {
  // 1. Generate unique admin IDs and emails
  const externalAdminId: string = RandomGenerator.alphaNumeric(16);
  const uniqueEmail: string = `${RandomGenerator.alphaNumeric(8)}@e2e.test.com`;

  // 2. Register new admin (with explicit actor_type)
  const joinInput = {
    external_admin_id: externalAdminId,
    email: uniqueEmail,
    actor_type: "systemAdmin",
  } satisfies IStoryfieldAiSystemAdmin.IJoin;
  const admin = await api.functional.auth.systemAdmin.join(connection, {
    body: joinInput,
  });
  typia.assert(admin);
  TestValidator.equals(
    "external_admin_id matches input",
    admin.external_admin_id,
    externalAdminId,
  );
  TestValidator.equals("email matches input", admin.email, uniqueEmail);
  TestValidator.equals(
    "actor_type always systemAdmin",
    admin.actor_type,
    "systemAdmin",
  );

  // 3. Duplicate registration (external_admin_id, new email): should fail
  const anotherEmail: string = `${RandomGenerator.alphaNumeric(8)}@e2e.test.com`;
  await TestValidator.error(
    "duplicate external_admin_id should fail",
    async () => {
      await api.functional.auth.systemAdmin.join(connection, {
        body: {
          external_admin_id: externalAdminId,
          email: anotherEmail,
          actor_type: "systemAdmin",
        },
      });
    },
  );

  // 4. Duplicate registration (new external_admin_id, same email): should fail
  const anotherExternalId: string = RandomGenerator.alphaNumeric(16);
  await TestValidator.error("duplicate email should fail", async () => {
    await api.functional.auth.systemAdmin.join(connection, {
      body: {
        external_admin_id: anotherExternalId,
        email: uniqueEmail,
        actor_type: "systemAdmin",
      },
    });
  });

  // 5. Register admin omitting optional actor_type (should default to 'systemAdmin')
  const minimalInput = {
    external_admin_id: RandomGenerator.alphaNumeric(16),
    email: `${RandomGenerator.alphaNumeric(8)}@e2e.test.com`,
  } satisfies IStoryfieldAiSystemAdmin.IJoin;
  const adminMinimal = await api.functional.auth.systemAdmin.join(connection, {
    body: minimalInput,
  });
  typia.assert(adminMinimal);
  TestValidator.equals(
    "actor_type defaults to systemAdmin",
    adminMinimal.actor_type,
    "systemAdmin",
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IStoryfieldAiS3UploadHistory } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiS3UploadHistory";
import type { IStoryfieldAiSystemAdmin } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiSystemAdmin";

/**
 * Validate retrieval of S3 upload history detail as system admin, and check
 * audit/authorization boundaries.
 *
 * 1. Register and log in as system admin (for authenticated privilege).
 * 2. Simulate a valid S3 upload history record (use typia.random to generate test
 *    UUID and simulate existence).
 * 3. Successfully retrieve the S3 upload history record detail as authenticated
 *    admin; validate all contract fields and masking business logic.
 * 4. Attempt retrieval of a non-existent S3 upload history record (random UUID);
 *    expect error.
 * 5. Logout or use unauthenticated connection; attempt access, expect error.
 */
export async function test_api_s3_upload_history_detail_view_success_and_authorization(
  connection: api.IConnection,
) {
  // 1. Register a new admin
  const adminJoinBody = {
    external_admin_id: RandomGenerator.alphaNumeric(20),
    email: `${RandomGenerator.alphabets(8)}@company.com`,
    actor_type: "systemAdmin",
  } satisfies IStoryfieldAiSystemAdmin.IJoin;
  const admin = await api.functional.auth.systemAdmin.join(connection, {
    body: adminJoinBody,
  });
  typia.assert(admin);

  // 2. Generate a random UUID for the S3 upload history (simulated record)
  const s3UploadHistoryId: string & tags.Format<"uuid"> = typia.random<
    string & tags.Format<"uuid">
  >();

  // 3. Retrieve upload history record successfully as admin
  const s3UploadHistory =
    await api.functional.storyfieldAi.systemAdmin.s3UploadHistories.at(
      connection,
      {
        s3UploadHistoryId,
      },
    );
  typia.assert(s3UploadHistory);
  TestValidator.predicate(
    "admin can retrieve s3 upload history record with expected file/audit/event fields",
    s3UploadHistory.id === s3UploadHistoryId,
  );
  TestValidator.predicate(
    "file_size is positive integer",
    typeof s3UploadHistory.file_size === "number" &&
      s3UploadHistory.file_size > 0,
  );
  TestValidator.predicate(
    "media_type is non-empty",
    typeof s3UploadHistory.media_type === "string" &&
      s3UploadHistory.media_type.length > 0,
  );
  TestValidator.predicate(
    "filename is non-empty",
    typeof s3UploadHistory.filename === "string" &&
      s3UploadHistory.filename.length > 0,
  );

  // 4. Attempt retrieval with non-existent UUID -- expect error
  const nonExistentId = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.error(
    "should error on non-existent s3 upload history record",
    async () => {
      await api.functional.storyfieldAi.systemAdmin.s3UploadHistories.at(
        connection,
        { s3UploadHistoryId: nonExistentId },
      );
    },
  );

  // 5. Attempt retrieval without authentication
  const unauthConn: api.IConnection = { ...connection, headers: {} };
  await TestValidator.error(
    "should error when not authenticated as system admin",
    async () => {
      await api.functional.storyfieldAi.systemAdmin.s3UploadHistories.at(
        unauthConn,
        { s3UploadHistoryId },
      );
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";
import type { IPageIStoryfieldAiExternalApiFailure } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIStoryfieldAiExternalApiFailure";
import type { IStoryfieldAiExternalApiFailure } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiExternalApiFailure";
import type { IStoryfieldAiSystemAdmin } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiSystemAdmin";

/**
 * E2E test for /storyfieldAi/systemAdmin/externalApiFailures search,
 * filter, pagination, and access control.
 *
 * This test validates that a system administrator can:
 *
 * 1. Register and login for privileged operations
 * 2. Search and filter external API failure records using various criteria
 *    (api_type, error_code, date range, user/story refs)
 * 3. Handle over-broad or invalid filters gracefully (receives no/empty
 *    results as expected)
 * 4. Enforce and verify pagination limits and page counts
 * 5. Ensure unauthorized (non-systemAdmin) users are denied access to this
 *    endpoint
 * 6. All assertions use strict type-safety and business logic validation.
 */
export async function test_api_external_api_failure_search_with_filters_and_pagination(
  connection: api.IConnection,
) {
  // 1. Register a new system admin and login
  const externalAdminId: string = RandomGenerator.alphaNumeric(12);
  const adminEmail: string = `${RandomGenerator.name(1)}_${RandomGenerator.alphaNumeric(6)}@company.com`;
  const joinBody = {
    external_admin_id: externalAdminId,
    email: adminEmail,
    actor_type: "systemAdmin",
  } satisfies IStoryfieldAiSystemAdmin.IJoin;
  const adminAuth = await api.functional.auth.systemAdmin.join(connection, {
    body: joinBody,
  });
  typia.assert(adminAuth);

  // Login (to ensure token/session is present)
  const loginBody = {
    external_admin_id: externalAdminId,
    email: adminEmail,
  } satisfies IStoryfieldAiSystemAdmin.ILogin;
  const loginAuth = await api.functional.auth.systemAdmin.login(connection, {
    body: loginBody,
  });
  typia.assert(loginAuth);
  TestValidator.equals(
    "admin email is correct after login",
    loginAuth.email,
    adminAuth.email,
  );

  // 2. Normal search for all failures (no filters, default pagination)
  const allFailures =
    await api.functional.storyfieldAi.systemAdmin.externalApiFailures.index(
      connection,
      { body: {} satisfies IStoryfieldAiExternalApiFailure.IRequest },
    );
  typia.assert(allFailures);
  TestValidator.predicate(
    "external API failures is array (data present or empty)",
    Array.isArray(allFailures.data),
  );

  // If there is data, pick a sample failure to use for filtered searches
  let sampleFailure = undefined;
  if (allFailures.data.length > 0) {
    sampleFailure = RandomGenerator.pick(allFailures.data);

    // 3. Filter by api_type, error_code, and date range
    const filterBody = {
      api_type: sampleFailure.api_type,
      error_code: sampleFailure.error_code,
      created_from: sampleFailure.created_at,
      created_to: sampleFailure.created_at,
      page: 0,
      limit: 5,
    } satisfies IStoryfieldAiExternalApiFailure.IRequest;
    const filteredFailures =
      await api.functional.storyfieldAi.systemAdmin.externalApiFailures.index(
        connection,
        { body: filterBody },
      );
    typia.assert(filteredFailures);
    for (const result of filteredFailures.data) {
      TestValidator.equals(
        "filter matches api_type",
        result.api_type,
        filterBody.api_type,
      );
      TestValidator.equals(
        "filter matches error_code",
        result.error_code,
        filterBody.error_code,
      );
      TestValidator.predicate(
        "created_at within filter",
        result.created_at >= filterBody.created_from! &&
          result.created_at <= filterBody.created_to!,
      );
    }
  }

  // 4. Filter where result is expected to be empty (nonsense error_code)
  const impossibleFilter = {
    error_code: "___NONEXISTENT___",
    page: 0,
    limit: 3,
  } satisfies IStoryfieldAiExternalApiFailure.IRequest;
  const emptyResult =
    await api.functional.storyfieldAi.systemAdmin.externalApiFailures.index(
      connection,
      { body: impossibleFilter },
    );
  typia.assert(emptyResult);
  TestValidator.equals(
    "empty data for nonsensical error_code",
    emptyResult.data.length,
    0,
  );

  // 5. Pagination boundary: large limit (should be capped), out-of-bounds page
  const pagedResult =
    await api.functional.storyfieldAi.systemAdmin.externalApiFailures.index(
      connection,
      {
        body: {
          page: 10000,
          limit: 100,
        } satisfies IStoryfieldAiExternalApiFailure.IRequest,
      },
    );
  typia.assert(pagedResult);
  TestValidator.predicate(
    "page does not exceed total possible pages",
    pagedResult.pagination.current <= pagedResult.pagination.pages,
  );
  TestValidator.predicate(
    "limit does not exceed allowed maximum",
    pagedResult.pagination.limit <= 100,
  );

  // 6. REQUIRED field validation (body param must exist)
  // Skipped: TypeScript prohibits omitting required parameters; scenario not possible in valid code

  // 7. Unauthorized access (no admin token)
  const unauthConn: api.IConnection = { ...connection, headers: {} };
  await TestValidator.error(
    "endpoint denies access to unauthenticated call",
    async () => {
      await api.functional.storyfieldAi.systemAdmin.externalApiFailures.index(
        unauthConn,
        { body: {} satisfies IStoryfieldAiExternalApiFailure.IRequest },
      );
    },
  );
}

