import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IStoryfieldAiEnvSetting } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiEnvSetting";
import type { IStoryfieldAiSystemAdmin } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiSystemAdmin";

/**
 * Validate that only system admins can create environment settings with correct
 * field persistence and business logic error handling.
 *
 * 1. Register system admin
 * 2. Login as admin
 * 3. Create valid environment setting and confirm persistence
 * 4. Error: duplicate key in same environment, oversize value
 * 5. Attempt creation without/with wrong auth context and expect rejection
 */
export async function test_api_env_setting_creation_by_admin(
  connection: api.IConnection,
) {
  // 1. Register a system admin
  const external_admin_id = RandomGenerator.alphaNumeric(12);
  const admin_email = `${RandomGenerator.alphaNumeric(8)}@company.com`;
  const join_result = await api.functional.auth.systemAdmin.join(connection, {
    body: {
      external_admin_id,
      email: admin_email,
      actor_type: "systemAdmin",
    },
  });
  typia.assert(join_result);
  TestValidator.equals("actor_type", join_result.actor_type, "systemAdmin");

  // 2. Login as admin
  const login_result = await api.functional.auth.systemAdmin.login(connection, {
    body: { external_admin_id, email: admin_email },
  });
  typia.assert(login_result);

  // 3. Create a valid environment setting
  const valid_env_key = RandomGenerator.alphaNumeric(14).toUpperCase();
  const valid_env_value = RandomGenerator.alphaNumeric(32);
  const valid_env_name = RandomGenerator.pick([
    "development",
    "staging",
    "production",
    "local",
  ] as const);
  const valid_change_reason = RandomGenerator.paragraph({ sentences: 6 });
  const createBody = {
    env_key: valid_env_key,
    env_value: valid_env_value,
    env_name: valid_env_name,
    changed_by: admin_email,
    change_reason: valid_change_reason,
  } satisfies IStoryfieldAiEnvSetting.ICreate;
  const setting =
    await api.functional.storyfieldAi.systemAdmin.envSettings.create(
      connection,
      {
        body: createBody,
      },
    );
  typia.assert(setting);
  TestValidator.equals("env_key persistence", setting.env_key, valid_env_key);
  TestValidator.equals(
    "env_value persistence",
    setting.env_value,
    valid_env_value,
  );
  TestValidator.equals(
    "env_name persistence",
    setting.env_name,
    valid_env_name,
  );
  TestValidator.equals(
    "changed_by persistence",
    setting.changed_by,
    admin_email,
  );
  TestValidator.equals(
    "change_reason persistence",
    setting.change_reason,
    valid_change_reason,
  );

  // 4. business logic error: duplicate key in same environment
  await TestValidator.error(
    "duplicate env_key in same env_name fails",
    async () => {
      await api.functional.storyfieldAi.systemAdmin.envSettings.create(
        connection,
        {
          body: {
            env_key: valid_env_key,
            env_value: RandomGenerator.alphaNumeric(24),
            env_name: valid_env_name,
            changed_by: admin_email,
            change_reason: "duplicate key",
          } satisfies IStoryfieldAiEnvSetting.ICreate,
        },
      );
    },
  );
  // Exceed allowed value size (simulate with oversize value for env_value)
  await TestValidator.error("oversized env_value fails", async () => {
    await api.functional.storyfieldAi.systemAdmin.envSettings.create(
      connection,
      {
        body: {
          env_key: RandomGenerator.alphaNumeric(14).toUpperCase(),
          env_value: RandomGenerator.alphaNumeric(4096 * 2), // Suppose 4K limit, try larger
          env_name: "development",
          changed_by: admin_email,
          change_reason: "too long value",
        } satisfies IStoryfieldAiEnvSetting.ICreate,
      },
    );
  });

  // 5. Attempt creation with missing or insufficient auth - expect rejection
  const unauth_connection = { ...connection, headers: {} };
  await TestValidator.error("without auth context fails", async () => {
    await api.functional.storyfieldAi.systemAdmin.envSettings.create(
      unauth_connection,
      {
        body: createBody,
      },
    );
  });
  // Simulated insufficient role is not possible (only systemAdmin join/login exists in this environment for role switching)
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IStoryfieldAiEnvSetting } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiEnvSetting";
import type { IStoryfieldAiSystemAdmin } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiSystemAdmin";

/**
 * Validates that a system administrator can update an environment setting by
 * envSettingId.
 *
 * Steps:
 *
 * 1. Register and authenticate as system admin.
 * 2. Create a new environment setting (to obtain valid envSettingId).
 * 3. Update fields (env_value, env_name, changed_by, change_reason) with PUT,
 *    verifying audit and persistence.
 * 4. Ensure changes are correctly reflected (env_value, env_name, change_reason,
 *    changed_by updated, updated_at advanced, created_at unchanged).
 * 5. Check business error cases:
 *
 *    - Updating with invalid envSettingId returns error
 *    - Updating with empty body returns error
 *    - Unauthorized/unauthenticated update returns error
 * 6. Confirm response schema and audit fields behave as expected.
 */
export async function test_api_env_setting_update_by_admin(
  connection: api.IConnection,
) {
  // 1. Register as system admin
  const externalAdminId = RandomGenerator.alphaNumeric(12);
  const adminEmail = RandomGenerator.name(2).replace(" ", ".") + "@company.com";
  const joinBody = {
    external_admin_id: externalAdminId,
    email: adminEmail,
    actor_type: "systemAdmin",
  } satisfies IStoryfieldAiSystemAdmin.IJoin;
  const admin = await api.functional.auth.systemAdmin.join(connection, {
    body: joinBody,
  });
  typia.assert(admin);

  // (authenticate again to ensure login flow is well-tested)
  const loginBody = {
    external_admin_id: externalAdminId,
    email: adminEmail,
  } satisfies IStoryfieldAiSystemAdmin.ILogin;
  const auth = await api.functional.auth.systemAdmin.login(connection, {
    body: loginBody,
  });
  typia.assert(auth);

  // 2. Create a new env setting
  const createBody = {
    env_key: ("SPRING_S3_URL_" + RandomGenerator.alphaNumeric(8)).toUpperCase(),
    env_value: RandomGenerator.alphaNumeric(32),
    env_name: RandomGenerator.pick([
      "local",
      "development",
      "staging",
      "production",
    ] as const),
    changed_by: adminEmail,
    change_reason: RandomGenerator.paragraph(),
  } satisfies IStoryfieldAiEnvSetting.ICreate;
  const envSetting =
    await api.functional.storyfieldAi.systemAdmin.envSettings.create(
      connection,
      { body: createBody },
    );
  typia.assert(envSetting);
  TestValidator.equals(
    "env_key as input",
    envSetting.env_key,
    createBody.env_key,
  );
  TestValidator.equals(
    "env_value as input",
    envSetting.env_value,
    createBody.env_value,
  );
  TestValidator.equals(
    "changed_by as input",
    envSetting.changed_by,
    createBody.changed_by,
  );
  TestValidator.equals(
    "change_reason as input",
    envSetting.change_reason,
    createBody.change_reason,
  );

  // 3. Update the env setting: change value, env_name, reason, changed_by
  const newEnvValue = RandomGenerator.alphaNumeric(36);
  const updateBody = {
    env_value: newEnvValue,
    env_name: RandomGenerator.pick([
      "local",
      "development",
      "staging",
      "production",
    ] as const),
    change_reason: RandomGenerator.paragraph({ sentences: 3 }),
    changed_by: adminEmail,
  } satisfies IStoryfieldAiEnvSetting.IUpdate;
  const updated =
    await api.functional.storyfieldAi.systemAdmin.envSettings.update(
      connection,
      {
        envSettingId: envSetting.id,
        body: updateBody,
      },
    );
  typia.assert(updated);
  TestValidator.equals(
    "updated env_value as input",
    updated.env_value,
    newEnvValue,
  );
  TestValidator.equals(
    "updated env_name as input",
    updated.env_name,
    updateBody.env_name,
  );
  TestValidator.equals(
    "updated change_reason as input",
    updated.change_reason,
    updateBody.change_reason,
  );
  TestValidator.equals(
    "updated changed_by as input",
    updated.changed_by,
    updateBody.changed_by,
  );
  TestValidator.equals(
    "env_key unchanged after update",
    updated.env_key,
    envSetting.env_key,
  );
  TestValidator.equals(
    "created_at unchanged after update",
    updated.created_at,
    envSetting.created_at,
  );
  TestValidator.predicate(
    "updated_at advanced after update",
    Date.parse(updated.updated_at) > Date.parse(envSetting.updated_at),
  );

  // 5a. Update with invalid envSettingId
  await TestValidator.error(
    "updating with invalid envSettingId fails",
    async () => {
      await api.functional.storyfieldAi.systemAdmin.envSettings.update(
        connection,
        {
          envSettingId: typia.random<string & tags.Format<"uuid">>(),
          body: updateBody,
        },
      );
    },
  );

  // 5b. Update with empty body (should fail: nothing to update)
  await TestValidator.error("update with empty body fails", async () => {
    await api.functional.storyfieldAi.systemAdmin.envSettings.update(
      connection,
      {
        envSettingId: envSetting.id,
        body: {} satisfies IStoryfieldAiEnvSetting.IUpdate,
      },
    );
  });

  // 5c. Attempt update as unauthenticated/non-admin (simulate anonymous)
  const unauthConn: api.IConnection = { ...connection, headers: {} };
  await TestValidator.error("unauthenticated update denied", async () => {
    await api.functional.storyfieldAi.systemAdmin.envSettings.update(
      unauthConn,
      {
        envSettingId: envSetting.id,
        body: updateBody,
      },
    );
  });
}

