      {
        body: {
          title: RandomGenerator.paragraph({ sentences: 3 }),
          main_plot: RandomGenerator.content({ paragraphs: 1 }),
          language: "ko",
        } satisfies IStoryfieldAiStory.ICreate,
      },
    );
  typia.assert(story);

  // 4. User uploads an image to the story
  const image =
    await api.functional.storyfieldAi.authenticatedUser.stories.images.create(
      connection,
      {
        storyId: story.id,
        body: {
          storyfield_ai_story_id: story.id,
          image_uri: "https://bucket.s3.amazonaws.com/path/to/test-image.png",
          description: RandomGenerator.paragraph({ sentences: 2 }),
        } satisfies IStoryfieldAiStoryImage.ICreate,
      },
    );
  typia.assert(image);

  // 5. System admin logs back in and deletes the image
  await api.functional.auth.systemAdmin.login(connection, {
    body: {
      external_admin_id: admin_external_id,
      email: admin_email,
    } satisfies IStoryfieldAiSystemAdmin.ILogin,
  });

  await api.functional.storyfieldAi.systemAdmin.stories.images.erase(
    connection,
    {
      storyId: story.id,
      imageId: image.id,
    },
  );

  // 6. Since there's no direct read for the image, verify business workflow by adding another image
  await api.functional.auth.authenticatedUser.login(connection, {
    body: {
      external_user_id: user_external_id,
      email: user_email,
    } satisfies IStoryfieldAiAuthenticatedUser.ILogin,
  });

  const newImage =
    await api.functional.storyfieldAi.authenticatedUser.stories.images.create(
      connection,
      {
        storyId: story.id,
        body: {
          storyfield_ai_story_id: story.id,
          image_uri: "https://bucket.s3.amazonaws.com/path/to/new-image.png",
          description: RandomGenerator.paragraph({ sentences: 2 }),
        } satisfies IStoryfieldAiStoryImage.ICreate,
      },
    );
  typia.assert(newImage);
  TestValidator.notEquals(
    "Deleted image id and new image id must be different",
    image.id,
    newImage.id,
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";
import type { IPageIStoryfieldAiTtsResult } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIStoryfieldAiTtsResult";
import type { IStoryfieldAiAuthenticatedUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiAuthenticatedUser";
import type { IStoryfieldAiStory } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStory";
import type { IStoryfieldAiStoryPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStoryPage";
import type { IStoryfieldAiTtsResult } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiTtsResult";

/**
 * Validate searching TTS results for a story by the authenticated owner, with
 * filtering and pagination.
 *
 * 1. Register and authenticate as a user
 * 2. Create a story
 * 3. Add a page to the story
 * 4. Generate a TTS result for the full story (dialect: "standard")
 * 5. Generate a TTS result for the story page (dialect: "gyeongsang")
 * 6. Search for all TTS results on that story (no filters) â€“ expect both
 * 7. Search filtered by dialect "standard" â€“ expect only that result
 * 8. Search filtered by dialect "gyeongsang" â€“ expect only the page result
 * 9. Search filtered by story page ID â€“ expect only per-page TTS
 * 10. Test pagination (limit=1) â€“ expect paginated/partial result
 * 11. Validate correct inclusion and exclusion of target records
 */
export async function test_api_story_tts_results_search_by_owner(
  connection: api.IConnection,
) {
  // 1. Register user
  const externalUserId = RandomGenerator.alphaNumeric(16);
  const email = `${RandomGenerator.name(1)}@domain.com`;
  const joinUser = await api.functional.auth.authenticatedUser.join(
    connection,
    {
      body: {
        external_user_id: externalUserId,
        email,
        actor_type: "authenticatedUser",
      },
    },
  );
  typia.assert(joinUser);

  // 2. Login
  const loginUser = await api.functional.auth.authenticatedUser.login(
    connection,
    {
      body: {
        external_user_id: externalUserId,
        email,
      },
    },
  );
  typia.assert(loginUser);

  // 3. Create story
  const story =
    await api.functional.storyfieldAi.authenticatedUser.stories.create(
      connection,
      {
        body: {
          title: RandomGenerator.name(3),
          main_plot: RandomGenerator.paragraph({ sentences: 5 }),
          language: RandomGenerator.pick(["ko", "en", "gyeongsang"] as const),
        },
      },
    );
  typia.assert(story);

  // 4. Create story page
  const page =
    await api.functional.storyfieldAi.authenticatedUser.stories.pages.create(
      connection,
      {
        storyId: story.id,
        body: {
          page_number: 1,
          text: RandomGenerator.paragraph({ sentences: 4 }),
        },
      },
    );
  typia.assert(page);

  // 5. Generate TTS for full story (dialect: "standard")
  const storyTts =
    await api.functional.storyfieldAi.authenticatedUser.stories.ttsResults.create(
      connection,
      {
        storyId: story.id,
        body: {
          tts_audio_uri: `https://audio.example.com/${RandomGenerator.alphaNumeric(24)}.mp3`,
          source_text: RandomGenerator.content({ paragraphs: 2 }),
          dialect: "standard",
        },
      },
    );
  typia.assert(storyTts);

  // 6. Generate TTS for story page (dialect: "gyeongsang")
  const pageTts =
    await api.functional.storyfieldAi.authenticatedUser.stories.ttsResults.create(
      connection,
      {
        storyId: story.id,
        body: {
          tts_audio_uri: `https://audio.example.com/${RandomGenerator.alphaNumeric(24)}.mp3`,
          source_text: page.text,
          dialect: "gyeongsang",
          storyfield_ai_story_page_id: page.id,
        },
      },
    );
  typia.assert(pageTts);

  // 7. Search all TTS results (no filters)
  const allResults =
    await api.functional.storyfieldAi.authenticatedUser.stories.ttsResults.index(
      connection,
      {
        storyId: story.id,
        body: {},
      },
    );
  typia.assert(allResults);
  const resultIds = allResults.data.map((x) => x.id);
  TestValidator.predicate(
    "all created TTS results should be present (unfiltered)",
    resultIds.includes(storyTts.id) && resultIds.includes(pageTts.id),
  );

  // 8. Search/filter by dialect ("standard")
  const standardResults =
    await api.functional.storyfieldAi.authenticatedUser.stories.ttsResults.index(
      connection,
      {
        storyId: story.id,
        body: { dialect: "standard" },
      },
    );
  typia.assert(standardResults);
  TestValidator.predicate(
    "only standard dialect result present",
    standardResults.data.length === 1 &&
      standardResults.data[0].id === storyTts.id,
  );

  // 9. Search/filter by dialect ("gyeongsang")
  const gyeongsangResults =
    await api.functional.storyfieldAi.authenticatedUser.stories.ttsResults.index(
      connection,
      {
        storyId: story.id,
        body: { dialect: "gyeongsang" },
      },
    );
  typia.assert(gyeongsangResults);
  TestValidator.predicate(
    "only gyeongsang dialect result present",
    gyeongsangResults.data.length === 1 &&
      gyeongsangResults.data[0].id === pageTts.id,
  );

  // 10. Search/filter by page id
  const pageResults =
    await api.functional.storyfieldAi.authenticatedUser.stories.ttsResults.index(
      connection,
      {
        storyId: story.id,
        body: { storyfield_ai_story_page_id: page.id },
      },
    );
  typia.assert(pageResults);
  TestValidator.predicate(
    "only per-page TTS result present via page id filter",
    pageResults.data.length === 1 && pageResults.data[0].id === pageTts.id,
  );

  // 11. Pagination test (limit=1)
  const pagedResults =
    await api.functional.storyfieldAi.authenticatedUser.stories.ttsResults.index(
      connection,
      {
        storyId: story.id,
        body: { limit: 1 },
      },
    );
  typia.assert(pagedResults);
  TestValidator.equals(
    "pagination with limit 1 returns exactly 1 record",
    pagedResults.data.length,
    1,
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IStoryfieldAiAuthenticatedUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiAuthenticatedUser";
import type { IStoryfieldAiStory } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStory";
import type { IStoryfieldAiStoryImage } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStoryImage";
import type { IStoryfieldAiSystemAdmin } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiSystemAdmin";

/**
 * This test validates that a system administrator can successfully access
 * detailed metadata for a story image created under a specific AI-generated
 * story, and that access to this metadata is restricted appropriately. The test
 * follows a multi-actor flow:
 *
 * 1. Register and log in as a system administrator
 * 2. Register and log in as an authenticated user to create the parent story
 * 3. The authenticated user creates a new AI-generated story
 * 4. Swap back to the system administrator and upload an image to the created
 *    story
 * 5. Retrieve the image detail with system admin credentials, validating all
 *    metadata (uri, description, references, audit fields, no deletion)
 * 6. Attempt to fetch details with a non-existent image ID to ensure error
 *    response and access control is correct
 */
export async function test_api_systemadmin_story_image_detail_access_by_admin(
  connection: api.IConnection,
) {
  // 1. Register and login as system admin
  const adminInfo = {
    external_admin_id: RandomGenerator.alphaNumeric(12),
    email: `${RandomGenerator.alphaNumeric(8)}@admin.com`,
    actor_type: "systemAdmin" as const,
  } satisfies IStoryfieldAiSystemAdmin.IJoin;
  const admin = await api.functional.auth.systemAdmin.join(connection, {
    body: adminInfo,
  });
  typia.assert(admin);

  await api.functional.auth.systemAdmin.login(connection, {
    body: {
      external_admin_id: adminInfo.external_admin_id,
      email: adminInfo.email,
    },
  });

  // 2. Register and login as authenticatedUser
  const userInfo = {
    external_user_id: RandomGenerator.alphaNumeric(14),
    email: `${RandomGenerator.alphaNumeric(8)}@example.com`,
    actor_type: "authenticatedUser" as const,
  } satisfies IStoryfieldAiAuthenticatedUser.ICreate;
  const user = await api.functional.auth.authenticatedUser.join(connection, {
    body: userInfo,
  });
  typia.assert(user);

  await api.functional.auth.authenticatedUser.login(connection, {
    body: {
      external_user_id: userInfo.external_user_id,
      email: userInfo.email,
    },
  });

  // 3. Create a new story as authenticated user
  const storyBody = {
    title: RandomGenerator.paragraph({ sentences: 2 }),
    main_plot: RandomGenerator.paragraph({ sentences: 5 }),
    language: RandomGenerator.pick([
      "ko-KR",
      "en",
      "fr",
      "jp",
      "zh-CN",
    ] as const),
  } satisfies IStoryfieldAiStory.ICreate;
  const story =
    await api.functional.storyfieldAi.authenticatedUser.stories.create(
      connection,
      { body: storyBody },
    );
  typia.assert(story);

  // 4. Switch to systemAdmin session
  await api.functional.auth.systemAdmin.login(connection, {
    body: {
      external_admin_id: adminInfo.external_admin_id,
      email: adminInfo.email,
    },
  });

  // 5. Add an image to the created story as systemAdmin
  const imageBody = {
    storyfield_ai_story_id: story.id,
    image_uri: `https://s3.example.com/${RandomGenerator.alphaNumeric(16)}.png`,
    description: RandomGenerator.paragraph({ sentences: 3 }),
  } satisfies IStoryfieldAiStoryImage.ICreate;
  const image =
    await api.functional.storyfieldAi.systemAdmin.stories.images.create(
      connection,
      {
        storyId: story.id,
        body: imageBody,
      },
    );
  typia.assert(image);

  // 6. Fetch image detail using systemAdmin API
  const imageDetail =
    await api.functional.storyfieldAi.systemAdmin.stories.images.at(
      connection,
      {
        storyId: story.id,
        imageId: image.id,
      },
    );
  typia.assert(imageDetail);
  TestValidator.equals(
    "image URI should match",
    imageDetail.image_uri,
    imageBody.image_uri,
  );
  TestValidator.equals(
    "description should match",
    imageDetail.description,
    imageBody.description,
  );
  TestValidator.equals("image id matches", imageDetail.id, image.id);
  TestValidator.equals(
    "parent story id matches",
    imageDetail.storyfield_ai_story_id,
    story.id,
  );
  TestValidator.equals("not deleted", imageDetail.deleted_at, null);

  // 7. Try to fetch image details with invalid image id - expect error
  const bogusImageId: string & tags.Format<"uuid"> = typia.random<
    string & tags.Format<"uuid">
  >();
  await TestValidator.error("should fail on nonexistent image id", async () => {
    await api.functional.storyfieldAi.systemAdmin.stories.images.at(
      connection,
      {
        storyId: story.id,
        imageId: bogusImageId,
      },
    );
  });
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IStoryfieldAiAuthenticatedUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiAuthenticatedUser";
import type { IStoryfieldAiStory } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStory";
import type { IStoryfieldAiStoryImage } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStoryImage";

/**
 * Verify that an authenticated user can upload an image to a story they own,
 * with proper business error handling for unauthorized operations.
 *
 * Steps:
 *
 * 1. Register a user and log in (store external_user_id, email).
 * 2. Create a story for the user.
 * 3. Add an image to their own story, validate image metadata
 *    (storyfield_ai_story_id matches, image_uri/description correct).
 * 4. Create a second user and story. Attempt to add an image to that story while
 *    logged in as the first user (should error).
 */
export async function test_api_authenticateduser_add_image_to_own_story(
  connection: api.IConnection,
) {
  // Register User 1
  const extUserId1 = RandomGenerator.alphaNumeric(16);
  const email1 = `${RandomGenerator.alphabets(8)}@company.com`;
  const reg1 = await api.functional.auth.authenticatedUser.join(connection, {
    body: {
      external_user_id: extUserId1,
      email: email1,
      actor_type: "authenticatedUser",
    } satisfies IStoryfieldAiAuthenticatedUser.ICreate,
  });
  typia.assert(reg1);

  // Log in as User 1
  const login1 = await api.functional.auth.authenticatedUser.login(connection, {
    body: {
      external_user_id: extUserId1,
      email: email1,
    } satisfies IStoryfieldAiAuthenticatedUser.ILogin,
  });
  typia.assert(login1);

  // Create a story for User 1
  const story1 =
    await api.functional.storyfieldAi.authenticatedUser.stories.create(
      connection,
      {
        body: {
          title: RandomGenerator.paragraph({ sentences: 2 }),
          main_plot: RandomGenerator.paragraph({ sentences: 5 }),
          language: "ko-KR",
        } satisfies IStoryfieldAiStory.ICreate,
      },
    );
  typia.assert(story1);

  // Add image to own story
  const imageData = {
    storyfield_ai_story_id: story1.id,
    image_uri: `https://storage.example.com/${RandomGenerator.alphaNumeric(32)}.jpg`,
    description: RandomGenerator.paragraph(),
  } satisfies IStoryfieldAiStoryImage.ICreate;
  const image =
    await api.functional.storyfieldAi.authenticatedUser.stories.images.create(
      connection,
      {
        storyId: story1.id,
        body: imageData,
      },
    );
  typia.assert(image);
  TestValidator.equals(
    "image metadata correct",
    image.storyfield_ai_story_id,
    story1.id,
  );
  TestValidator.equals(
    "image_uri matches",
    image.image_uri,
    imageData.image_uri,
  );
  TestValidator.equals(
    "description matches",
    image.description,
    imageData.description,
  );

  // Register User 2
  const extUserId2 = RandomGenerator.alphaNumeric(16);
  const email2 = `${RandomGenerator.alphabets(8)}@company.com`;
  const reg2 = await api.functional.auth.authenticatedUser.join(connection, {
    body: {
      external_user_id: extUserId2,
      email: email2,
      actor_type: "authenticatedUser",
    } satisfies IStoryfieldAiAuthenticatedUser.ICreate,
  });
  typia.assert(reg2);

  // Log in as User 2 and create a story
  const login2 = await api.functional.auth.authenticatedUser.login(connection, {
    body: {
      external_user_id: extUserId2,
      email: email2,
    } satisfies IStoryfieldAiAuthenticatedUser.ILogin,
  });
  typia.assert(login2);
  const story2 =
    await api.functional.storyfieldAi.authenticatedUser.stories.create(
      connection,
      {
        body: {
          title: RandomGenerator.paragraph({ sentences: 3 }),
          main_plot: RandomGenerator.paragraph({ sentences: 6 }),
          language: "en-US",
        } satisfies IStoryfieldAiStory.ICreate,
      },
    );
  typia.assert(story2);

  // Log back as User 1
  await api.functional.auth.authenticatedUser.login(connection, {
    body: {
      external_user_id: extUserId1,
      email: email1,
    } satisfies IStoryfieldAiAuthenticatedUser.ILogin,
  });

  // Attempt unauthorized image upload (User 1 -> User 2's story)
  await TestValidator.error(
    "cannot add image to another user's story",
    async () => {
      await api.functional.storyfieldAi.authenticatedUser.stories.images.create(
        connection,
        {
          storyId: story2.id,
          body: {
            storyfield_ai_story_id: story2.id,
            image_uri: `https://storage.example.com/${RandomGenerator.alphaNumeric(12)}.png`,
          } satisfies IStoryfieldAiStoryImage.ICreate,
        },
      );
    },
  );
}

