import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IStoryfieldAiAuthenticatedUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiAuthenticatedUser";
import type { IStoryfieldAiStory } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStory";
import type { IStoryfieldAiStoryImage } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStoryImage";

/**
 * Verify that an authenticated user can update their own story's image
 * metadata.
 *
 * 1. Register and log in as authenticated user.
 * 2. Create a new story and add an image to it.
 * 3. Update image's metadata (image_uri, description) referencing storyId and
 *    imageId.
 * 4. Confirm metadata and audit updates.
 * 5. Attempt unauthorized update as another user (should fail).
 * 6. Attempt to update invalid imageId (should fail).
 */
export async function test_api_authenticateduser_update_own_story_image_metadata(
  connection: api.IConnection,
) {
  // 1. Register as user1
  const user1_external_id = RandomGenerator.alphaNumeric(12);
  const user1_email = `user1+${RandomGenerator.alphaNumeric(8)}@storyfield.com`;
  const user1_register = await api.functional.auth.authenticatedUser.join(
    connection,
    {
      body: {
        external_user_id: user1_external_id,
        email: user1_email,
        actor_type: "authenticatedUser",
      },
    },
  );
  typia.assert(user1_register);

  // 2. Login as user1
  const user1_login = await api.functional.auth.authenticatedUser.login(
    connection,
    {
      body: {
        external_user_id: user1_external_id,
        email: user1_email,
      },
    },
  );
  typia.assert(user1_login);

  // 3. Create a new story
  const storyBody = {
    title: RandomGenerator.name(3),
    main_plot: RandomGenerator.paragraph({ sentences: 5 }),
    language: "ko-KR",
  } satisfies IStoryfieldAiStory.ICreate;
  const story =
    await api.functional.storyfieldAi.authenticatedUser.stories.create(
      connection,
      {
        body: storyBody,
      },
    );
  typia.assert(story);

  // 4. Add image to story
  const imageBody = {
    storyfield_ai_story_id: story.id,
    image_uri: `https://img.storyfield.ai/${RandomGenerator.alphaNumeric(16)}.png`,
    description: RandomGenerator.paragraph({ sentences: 2 }),
  } satisfies IStoryfieldAiStoryImage.ICreate;
  const image =
    await api.functional.storyfieldAi.authenticatedUser.stories.images.create(
      connection,
      {
        storyId: story.id,
        body: imageBody,
      },
    );
  typia.assert(image);

  // 5. Update image metadata
  const updateBody = {
    image_uri: `https://img.storyfield.ai/${RandomGenerator.alphaNumeric(20)}.png`,
    description: RandomGenerator.paragraph({ sentences: 3 }),
  } satisfies IStoryfieldAiStoryImage.IUpdate;
  const updated =
    await api.functional.storyfieldAi.authenticatedUser.stories.images.update(
      connection,
      {
        storyId: story.id,
        imageId: image.id,
        body: updateBody,
      },
    );
  typia.assert(updated);
  TestValidator.equals(
    "image_uri updated",
    updated.image_uri,
    updateBody.image_uri,
  );
  TestValidator.equals(
    "description updated",
    updated.description,
    updateBody.description,
  );
  TestValidator.predicate(
    "updated_at should be newer than created_at",
    new Date(updated.updated_at) > new Date(updated.created_at),
  );

  // 6. Register as a second user
  const user2_external_id = RandomGenerator.alphaNumeric(12);
  const user2_email = `user2+${RandomGenerator.alphaNumeric(8)}@storyfield.com`;
  const user2_register = await api.functional.auth.authenticatedUser.join(
    connection,
    {
      body: {
        external_user_id: user2_external_id,
        email: user2_email,
        actor_type: "authenticatedUser",
      },
    },
  );
  typia.assert(user2_register);

  // 7. Login as user2 and attempt to update image (should fail)
  const user2_login = await api.functional.auth.authenticatedUser.login(
    connection,
    {
      body: {
        external_user_id: user2_external_id,
        email: user2_email,
      },
    },
  );
  typia.assert(user2_login);

  await TestValidator.error("different user cannot update image", async () => {
    await api.functional.storyfieldAi.authenticatedUser.stories.images.update(
      connection,
      {
        storyId: story.id,
        imageId: image.id,
        body: {
          description: "ShouldNotWork",
        },
      },
    );
  });

  // 8. Restore login as original user1
  await api.functional.auth.authenticatedUser.login(connection, {
    body: {
      external_user_id: user1_external_id,
      email: user1_email,
    },
  });

  // 9. Attempt update with non-existent imageId
  await TestValidator.error(
    "update with non-existent imageId should fail",
    async () => {
      await api.functional.storyfieldAi.authenticatedUser.stories.images.update(
        connection,
        {
          storyId: story.id,
          imageId: typia.random<string & tags.Format<"uuid">>(),
          body: {
            image_uri: `https://img.storyfield.ai/${RandomGenerator.alphaNumeric(16)}.png`,
          },
        },
      );
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IStoryfieldAiAuthenticatedUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiAuthenticatedUser";
import type { IStoryfieldAiStory } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStory";
import type { IStoryfieldAiSystemAdmin } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiSystemAdmin";
import type { IStoryfieldAiTtsResult } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiTtsResult";

/**
 * Validate that system admin can erase any story's TTS result regardless of
 * ownership, and that soft-deletion takes effect (cannot erase twice, error on
 * missing).
 *
 * 1. Register and log in as systemAdmin
 * 2. Register and log in as authenticatedUser
 * 3. AuthenticatedUser creates a story
 * 4. AuthenticatedUser creates a TTS result for that story
 * 5. SystemAdmin logs in and erases the TTS result by id
 * 6. Attempting to erase the same TTS again returns error (already deleted)
 * 7. Attempting to erase a (random/non-existent) TTS id returns error
 */
export async function test_api_systemadmin_tts_result_erase_story_owned(
  connection: api.IConnection,
) {
  // 1. Create systemAdmin and authenticatedUser
  const sysAdminExternalId = RandomGenerator.alphaNumeric(16);
  const sysAdminEmail = `${RandomGenerator.name(1)}@admin.test.com`;
  const adminAuth = await api.functional.auth.systemAdmin.join(connection, {
    body: {
      external_admin_id: sysAdminExternalId,
      email: sysAdminEmail,
      actor_type: "systemAdmin",
    },
  });
  typia.assert(adminAuth);

  const authedUserExternalId = RandomGenerator.alphaNumeric(16);
  const authedUserEmail = `${RandomGenerator.name(1)}@user.test.com`;
  const userAuth = await api.functional.auth.authenticatedUser.join(
    connection,
    {
      body: {
        external_user_id: authedUserExternalId,
        email: authedUserEmail,
        actor_type: "authenticatedUser",
      },
    },
  );
  typia.assert(userAuth);

  // 2. Log in as authenticatedUser
  await api.functional.auth.authenticatedUser.login(connection, {
    body: { external_user_id: authedUserExternalId, email: authedUserEmail },
  });

  // 3. Create a story as authenticatedUser
  const story =
    await api.functional.storyfieldAi.authenticatedUser.stories.create(
      connection,
      {
        body: {
          title: RandomGenerator.paragraph({ sentences: 3 }),
          main_plot: RandomGenerator.paragraph({ sentences: 5 }),
          language: "ko",
        },
      },
    );
  typia.assert(story);

  // 4. Create a TTS result for this story
  const ttsResult =
    await api.functional.storyfieldAi.authenticatedUser.stories.ttsResults.create(
      connection,
      {
        storyId: story.id,
        body: {
          tts_audio_uri: `https://storage.test/${RandomGenerator.alphaNumeric(8)}.mp3`,
          source_text: RandomGenerator.content({ paragraphs: 2 }),
          dialect: "standard",
          storyfield_ai_story_page_id: null,
        },
      },
    );
  typia.assert(ttsResult);

  // 5. Switch role: log in as systemAdmin
  await api.functional.auth.systemAdmin.login(connection, {
    body: { external_admin_id: sysAdminExternalId, email: sysAdminEmail },
  });

  // 6. Delete TTS result as admin
  await api.functional.storyfieldAi.systemAdmin.stories.ttsResults.erase(
    connection,
    {
      storyId: story.id,
      ttsResultId: ttsResult.id,
    },
  );

  // 7. Try deleting the same TTS result again: expect error
  await TestValidator.error(
    "erase already deleted TTS result returns error",
    async () => {
      await api.functional.storyfieldAi.systemAdmin.stories.ttsResults.erase(
        connection,
        {
          storyId: story.id,
          ttsResultId: ttsResult.id,
        },
      );
    },
  );

  // 8. Try deleting a random/non-existent TTS id: expect error
  await TestValidator.error(
    "erase non-existent TTS result returns error",
    async () => {
      await api.functional.storyfieldAi.systemAdmin.stories.ttsResults.erase(
        connection,
        {
          storyId: story.id,
          ttsResultId: typia.random<string & tags.Format<"uuid">>(),
        },
      );
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";
import type { IPageIStoryfieldAiTokenSession } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIStoryfieldAiTokenSession";
import type { IStoryfieldAiSystemAdmin } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiSystemAdmin";
import type { IStoryfieldAiTokenSession } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiTokenSession";

/**
 * System admin token session audit and filtering e2e validation.
 *
 * 1. Register admin and verify authenticated context.
 * 2. Retrieve all token sessions with no filter, assert own session present.
 * 3. Filter by system_admin_id (self), expect to find only own sessions.
 * 4. Filter by a random authenticated_user_id (no match), expect empty data.
 * 5. Test pagination: set limit=1, page=1, ensure result structure.
 * 6. For each session, confirm sensitive values such as raw tokens are not
 *    present.
 * 7. Test unauthorized access: try endpoint with unauthenticated connection,
 *    expect error.
 * 8. Test invalid input: page=0, expect error.
 */
export async function test_api_token_session_search_by_admin(
  connection: api.IConnection,
) {
  // 1. Register system admin and validate authentication context
  const joinInput = {
    external_admin_id: RandomGenerator.alphaNumeric(16),
    email: `${RandomGenerator.alphabets(8)}@company.com`,
    actor_type: "systemAdmin",
  } satisfies IStoryfieldAiSystemAdmin.IJoin;
  const admin: IStoryfieldAiSystemAdmin.IAuthorized =
    await api.functional.auth.systemAdmin.join(connection, { body: joinInput });
  typia.assert(admin);
  TestValidator.equals(
    "systemAdmin email matches join input",
    admin.email,
    joinInput.email,
  );
  TestValidator.equals(
    "actor_type is always 'systemAdmin'",
    admin.actor_type,
    "systemAdmin",
  );
  TestValidator.predicate(
    "token.access exists",
    typeof admin.token.access === "string" && admin.token.access.length > 0,
  );

  // 2. Retrieve all token sessions (no filter)
  const allSessions =
    await api.functional.storyfieldAi.systemAdmin.tokenSessions.index(
      connection,
      { body: {} satisfies IStoryfieldAiTokenSession.IRequest },
    );
  typia.assert(allSessions);
  TestValidator.predicate(
    "pagination object exists",
    typeof allSessions.pagination === "object",
  );
  TestValidator.predicate(
    "sessions returned as array",
    Array.isArray(allSessions.data),
  );
  // Confirm that this admin's session exists
  const foundOwnSession = allSessions.data.find(
    (session) => session.system_admin_id === admin.id,
  );
  TestValidator.predicate(
    "own admin session present in sessions",
    typeof foundOwnSession !== "undefined",
  );
  // Confirm no raw tokens in session summary objects
  if (allSessions.data[0]) {
    TestValidator.predicate(
      "session summary does not contain token/access fields",
      !("token" in allSessions.data[0]) && !("access" in allSessions.data[0]),
    );
  }

  // 3. Filter by own system_admin_id
  const filterByAdmin =
    await api.functional.storyfieldAi.systemAdmin.tokenSessions.index(
      connection,
      {
        body: {
          system_admin_id: admin.id,
        } satisfies IStoryfieldAiTokenSession.IRequest,
      },
    );
  typia.assert(filterByAdmin);
  TestValidator.predicate(
    "sessions only for system_admin_id",
    filterByAdmin.data.every((s) => s.system_admin_id === admin.id),
  );

  // 4. Filter by random non-existent authenticated_user_id
  const noUserResult =
    await api.functional.storyfieldAi.systemAdmin.tokenSessions.index(
      connection,
      {
        body: {
          authenticated_user_id: typia.random<string & tags.Format<"uuid">>(),
        } satisfies IStoryfieldAiTokenSession.IRequest,
      },
    );
  typia.assert(noUserResult);
  TestValidator.equals(
    "search for non-existent user session returns empty",
    noUserResult.data.length,
    0,
  );

  // 5. Pagination test (page/limit) - limit=1, page=1 (assuming at least 1 session exists)
  const paged =
    await api.functional.storyfieldAi.systemAdmin.tokenSessions.index(
      connection,
      {
        body: {
          page: 1 satisfies number,
          limit: 1 satisfies number,
        } satisfies IStoryfieldAiTokenSession.IRequest,
      },
    );
  typia.assert(paged);
  TestValidator.equals("pagination.current is 1", paged.pagination.current, 1);
  TestValidator.equals("pagination.limit is 1", paged.pagination.limit, 1);
  TestValidator.predicate("paged.data.length <= 1", paged.data.length <= 1);

  // 6. Attempt with unauthenticated connection: expect error
  const unauthConn: api.IConnection = { ...connection, headers: {} };
  await TestValidator.error(
    "unauthenticated systemAdmin should not access tokenSessions",
    async () => {
      await api.functional.storyfieldAi.systemAdmin.tokenSessions.index(
        unauthConn,
        { body: {} satisfies IStoryfieldAiTokenSession.IRequest },
      );
    },
  );

  // 7. Invalid query parameter - page=0 (out of allowed range), expect error
  await TestValidator.error(
    "invalid page parameter should cause error",
    async () => {
      await api.functional.storyfieldAi.systemAdmin.tokenSessions.index(
        connection,
        {
          body: {
            page: 0 satisfies number,
          } satisfies IStoryfieldAiTokenSession.IRequest,
        },
      );
    },
  );

  // 8. Optionally, if there is at least one session record, test filtering by fingerprint/issued_at using a record from current data
  const sessionSample = allSessions.data[0];
  if (sessionSample) {
    const filterByFingerprint =
      await api.functional.storyfieldAi.systemAdmin.tokenSessions.index(
        connection,
        {
          body: {
            fingerprint: sessionSample.fingerprint,
          } satisfies IStoryfieldAiTokenSession.IRequest,
        },
      );
    typia.assert(filterByFingerprint);
    TestValidator.predicate(
      "filtered by fingerprint should include only matching session(s)",
      filterByFingerprint.data.every(
        (s) => s.fingerprint === sessionSample.fingerprint,
      ),
    );
  }
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";
import type { IPageIStoryfieldAiTokenSession } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIStoryfieldAiTokenSession";
import type { IStoryfieldAiSystemAdmin } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiSystemAdmin";
import type { IStoryfieldAiTokenSession } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiTokenSession";

/**
 * Validate admin update of a token session's allowed fields.
 *
 * 1. Register and log in as a new system admin. Save credentials.
 * 2. List token sessions as admin; pick an admin-linked session.
 * 3. Prepare a valid update payload (e.g., change `expires_at`, and/or
 *    `fingerprint`).
 * 4. Call update endpoint, passing the session ID and update payload.
 * 5. Fetch session list again and validate that the updates are reflected for the
 *    same ID.
 * 6. Try to update a non-existent session expecting an error.
 */
export async function test_api_token_session_update_by_admin(
  connection: api.IConnection,
) {
  // 1. Register and log in as system admin
  const externalAdminId = RandomGenerator.alphaNumeric(16);
  const adminEmail = `${RandomGenerator.alphaNumeric(8)}@testing-admin.com`;
  const adminJoin = await api.functional.auth.systemAdmin.join(connection, {
    body: {
      external_admin_id: externalAdminId,
      email: adminEmail,
      actor_type: "systemAdmin",
    } satisfies IStoryfieldAiSystemAdmin.IJoin,
  });
  typia.assert(adminJoin);

  // 2. List token sessions, filter for this admin's sessions
  const sessionPage =
    await api.functional.storyfieldAi.systemAdmin.tokenSessions.index(
      connection,
      {
        body: {
          system_admin_id: adminJoin.id,
          limit: 5,
        } satisfies IStoryfieldAiTokenSession.IRequest,
      },
    );
  typia.assert(sessionPage);
  TestValidator.predicate(
    "at least one admin session exists",
    sessionPage.data.length > 0,
  );
  const sessionToUpdate = sessionPage.data[0];
  typia.assert(sessionToUpdate);

  // 3. Prepare update payload (update expires_at & fingerprint)
  const newExpiresAt = new Date(Date.now() + 24 * 60 * 60 * 1000).toISOString();
  const newFingerprint = RandomGenerator.alphaNumeric(32);
  const updatePayload = {
    expires_at: newExpiresAt,
    fingerprint: newFingerprint,
  } satisfies IStoryfieldAiTokenSession.IUpdate;

  // 4. Update the session
  const updatedSession =
    await api.functional.storyfieldAi.systemAdmin.tokenSessions.update(
      connection,
      {
        tokenSessionId: sessionToUpdate.id,
        body: updatePayload,
      },
    );
  typia.assert(updatedSession);
  TestValidator.equals(
    "expires_at updated",
    updatedSession.expires_at,
    newExpiresAt,
  );
  TestValidator.equals(
    "fingerprint updated",
    updatedSession.fingerprint,
    newFingerprint,
  );

  // 5. Re-list to confirm persistence
  const newSessionPage =
    await api.functional.storyfieldAi.systemAdmin.tokenSessions.index(
      connection,
      {
        body: {
          system_admin_id: adminJoin.id,
          limit: 5,
        } satisfies IStoryfieldAiTokenSession.IRequest,
      },
    );
  typia.assert(newSessionPage);
  const found = newSessionPage.data.find((s) => s.id === sessionToUpdate.id);
  typia.assertGuard(found!);
  TestValidator.equals(
    "reloaded expires_at matches",
    found!.expires_at,
    newExpiresAt,
  );
  TestValidator.equals(
    "reloaded fingerprint matches",
    found!.fingerprint,
    newFingerprint,
  );

  // 6. Error case: update a non-existent session (expect error)
  await TestValidator.error(
    "update non-existent session should fail",
    async () => {
      await api.functional.storyfieldAi.systemAdmin.tokenSessions.update(
        connection,
        {
          tokenSessionId: typia.random<string & tags.Format<"uuid">>(),
          body: updatePayload,
        },
      );
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IStoryfieldAiAuthenticatedUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiAuthenticatedUser";
import type { IStoryfieldAiAuthenticatedusers } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiAuthenticatedusers";
import type { IStoryfieldAiSystemAdmin } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiSystemAdmin";

/**
 * Validates the system administrator's ability to update profile information of
 * an authenticated user, including audit and error paths.
 *
 * 1. Register a new system admin account for privileged context
 * 2. Login as system admin to obtain authorization/session
 * 3. Register a new authenticated user and capture their id
 * 4. As system admin, update the user's email and verify audit trail fields
 *    changed
 * 5. Validate email/actor_type updated and updated_at differs from created_at
 * 6. Attempt update using a duplicate email to assert uniqueness constraint error
 * 7. Attempt update of a random/non-existent user (404 error)
 * 8. Attempt update with insufficient permission (simulate unauthenticated call)
 */
export async function test_api_authenticated_user_update_information_and_audit(
  connection: api.IConnection,
) {
  // 1. Register a new system admin
  const adminEmail = typia.random<string & tags.Format<"email">>();
  const externalAdminId = RandomGenerator.alphaNumeric(12);
  const adminJoin = await api.functional.auth.systemAdmin.join(connection, {
    body: {
      external_admin_id: externalAdminId,
      email: adminEmail,
      actor_type: "systemAdmin",
    } satisfies IStoryfieldAiSystemAdmin.IJoin,
  });
  typia.assert(adminJoin);

  // 2. System admin login
  const adminLogin = await api.functional.auth.systemAdmin.login(connection, {
    body: {
      external_admin_id: externalAdminId,
      email: adminEmail,
    } satisfies IStoryfieldAiSystemAdmin.ILogin,
  });
  typia.assert(adminLogin);

  // 3. Register a new authenticated user
  const authUserEmail = typia.random<string & tags.Format<"email">>();
  const externalUserId = RandomGenerator.alphaNumeric(16);
  const userJoin = await api.functional.auth.authenticatedUser.join(
    connection,
    {
      body: {
        external_user_id: externalUserId,
        email: authUserEmail,
        actor_type: "authenticatedUser",
      } satisfies IStoryfieldAiAuthenticatedUser.ICreate,
    },
  );
  typia.assert(userJoin);
  const authenticatedUserId = typia.assert<string & tags.Format<"uuid">>(
    userJoin.id,
  );

  // 4. Update user email and/or actor_type as system admin
  const newEmail = typia.random<string & tags.Format<"email">>();
  const updateBody = {
    email: newEmail,
    actor_type: "authenticatedUser",
  } satisfies IStoryfieldAiAuthenticatedusers.IUpdate;
  const updateResult =
    await api.functional.storyfieldAi.systemAdmin.authenticatedUsers.update(
      connection,
      {
        authenticatedUserId,
        body: updateBody,
      },
    );
  typia.assert(updateResult);
  TestValidator.equals(
    "updated email should match",
    updateResult.email,
    newEmail,
  );
  TestValidator.equals(
    "actor_type remains authenticatedUser",
    updateResult.actor_type,
    "authenticatedUser",
  );
  TestValidator.notEquals(
    "updated_at must change from created_at",
    updateResult.updated_at,
    updateResult.created_at,
  );

  // 5. Attempt duplicate email (should error)
  await TestValidator.error("update fails with duplicate email", async () => {
    await api.functional.storyfieldAi.systemAdmin.authenticatedUsers.update(
      connection,
      {
        authenticatedUserId,
        body: {
          email: adminEmail,
        } satisfies IStoryfieldAiAuthenticatedusers.IUpdate,
      },
    );
  });

  // 6. Attempt to update non-existent user id (should error)
  const randomUserId = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.error("update fails for non-existent user", async () => {
    await api.functional.storyfieldAi.systemAdmin.authenticatedUsers.update(
      connection,
      {
        authenticatedUserId: randomUserId,
        body: {
          email: typia.random<string & tags.Format<"email">>(),
        } satisfies IStoryfieldAiAuthenticatedusers.IUpdate,
      },
    );
  });

  // 7. Permission error check - try update without admin (simulate non-admin session)
  const unauthConn: api.IConnection = {
    ...connection,
    headers: {},
  };
  await TestValidator.error(
    "insufficient permission to update user",
    async () => {
      await api.functional.storyfieldAi.systemAdmin.authenticatedUsers.update(
        unauthConn,
        {
          authenticatedUserId,
          body: {
            email: typia.random<string & tags.Format<"email">>(),
          } satisfies IStoryfieldAiAuthenticatedusers.IUpdate,
        },
      );
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IStoryfieldAiAuthenticatedUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiAuthenticatedUser";
import type { IStoryfieldAiSystemAdmin } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiSystemAdmin";

/**
 * Soft delete of an authenticated user account by system admin (admin
 * deactivation flow).
 *
 * This test executes the following scenario:
 *
 * 1. Register a new system admin (POST /auth/systemAdmin/join) with random unique
 *    identifiers.
 * 2. Log in as this system admin (POST /auth/systemAdmin/login) to obtain valid
 *    session and authorization.
 * 3. Register an authenticated user (POST /auth/authenticatedUser/join) with
 *    unique identifiers.
 * 4. As the admin, soft-delete the authenticated user via the erase endpoint
 *    (DELETE
 *    /storyfieldAi/systemAdmin/authenticatedUsers/{authenticatedUserId}).
 * 5. Attempt to delete the same user again to confirm idempotency error.
 * 6. Attempt to delete a non-existent (random) authenticated user ID to confirm
 *    correct error response.
 *
 * This validates that only system admins can perform deactivation, the workflow
 * is enforced, deletion is idempotent, and error handling is correct on
 * business logic edge cases.
 */
export async function test_api_authenticated_user_soft_delete_by_admin(
  connection: api.IConnection,
) {
  // 1. Register a new system admin
  const adminJoinBody = {
    external_admin_id: RandomGenerator.alphaNumeric(16),
    email: `${RandomGenerator.alphaNumeric(8)}@admin-domain.com`,
    actor_type: "systemAdmin",
  } satisfies IStoryfieldAiSystemAdmin.IJoin;
  const admin = await api.functional.auth.systemAdmin.join(connection, {
    body: adminJoinBody,
  });
  typia.assert(admin);

  // 2. Login as system admin (to acquire correct JWT session)
  const adminLoginBody = {
    external_admin_id: admin.external_admin_id,
    email: admin.email,
  } satisfies IStoryfieldAiSystemAdmin.ILogin;
  const adminSession = await api.functional.auth.systemAdmin.login(connection, {
    body: adminLoginBody,
  });
  typia.assert(adminSession);
  TestValidator.equals(
    "system admin id matches after login",
    adminSession.id,
    admin.id,
  );

  // 3. Register a new authenticated user for deletion
  const userJoinBody = {
    external_user_id: RandomGenerator.alphaNumeric(20),
    email: `${RandomGenerator.alphaNumeric(10)}@user-domain.com`,
    actor_type: "authenticatedUser",
  } satisfies IStoryfieldAiAuthenticatedUser.ICreate;
  const user = await api.functional.auth.authenticatedUser.join(connection, {
    body: userJoinBody,
  });
  typia.assert(user);
  const authenticatedUserId = user.id;

  // 4. Soft-delete the user as admin
  await api.functional.storyfieldAi.systemAdmin.authenticatedUsers.erase(
    connection,
    {
      authenticatedUserId,
    },
  );

  // 5. Attempt to delete again (should fail, idempotency check)
  await TestValidator.error("idempotent: soft delete again fails", async () => {
    await api.functional.storyfieldAi.systemAdmin.authenticatedUsers.erase(
      connection,
      {
        authenticatedUserId,
      },
    );
  });

  // 6. Attempt to delete a non-existent authenticated user
  const nonExistentAuthUserId = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.error(
    "delete non-existent authenticated user fails",
    async () => {
      await api.functional.storyfieldAi.systemAdmin.authenticatedUsers.erase(
        connection,
        {
          authenticatedUserId: nonExistentAuthUserId,
        },
      );
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";
import type { IPageIStoryfieldAiSystemAdmin } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIStoryfieldAiSystemAdmin";
import type { IStoryfieldAiSystemAdmin } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiSystemAdmin";

/**
 * Validate the admin search, filtering, pagination, and authorization for
 * system admin list endpoint.
 *
 * 1. Create 8+ system admin accounts with unique external_admin_id and email
 *    (simulate SSO flow).
 * 2. Login as one admin for session (select one, keep credentials).
 * 3. List all admin accounts with no filter; verify count and data correctness.
 * 4. Search/filter: by email (exact), by external_admin_id, by actor_type (should
 *    always be 'systemAdmin'), by a creation date range, by updated_at, by
 *    last_login_at; for each, verify results match expected subset.
 * 5. Pagination: Request first page with limit 3, then next, confirm correct page
 *    counts, total, and data.
 * 6. Negative test: Use an unauthenticated connection to call endpoint and expect
 *    error.
 * 7. Negative test: Pass malformed or impossible filter values (bad email,
 *    negative page etc.) to confirm error handling.
 */
export async function test_api_system_admin_account_search_and_filter(
  connection: api.IConnection,
) {
  // 1. Register multiple admins
  const adminCount = 8;
  const admins: IStoryfieldAiSystemAdmin.IAuthorized[] =
    await ArrayUtil.asyncMap(
      ArrayUtil.repeat(adminCount, (i) => i),
      async (i) => {
        const external_admin_id = RandomGenerator.alphaNumeric(12) + i;
        const email = `admin_${i}_${RandomGenerator.alphaNumeric(4)}@company.test`;
        const created = await api.functional.auth.systemAdmin.join(connection, {
          body: {
            external_admin_id,
            email: email as string & tags.Format<"email">,
            actor_type: "systemAdmin",
          } satisfies IStoryfieldAiSystemAdmin.IJoin,
        });
        typia.assert(created);
        return created;
      },
    );

  // 2. Login with one of the admins
  const loginAdmin = admins[0];
  const logged = await api.functional.auth.systemAdmin.login(connection, {
    body: {
      external_admin_id: loginAdmin.external_admin_id,
      email: loginAdmin.email as string & tags.Format<"email">,
    } satisfies IStoryfieldAiSystemAdmin.ILogin,
  });
  typia.assert(logged);

  // 3. Unfiltered list: get all
  const allResult =
    await api.functional.storyfieldAi.systemAdmin.systemAdmins.index(
      connection,
      {
        body: {} satisfies IStoryfieldAiSystemAdmin.IRequest,
      },
    );
  typia.assert(allResult);
  TestValidator.predicate(
    "should return at least adminCount admins",
    allResult.data.length >= adminCount,
  );
  TestValidator.equals(
    "pagination.records",
    allResult.pagination.records,
    allResult.data.length,
  );

  // 4. Filter: by email
  const emailFilterResult =
    await api.functional.storyfieldAi.systemAdmin.systemAdmins.index(
      connection,
      {
        body: {
          email: loginAdmin.email,
        } satisfies IStoryfieldAiSystemAdmin.IRequest,
      },
    );
  typia.assert(emailFilterResult);
  TestValidator.equals(
    "filtered by email: single match",
    emailFilterResult.data.length,
    1,
  );
  TestValidator.equals(
    "filtered by email: match email",
    emailFilterResult.data[0].email,
    loginAdmin.email,
  );

  // Filter: by external_admin_id
  const idFilterResult =
    await api.functional.storyfieldAi.systemAdmin.systemAdmins.index(
      connection,
      {
        body: {
          external_admin_id: loginAdmin.external_admin_id,
        } satisfies IStoryfieldAiSystemAdmin.IRequest,
      },
    );
  typia.assert(idFilterResult);
  TestValidator.equals(
    "filtered by external_admin_id: single match",
    idFilterResult.data.length,
    1,
  );
  TestValidator.equals(
    "filtered by external_admin_id: match id",
    idFilterResult.data[0].id,
    loginAdmin.id,
  );

  // Filter: by actor_type
  const actorTypeFilterResult =
    await api.functional.storyfieldAi.systemAdmin.systemAdmins.index(
      connection,
      {
        body: {
          actor_type: "systemAdmin",
        } satisfies IStoryfieldAiSystemAdmin.IRequest,
      },
    );
  typia.assert(actorTypeFilterResult);
  // Must include all created admins (all have same actor_type)
  TestValidator.predicate(
    "actor_type=systemAdmin includes created admins",
    actorTypeFilterResult.data.some((x) => x.email === loginAdmin.email),
  );

  // Filter: created_from (use the earliest created_at minus 1ms)
  const createdAtEarliest = Math.min(
    ...admins.map((a) => Date.parse(a.created_at)),
  );
  const createdFromDate = new Date(createdAtEarliest - 10).toISOString();
  const createdFromResult =
    await api.functional.storyfieldAi.systemAdmin.systemAdmins.index(
      connection,
      {
        body: {
          created_from: createdFromDate,
        } satisfies IStoryfieldAiSystemAdmin.IRequest,
      },
    );
  typia.assert(createdFromResult);
  // All records should be included
  TestValidator.predicate(
    "created_from includes all admins",
    createdFromResult.data.length >= adminCount,
  );

  // 5. Pagination: limit/page
  const pagedPage1 =
    await api.functional.storyfieldAi.systemAdmin.systemAdmins.index(
      connection,
      {
        body: { page: 1, limit: 3 } satisfies IStoryfieldAiSystemAdmin.IRequest,
      },
    );
  typia.assert(pagedPage1);
  TestValidator.equals(
    "pagination.limit matches request",
    pagedPage1.pagination.limit,
    3,
  );
  TestValidator.equals(
    "pagination.current is 1st page",
    pagedPage1.pagination.current,
    1,
  );
  TestValidator.predicate(
    "page 1 has at most 3 items",
    pagedPage1.data.length <= 3,
  );

  const pagedPage2 =
    await api.functional.storyfieldAi.systemAdmin.systemAdmins.index(
      connection,
      {
        body: { page: 2, limit: 3 } satisfies IStoryfieldAiSystemAdmin.IRequest,
      },
    );
  typia.assert(pagedPage2);
  TestValidator.equals(
    "pagination.current is 2nd page",
    pagedPage2.pagination.current,
    2,
  );
  TestValidator.predicate(
    "page 2 has at most 3 items",
    pagedPage2.data.length <= 3,
  );
  TestValidator.notEquals(
    "page 2 result is not empty when adminCount > limit",
    pagedPage2.data.length,
    0,
  );

  // 6. Negative test: unauthenticated connection
  const unauthConn: api.IConnection = { ...connection, headers: {} };
  await TestValidator.error(
    "unauthenticated system admin search should fail",
    async () => {
      await api.functional.storyfieldAi.systemAdmin.systemAdmins.index(
        unauthConn,
        {
          body: {} satisfies IStoryfieldAiSystemAdmin.IRequest,
        },
      );
    },
  );

  // 7. Negative test: invalid filters
  await TestValidator.error(
    "system admin search with invalid email should fail",
    async () => {
      await api.functional.storyfieldAi.systemAdmin.systemAdmins.index(
        connection,
        {
          body: {
            email: "not-an-email",
          } satisfies IStoryfieldAiSystemAdmin.IRequest,
        },
      );
    },
  );

  await TestValidator.error(
    "system admin search with negative page should fail",
    async () => {
      await api.functional.storyfieldAi.systemAdmin.systemAdmins.index(
        connection,
        {
          body: { page: -1 } satisfies IStoryfieldAiSystemAdmin.IRequest,
        },
      );
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IStoryfieldAiSystemAdmin } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiSystemAdmin";

/**
 * Test retrieval and access control for system administrator detail view.
 *
 * This scenario validates the following business objectives:
 *
 * 1. System admin registration and authorized login
 * 2. System admin can read their own detailed info
 * 3. System admin can read other system admins' details (cross-admin access)
 * 4. Error is thrown when fetching details for a non-existent or deleted admin
 *
 * Stepwise process:
 *
 * 1. Register and login Admin A
 * 2. Register and login Admin B
 * 3. As Admin A, fetch own data and verify fields
 * 4. As Admin A, fetch Admin B's data (cross-access) and verify fields
 * 5. As Admin A, fetch non-existent admin by random UUID - expect error
 */
export async function test_api_system_admin_account_detail_view(
  connection: api.IConnection,
) {
  // 1. Register Admin A
  const externalAdminIdA = RandomGenerator.alphaNumeric(16);
  const emailA = RandomGenerator.name(1) + "@company.com";
  const adminA = await api.functional.auth.systemAdmin.join(connection, {
    body: {
      external_admin_id: externalAdminIdA,
      email: emailA,
      actor_type: "systemAdmin",
    } satisfies IStoryfieldAiSystemAdmin.IJoin,
  });
  typia.assert(adminA);

  // Admin A tokens implicitly set

  // 2. Register Admin B
  const externalAdminIdB = RandomGenerator.alphaNumeric(16);
  const emailB = RandomGenerator.name(1) + "@company.com";
  const adminB = await api.functional.auth.systemAdmin.join(connection, {
    body: {
      external_admin_id: externalAdminIdB,
      email: emailB,
      actor_type: "systemAdmin",
    } satisfies IStoryfieldAiSystemAdmin.IJoin,
  });
  typia.assert(adminB);

  // 3. As Admin A, ensure session by logging in
  await api.functional.auth.systemAdmin.login(connection, {
    body: {
      external_admin_id: externalAdminIdA,
      email: emailA,
    } satisfies IStoryfieldAiSystemAdmin.ILogin,
  });

  // 4. As Admin A, get account detail for self
  const adminSelf =
    await api.functional.storyfieldAi.systemAdmin.systemAdmins.at(connection, {
      systemAdminId: adminA.id,
    });
  typia.assert(adminSelf);
  TestValidator.equals("self account id matches", adminSelf.id, adminA.id);
  TestValidator.equals(
    "self external_admin_id matches",
    adminSelf.external_admin_id,
    externalAdminIdA,
  );
  TestValidator.equals("self email matches", adminSelf.email, emailA);
  TestValidator.equals(
    "self actor_type is systemAdmin",
    adminSelf.actor_type,
    "systemAdmin",
  );
  TestValidator.equals("self deleted_at is null", adminSelf.deleted_at, null);

  // 5. As Admin A, get account detail for Admin B (cross-admin view)
  const adminBCross =
    await api.functional.storyfieldAi.systemAdmin.systemAdmins.at(connection, {
      systemAdminId: adminB.id,
    });
  typia.assert(adminBCross);
  TestValidator.equals("cross admin id matches", adminBCross.id, adminB.id);
  TestValidator.equals(
    "cross admin actor_type is systemAdmin",
    adminBCross.actor_type,
    "systemAdmin",
  );
  TestValidator.equals(
    "cross admin deleted_at is null",
    adminBCross.deleted_at,
    null,
  );

  // 6. As Admin A, get detail for random non-existent admin, expect error
  await TestValidator.error(
    "fetching non-existent admin should throw",
    async () => {
      await api.functional.storyfieldAi.systemAdmin.systemAdmins.at(
        connection,
        {
          systemAdminId: typia.random<string & tags.Format<"uuid">>(),
        },
      );
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IStoryfieldAiAuthenticatedUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiAuthenticatedUser";
import type { IStoryfieldAiStory } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStory";
import type { IStoryfieldAiStoryPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStoryPage";
import type { IStoryfieldAiTtsResult } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiTtsResult";

/**
 * Test authenticated user TTS result detail view access and enforcement.
 *
 * 1. Register a new authenticated user.
 * 2. Log in as that user.
 * 3. Create a new story.
 * 4. Add a page to the story.
 * 5. Create a TTS result for that story page.
 * 6. Retrieve TTS result details by ID and validate key properties for the owner.
 * 7. Attempt detail retrieval with a non-existent TTS result ID and expect error.
 * 8. Register a second user, log in as them, and attempt access to the first
 *    user's TTS result (should fail).
 */
export async function test_api_authenticated_user_story_tts_result_detail_view_access(
  connection: api.IConnection,
) {
  // 1. Register a new authenticated user
  const externalUserId: string = RandomGenerator.alphaNumeric(16);
  const email: string = `${RandomGenerator.alphaNumeric(8)}@example.com`;
  const userJoin = await api.functional.auth.authenticatedUser.join(
    connection,
    {
      body: {
        external_user_id: externalUserId,
        email,
        actor_type: "authenticatedUser",
      },
    },
  );
  typia.assert(userJoin);

  // 2. Log in as the authenticated user
  const auth = await api.functional.auth.authenticatedUser.login(connection, {
    body: {
      external_user_id: externalUserId,
      email,
    },
  });
  typia.assert(auth);

  // 3. Create a story
  const storyTitle = RandomGenerator.paragraph({ sentences: 2 });
  const story =
    await api.functional.storyfieldAi.authenticatedUser.stories.create(
      connection,
      {
        body: {
          title: storyTitle,
          main_plot: RandomGenerator.paragraph({ sentences: 4 }),
          language: "ko-KR",
        },
      },
    );
  typia.assert(story);

  // 4. Add a page to the story
  const page =
    await api.functional.storyfieldAi.authenticatedUser.stories.pages.create(
      connection,
      {
        storyId: story.id,
        body: {
          page_number: 1 as number & tags.Type<"int32">,
          text: RandomGenerator.paragraph({ sentences: 10 }),
        },
      },
    );
  typia.assert(page);

  // 5. Create a TTS result for the page
  const ttsAudioUri = `https://audio.example.com/${RandomGenerator.alphaNumeric(12)}.mp3`;
  const ttsResult =
    await api.functional.storyfieldAi.authenticatedUser.stories.ttsResults.create(
      connection,
      {
        storyId: story.id,
        body: {
          tts_audio_uri: ttsAudioUri,
          source_text: page.text,
          dialect: story.language,
          storyfield_ai_story_page_id: page.id,
        },
      },
    );
  typia.assert(ttsResult);

  // 6. Retrieve TTS result details by ID as owner
  const fetched =
    await api.functional.storyfieldAi.authenticatedUser.stories.ttsResults.at(
      connection,
      {
        storyId: story.id,
        ttsResultId: ttsResult.id,
      },
    );
  typia.assert(fetched);
  TestValidator.equals(
    "tts result detail id matches",
    fetched.id,
    ttsResult.id,
  );
  TestValidator.equals(
    "tts result story id matches",
    fetched.storyfield_ai_story_id,
    story.id,
  );
  TestValidator.equals(
    "tts audio uri matches",
    fetched.tts_audio_uri,
    ttsAudioUri,
  );
  TestValidator.equals(
    "tts result story page id matches",
    fetched.storyfield_ai_story_page_id,
    page.id,
  );

  // 7. Attempt retrieve with non-existent TTS result id
  const fakeTtsId = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.error(
    "non-existent tts result should error",
    async () => {
      await api.functional.storyfieldAi.authenticatedUser.stories.ttsResults.at(
        connection,
        {
          storyId: story.id,
          ttsResultId: fakeTtsId,
        },
      );
    },
  );

  // 8. Register a second authenticated user and attempt access to the first user's TTS result
  const anotherExternalId: string = RandomGenerator.alphaNumeric(16);
  const anotherEmail: string = `${RandomGenerator.alphaNumeric(8)}@test.com`;
  const anotherUser = await api.functional.auth.authenticatedUser.join(
    connection,
    {
      body: {
        external_user_id: anotherExternalId,
        email: anotherEmail,
        actor_type: "authenticatedUser",
      },
    },
  );
  typia.assert(anotherUser);
  const anotherAuth = await api.functional.auth.authenticatedUser.login(
    connection,
    {
      body: {
        external_user_id: anotherExternalId,
        email: anotherEmail,
      },
    },
  );
  typia.assert(anotherAuth);
  await TestValidator.error(
    "non-owner cannot view tts result detail",
    async () => {
      await api.functional.storyfieldAi.authenticatedUser.stories.ttsResults.at(
        connection,
        {
          storyId: story.id,
          ttsResultId: ttsResult.id,
        },
      );
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IStoryfieldAiAuthenticatedUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiAuthenticatedUser";
import type { IStoryfieldAiStory } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStory";
import type { IStoryfieldAiStoryPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStoryPage";
import type { IStoryfieldAiSystemAdmin } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiSystemAdmin";
import type { IStoryfieldAiTtsResult } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiTtsResult";

/**
 * Ensure that a system administrator can view the full details of any TTS
 * result for a story, regardless of ownership.
 *
 * 1. Register and log in a systemAdmin
 * 2. Register and log in an authenticatedUser
 * 3. AuthenticatedUser creates a story
 * 4. AuthenticatedUser adds a page to that story
 * 5. AuthenticatedUser generates a TTS result for the page
 * 6. Log back in as systemAdmin
 * 7. Access the TTS result detail via admin endpoint
 * 8. Validate detail retrieval (all fields match the created record)
 * 9. Try to fetch a non-existent TTSResult (expect error)
 * 10. Log in as unrelated authenticatedUser, try fetch (expect error)
 */
export async function test_api_system_admin_story_tts_result_detail_view_access(
  connection: api.IConnection,
) {
  // 1. Register and login as systemAdmin
  const systemAdmin_external_admin_id = RandomGenerator.alphaNumeric(10);
  const systemAdmin_email = `${RandomGenerator.alphabets(6)}@admin.com`;
  const systemAdminJoin = {
    external_admin_id: systemAdmin_external_admin_id,
    email: systemAdmin_email,
    actor_type: "systemAdmin",
  } satisfies IStoryfieldAiSystemAdmin.IJoin;
  const systemAdminAuth = await api.functional.auth.systemAdmin.join(
    connection,
    { body: systemAdminJoin },
  );
  typia.assert(systemAdminAuth);

  await api.functional.auth.systemAdmin.login(connection, {
    body: {
      external_admin_id: systemAdmin_external_admin_id,
      email: systemAdmin_email,
    } satisfies IStoryfieldAiSystemAdmin.ILogin,
  });

  // 2. Register and login as content-owner authenticatedUser
  const user_external_user_id = RandomGenerator.alphaNumeric(12);
  const user_email = `${RandomGenerator.alphabets(8)}@user.com`;
  const userJoin = {
    external_user_id: user_external_user_id,
    email: user_email,
    actor_type: "authenticatedUser",
  } satisfies IStoryfieldAiAuthenticatedUser.ICreate;
  const userAuth = await api.functional.auth.authenticatedUser.join(
    connection,
    { body: userJoin },
  );
  typia.assert(userAuth);
  await api.functional.auth.authenticatedUser.login(connection, {
    body: {
      external_user_id: user_external_user_id,
      email: user_email,
    } satisfies IStoryfieldAiAuthenticatedUser.ILogin,
  });

  // 3. Create a story
  const storyCreate = {
    title: RandomGenerator.paragraph({ sentences: 3 }),
    main_plot: RandomGenerator.paragraph({ sentences: 6 }),
    language: "ko-KR",
  } satisfies IStoryfieldAiStory.ICreate;
  const story =
    await api.functional.storyfieldAi.authenticatedUser.stories.create(
      connection,
      { body: storyCreate },
    );
  typia.assert(story);

  // 4. Create a story page
  const pageCreate = {
    page_number: 1 as number & tags.Type<"int32">,
    text: RandomGenerator.content({ paragraphs: 1, sentenceMin: 8 }),
  } satisfies IStoryfieldAiStoryPage.ICreate;
  const page =
    await api.functional.storyfieldAi.authenticatedUser.stories.pages.create(
      connection,
      { storyId: story.id, body: pageCreate },
    );
  typia.assert(page);

  // 5. Generate a TTS result for the page
  const ttsResultCreate = {
    tts_audio_uri: `https://audio.example.com/${RandomGenerator.alphaNumeric(12)}.mp3`,
    source_text: page.text,
    dialect: story.language,
    storyfield_ai_story_page_id: page.id,
  } satisfies IStoryfieldAiTtsResult.ICreate;
  const ttsResult =
    await api.functional.storyfieldAi.authenticatedUser.stories.ttsResults.create(
      connection,
      { storyId: story.id, body: ttsResultCreate },
    );
  typia.assert(ttsResult);

  // 6. Log back in as systemAdmin
  await api.functional.auth.systemAdmin.login(connection, {
    body: {
      external_admin_id: systemAdmin_external_admin_id,
      email: systemAdmin_email,
    } satisfies IStoryfieldAiSystemAdmin.ILogin,
  });

  // 7. Fetch TTS result detail as systemAdmin
  const ttsResultDetail =
    await api.functional.storyfieldAi.systemAdmin.stories.ttsResults.at(
      connection,
      {
        storyId: story.id,
        ttsResultId: ttsResult.id,
      },
    );
  typia.assert(ttsResultDetail);
  TestValidator.equals(
    "system admin can get TTS result details",
    ttsResultDetail,
    ttsResult,
  );

  // 8. Try to get non-existent TTSResult (expect error)
  const randomTtsResultId = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.error(
    "system admin get non-existent TTS result",
    async () => {
      await api.functional.storyfieldAi.systemAdmin.stories.ttsResults.at(
        connection,
        {
          storyId: story.id,
          ttsResultId: randomTtsResultId,
        },
      );
    },
  );

  // 9. Create unrelated authenticatedUser, login as this new user
  const another_external_user_id = RandomGenerator.alphaNumeric(12);
  const another_email = `${RandomGenerator.alphabets(8)}@user.com`;
  await api.functional.auth.authenticatedUser.join(connection, {
    body: {
      external_user_id: another_external_user_id,
      email: another_email,
      actor_type: "authenticatedUser",
    } satisfies IStoryfieldAiAuthenticatedUser.ICreate,
  });
  await api.functional.auth.authenticatedUser.login(connection, {
    body: {
      external_user_id: another_external_user_id,
      email: another_email,
    } satisfies IStoryfieldAiAuthenticatedUser.ILogin,
  });

  // 10. Try to access the admin TTS view endpoint as this ordinary user (expect error)
  await TestValidator.error(
    "non-admin/non-owner is denied admin TTS result detail",
    async () => {
      await api.functional.storyfieldAi.systemAdmin.stories.ttsResults.at(
        connection,
        {
          storyId: story.id,
          ttsResultId: ttsResult.id,
        },
      );
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IStoryfieldAiAuthenticatedUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiAuthenticatedUser";
import type { IStoryfieldAiStory } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStory";
import type { IStoryfieldAiStoryPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStoryPage";
import type { IStoryfieldAiTtsResult } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiTtsResult";

/**
 * Validates TTS result creation for an authenticated user's story. Ensures that
 * after onboarding and authentication, the user can create a story, add a page,
 * and then generate a TTS result for that page, and that the system returns the
 * correct TTS result structure. Also checks boundary and error enforcement.
 */
export async function test_api_authenticated_user_story_tts_result_generation(
  connection: api.IConnection,
) {
  // 1. Register a new authenticated user with verified external_user_id and a business-unique email.
  const external_user_id: string = RandomGenerator.alphaNumeric(16);
  const email: string = `${RandomGenerator.alphaNumeric(10)}@autobe-e2e.com`;
  const joinPayload = {
    external_user_id,
    email,
    actor_type: "authenticatedUser",
  } satisfies IStoryfieldAiAuthenticatedUser.ICreate;
  const user: IStoryfieldAiAuthenticatedUser.IAuthorized =
    await api.functional.auth.authenticatedUser.join(connection, {
      body: joinPayload,
    });
  typia.assert(user);
  TestValidator.equals(
    "external_user_id matches",
    user.external_user_id,
    external_user_id,
  );
  TestValidator.equals("email matches", user.email, email);

  // 2. Log in as the created user to construct session context.
  const loginRes = await api.functional.auth.authenticatedUser.login(
    connection,
    {
      body: {
        external_user_id,
        email,
      } satisfies IStoryfieldAiAuthenticatedUser.ILogin,
    },
  );
  typia.assert(loginRes);
  TestValidator.equals("login email", loginRes.email, email);

  // 3. Create a Story associated with user
  const storyBody = {
    title: RandomGenerator.name(3),
    main_plot: RandomGenerator.paragraph({ sentences: 5 }),
    language: "ko-KR",
  } satisfies IStoryfieldAiStory.ICreate;
  const story =
    await api.functional.storyfieldAi.authenticatedUser.stories.create(
      connection,
      {
        body: storyBody,
      },
    );
  typia.assert(story);
  TestValidator.equals("story title matches", story.title, storyBody.title);

  // 4. Add a page to the story
  const pageBody = {
    page_number: 1,
    text: RandomGenerator.paragraph({ sentences: 10 }),
  } satisfies IStoryfieldAiStoryPage.ICreate;
  const page =
    await api.functional.storyfieldAi.authenticatedUser.stories.pages.create(
      connection,
      {
        storyId: story.id,
        body: pageBody,
      },
    );
  typia.assert(page);
  TestValidator.equals(
    "page story id matches",
    page.storyfield_ai_story_id,
    story.id,
  );

  // 5. Submit a TTS result for this page (success path)
  const ttsBody = {
    tts_audio_uri: `https://storage.example.com/audio/${RandomGenerator.alphaNumeric(24)}.mp3`,
    source_text: page.text,
    dialect: story.language,
    storyfield_ai_story_page_id: page.id,
  } satisfies IStoryfieldAiTtsResult.ICreate;
  const ttsResult =
    await api.functional.storyfieldAi.authenticatedUser.stories.ttsResults.create(
      connection,
      {
        storyId: story.id,
        body: ttsBody,
      },
    );
  typia.assert(ttsResult);
  TestValidator.equals(
    "ttsResult.story id matches",
    ttsResult.storyfield_ai_story_id,
    story.id,
  );
  TestValidator.equals(
    "ttsResult.page id matches",
    ttsResult.storyfield_ai_story_page_id,
    page.id,
  );
  TestValidator.equals(
    "ttsResult source text",
    ttsResult.source_text,
    page.text,
  );
  TestValidator.equals(
    "ttsResult dialect matches",
    ttsResult.dialect,
    story.language,
  );
  TestValidator.equals(
    "ttsResult audio URI matches",
    ttsResult.tts_audio_uri,
    ttsBody.tts_audio_uri,
  );

  // 6. Error: Create TTS for non-existent story
  await TestValidator.error("error on non-existent story id", async () => {
    await api.functional.storyfieldAi.authenticatedUser.stories.ttsResults.create(
      connection,
      {
        storyId: typia.random<string & tags.Format<"uuid">>(),
        body: ttsBody,
      },
    );
  });
  // 7. Error: Create TTS for wrong page id (not in story)
  await TestValidator.error(
    "error on invalid storyfield_ai_story_page_id",
    async () => {
      await api.functional.storyfieldAi.authenticatedUser.stories.ttsResults.create(
        connection,
        {
          storyId: story.id,
          body: {
            ...ttsBody,
            storyfield_ai_story_page_id: typia.random<
              string & tags.Format<"uuid">
            >(),
          },
        },
      );
    },
  );
  // 8. Error: Wrong owner - simulate with a second account
  const otherJoin = await api.functional.auth.authenticatedUser.join(
    connection,
    {
      body: {
        external_user_id: RandomGenerator.alphaNumeric(16),
        email: RandomGenerator.alphaNumeric(10) + "@autobe-e2e-2.com",
        actor_type: "authenticatedUser",
      } satisfies IStoryfieldAiAuthenticatedUser.ICreate,
    },
  );
  typia.assert(otherJoin);
  await api.functional.auth.authenticatedUser.login(connection, {
    body: {
      external_user_id: otherJoin.external_user_id,
      email: otherJoin.email,
    } satisfies IStoryfieldAiAuthenticatedUser.ILogin,
  });
  await TestValidator.error(
    "TTS forbidden for other user's story",
    async () => {
      await api.functional.storyfieldAi.authenticatedUser.stories.ttsResults.create(
        connection,
        {
          storyId: story.id,
          body: ttsBody,
        },
      );
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";
import type { IPageIStoryfieldAiStoryPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIStoryfieldAiStoryPage";
import type { IStoryfieldAiAuthenticatedUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiAuthenticatedUser";
import type { IStoryfieldAiStory } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStory";
import type { IStoryfieldAiStoryPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStoryPage";
import type { IStoryfieldAiSystemAdmin } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiSystemAdmin";

/**
 * Validates the paginated and filtered retrieval of story pages by a system
 * admin.
 *
 * This test ensures:
 *
 * 1. System admin user creation and login.
 * 2. Creation of a test story as an authenticated user to verify business workflow
 *    boundaries.
 * 3. Several pages with unique text and page numbers are created for the story by
 *    admin.
 * 4. PATCH (index) endpoint is called with various filters:
 *
 *    - No filter (all pages in story)
 *    - Page number exact match
 *    - Text search matches specific page
 *    - Limit/page (pagination)
 *    - ShowDeleted flag for soft-deleted records
 *    - Search for term with zero matches
 * 5. Tests for each scenario, confirming correct result set, accurate pagination
 *    meta, and all necessary audit fields (created_at, updated_at, etc). Soft
 *    deletion is also checked by deleting a page (simulated by direct
 *    deleted_at setting if API doesn't support real delete).
 */
export async function test_api_story_pages_index_admin_success_and_filtering(
  connection: api.IConnection,
) {
  // 1. Create system admin user and login
  const adminExternalId = RandomGenerator.alphaNumeric(12);
  const adminEmail = `${RandomGenerator.name(1)}@e2e-admin.com`;
  const admin = await api.functional.auth.systemAdmin.join(connection, {
    body: {
      external_admin_id: adminExternalId,
      email: adminEmail,
    } satisfies IStoryfieldAiSystemAdmin.IJoin,
  });
  typia.assert(admin);
  await api.functional.auth.systemAdmin.login(connection, {
    body: {
      external_admin_id: adminExternalId,
      email: adminEmail,
    } satisfies IStoryfieldAiSystemAdmin.ILogin,
  });

  // 2. Create an authenticatedUser and log in (to emulate real resource ownership)
  const externalUserId = RandomGenerator.alphaNumeric(15);
  const userEmail = `${RandomGenerator.name(1)}@e2e.com`;
  await api.functional.auth.authenticatedUser.join(connection, {
    body: {
      external_user_id: externalUserId,
      email: userEmail,
      actor_type: "authenticatedUser",
    } satisfies IStoryfieldAiAuthenticatedUser.ICreate,
  });
  await api.functional.auth.authenticatedUser.login(connection, {
    body: {
      external_user_id: externalUserId,
      email: userEmail,
    } satisfies IStoryfieldAiAuthenticatedUser.ILogin,
  });

  // 3. Create a story as the authenticated user
  const storyTitle = RandomGenerator.name(3);
  const mainPlot = RandomGenerator.paragraph({ sentences: 5 });
  const language = RandomGenerator.pick(["ko", "en", "ê²½ìƒë„"] as const);
  const story =
    await api.functional.storyfieldAi.authenticatedUser.stories.create(
      connection,
      {
        body: {
          title: storyTitle,
          main_plot: mainPlot,
          language,
        } satisfies IStoryfieldAiStory.ICreate,
      },
    );
  typia.assert(story);

  // 4. Log back in as system admin for privileged operations
  await api.functional.auth.systemAdmin.login(connection, {
    body: {
      external_admin_id: adminExternalId,
      email: adminEmail,
    } satisfies IStoryfieldAiSystemAdmin.ILogin,
  });

  // 5. Create 6 story pages with incremented page numbers and known texts
  const texts = ArrayUtil.repeat(
    6,
    (i) =>
      `Page content ${i} - ${RandomGenerator.paragraph({ sentences: 8 + i })}`,
  );
  const createdPages = [] as IStoryfieldAiStoryPage[];
  for (let i = 0; i < texts.length; ++i) {
    const page =
      await api.functional.storyfieldAi.systemAdmin.stories.pages.create(
        connection,
        {
          storyId: story.id,
          body: {
            page_number: (i + 1) as number & tags.Type<"int32">,
            text: texts[i],
          } satisfies IStoryfieldAiStoryPage.ICreate,
        },
      );
    typia.assert(page);
    createdPages.push(page);
  }
  // Simulate soft-delete: Create a "duplicate" page with same page number but modified text.
  await api.functional.storyfieldAi.systemAdmin.stories.pages.create(
    connection,
    {
      storyId: story.id,
      body: {
        page_number: createdPages[1].page_number,
        text: `${createdPages[1].text} (deleted)`,
      } satisfies IStoryfieldAiStoryPage.ICreate,
    },
  );

  // 6. Retrieve all non-deleted story pages (default)
  let resultAll =
    await api.functional.storyfieldAi.systemAdmin.stories.pages.index(
      connection,
      {
        storyId: story.id,
        body: {},
      },
    );
  typia.assert(resultAll);
  const nonDeletedPageNumbers = createdPages
    .filter((_, idx) => idx !== 1)
    .map((x) => x.page_number)
    .sort();
  TestValidator.equals(
    "should fetch all non-deleted story pages",
    resultAll.data.map((x) => x.page_number).sort(),
    nonDeletedPageNumbers,
  );

  // 7. Filter by page_number pagination
  const pagedResult =
    await api.functional.storyfieldAi.systemAdmin.stories.pages.index(
      connection,
      {
        storyId: story.id,
        body: { page: 1, limit: 1 },
      },
    );
  typia.assert(pagedResult);
  TestValidator.equals(
    "pagination: should return single item",
    pagedResult.data.length,
    1,
  );
  TestValidator.equals(
    "pagination: current page",
    pagedResult.pagination.current,
    1,
  );
  TestValidator.equals(
    "pagination: limit respected",
    pagedResult.pagination.limit,
    1,
  );

  // 8. Fulltext search by text
  const searchTerm = RandomGenerator.substring(texts[2]);
  const searchResult =
    await api.functional.storyfieldAi.systemAdmin.stories.pages.index(
      connection,
      {
        storyId: story.id,
        body: { search: searchTerm },
      },
    );
  typia.assert(searchResult);
  TestValidator.predicate(
    "search: every result text includes needle",
    searchResult.data.every((pg) => pg.text.includes(searchTerm)),
  );
  TestValidator.equals(
    "search: correct story id in all results",
    searchResult.data.map((pg) => pg.page_number).sort(),
    createdPages
      .filter((p) => p.text.includes(searchTerm))
      .map((p) => p.page_number)
      .sort(),
  );

  // 9. Filter including soft deleted
  const resultWithDeleted =
    await api.functional.storyfieldAi.systemAdmin.stories.pages.index(
      connection,
      {
        storyId: story.id,
        body: { showDeleted: true },
      },
    );
  typia.assert(resultWithDeleted);
  TestValidator.predicate(
    "showDeleted: result has at least one with deleted_at",
    resultWithDeleted.data.some(
      (pg) => pg.deleted_at !== null && pg.deleted_at !== undefined,
    ),
  );

  // 10. Edge case: filter returns zero pages
  const emptySearch =
    await api.functional.storyfieldAi.systemAdmin.stories.pages.index(
      connection,
      {
        storyId: story.id,
        body: { search: "impossible-search-term-value" },
      },
    );
  typia.assert(emptySearch);
  TestValidator.equals("zero-result search", emptySearch.data.length, 0);

  // 11. Audit/Meta field presence checks
  for (const page of resultAll.data) {
    TestValidator.predicate(
      "page id is uuid",
      typeof page.id === "string" && page.id.length > 0,
    );
    TestValidator.predicate(
      "page number is number",
      typeof page.page_number === "number",
    );
    TestValidator.predicate(
      "page text is string",
      typeof page.text === "string",
    );
    // Check deleted_at presence/absence per showDeleted flag can be added as desired
  }
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IStoryfieldAiAuthenticatedUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiAuthenticatedUser";
import type { IStoryfieldAiStory } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStory";
import type { IStoryfieldAiStoryPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStoryPage";

/**
 * Validate authenticated user page detail access and audit, including negative
 * access control.
 *
 * 1. Register and login as a user (userA)
 * 2. Create a new story
 * 3. Create a page in the story
 * 4. Retrieve and validate details via GET for that page
 * 5. Attempt access with invalid pageId (random UUID)
 * 6. Register another user (userB), login, create their own story/page
 * 7. Switch back to userA, try to get userB's page by storyId/pageId: should fail
 */
export async function test_api_story_page_detail_access_control_and_audit(
  connection: api.IConnection,
) {
  // 1. Register userA
  const userAJoinInput: IStoryfieldAiAuthenticatedUser.ICreate = {
    external_user_id: RandomGenerator.alphaNumeric(16),
    email: `${RandomGenerator.alphaNumeric(8)}@autotest.com`,
    actor_type: "authenticatedUser",
  };
  const userA: IStoryfieldAiAuthenticatedUser.IAuthorized =
    await api.functional.auth.authenticatedUser.join(connection, {
      body: userAJoinInput,
    });
  typia.assert(userA);

  // 2. Login userA to refresh token and headers (SDK sets header)
  const loginA = await api.functional.auth.authenticatedUser.login(connection, {
    body: {
      external_user_id: userA.external_user_id,
      email: userA.email,
    },
  });
  typia.assert(loginA);

  // 3. Create a story as userA
  const storyInput: IStoryfieldAiStory.ICreate = {
    title: RandomGenerator.paragraph({ sentences: 3 }),
    main_plot: RandomGenerator.paragraph({ sentences: 5 }),
    language: "ko-KR",
  };
  const story: IStoryfieldAiStory =
    await api.functional.storyfieldAi.authenticatedUser.stories.create(
      connection,
      { body: storyInput },
    );
  typia.assert(story);
  TestValidator.equals(
    "created story is for userA",
    story.storyfield_ai_authenticateduser_id,
    userA.id,
  );
  TestValidator.equals("story title matches", story.title, storyInput.title);

  // 4. Create a story page in userA's story
  const pageInput: IStoryfieldAiStoryPage.ICreate = {
    page_number: 1,
    text: RandomGenerator.content({ paragraphs: 2 }),
  };
  const page: IStoryfieldAiStoryPage =
    await api.functional.storyfieldAi.authenticatedUser.stories.pages.create(
      connection,
      {
        storyId: story.id,
        body: pageInput,
      },
    );
  typia.assert(page);
  TestValidator.equals(
    "page created is for story",
    page.storyfield_ai_story_id,
    story.id,
  );
  TestValidator.equals("page_number", page.page_number, pageInput.page_number);
  TestValidator.equals("page text", page.text, pageInput.text);

  // 5. Retrieve the created page (should succeed)
  const got: IStoryfieldAiStoryPage =
    await api.functional.storyfieldAi.authenticatedUser.stories.pages.at(
      connection,
      {
        storyId: story.id,
        pageId: page.id,
      },
    );
  typia.assert(got);
  TestValidator.equals("retrieved page matches created page", got, page);

  // 6. Negative: Try to get a page with invalid pageId (random UUID in same story)
  await TestValidator.error("get on non-existent pageId fails", async () => {
    await api.functional.storyfieldAi.authenticatedUser.stories.pages.at(
      connection,
      {
        storyId: story.id,
        pageId: typia.random<string & tags.Format<"uuid">>(),
      },
    );
  });

  // 7. Register userB (with different external_user_id/email)
  const userBJoinInput: IStoryfieldAiAuthenticatedUser.ICreate = {
    external_user_id: RandomGenerator.alphaNumeric(16),
    email: `${RandomGenerator.alphaNumeric(8)}@autotest.com`,
    actor_type: "authenticatedUser",
  };
  const userB: IStoryfieldAiAuthenticatedUser.IAuthorized =
    await api.functional.auth.authenticatedUser.join(connection, {
      body: userBJoinInput,
    });
  typia.assert(userB);

  // login as userB
  const loginB = await api.functional.auth.authenticatedUser.login(connection, {
    body: {
      external_user_id: userB.external_user_id,
      email: userB.email,
    },
  });
  typia.assert(loginB);

  // Create story and page for userB
  const storyBInput: IStoryfieldAiStory.ICreate = {
    title: RandomGenerator.paragraph({ sentences: 3 }),
    main_plot: RandomGenerator.paragraph({ sentences: 5 }),
    language: "en-US",
  };
  const storyB: IStoryfieldAiStory =
    await api.functional.storyfieldAi.authenticatedUser.stories.create(
      connection,
      { body: storyBInput },
    );
  typia.assert(storyB);

  const pageBInput: IStoryfieldAiStoryPage.ICreate = {
    page_number: 1,
    text: RandomGenerator.content({ paragraphs: 1 }),
  };
  const pageB: IStoryfieldAiStoryPage =
    await api.functional.storyfieldAi.authenticatedUser.stories.pages.create(
      connection,
      {
        storyId: storyB.id,
        body: pageBInput,
      },
    );
  typia.assert(pageB);

  // 8. Switch back to userA for negative access control (login again)
  const reloginA = await api.functional.auth.authenticatedUser.login(
    connection,
    {
      body: {
        external_user_id: userA.external_user_id,
        email: userA.email,
      },
    },
  );
  typia.assert(reloginA);

  // 9. Try to fetch page from userB's story as userA (must be access denied)
  await TestValidator.error("userA cannot access userB's page", async () => {
    await api.functional.storyfieldAi.authenticatedUser.stories.pages.at(
      connection,
      {
        storyId: storyB.id,
        pageId: pageB.id,
      },
    );
  });
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IStoryfieldAiAuthenticatedUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiAuthenticatedUser";
import type { IStoryfieldAiStory } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStory";
import type { IStoryfieldAiStoryPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStoryPage";
import type { IStoryfieldAiSystemAdmin } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiSystemAdmin";

/**
 * Validate system admin privilege to retrieve any story page detail and
 * compliance fields.
 *
 * 1. Register and login as system admin (to establish admin context).
 * 2. Create a new authenticated user and login as that user.
 * 3. User creates a story and a single page for that story.
 * 4. Switch back to system admin session.
 * 5. Admin fetches the detail of the story page with GET
 *    /storyfieldAi/systemAdmin/stories/{storyId}/pages/{pageId}.
 * 6. Ensure all expected fields and compliance/audit fields (like deleted_at)
 *    are present.
 * 7. Confirm content fields match those originally created.
 * 8. Attempt to retrieve a non-existent pageId and verify error handling.
 * 9. Optionally: Soft delete the story page (if possible; skip if no API
 *    exists), then attempt to get it again as admin, ensuring admin can
 *    still see audit/deleted fields or correct error response.
 */
export async function test_api_story_page_detail_admin_privilege_and_compliance(
  connection: api.IConnection,
) {
  // 1. Register and login as system admin
  const admin_email = RandomGenerator.alphabets(10) + "@company.com";
  const admin_external_id = RandomGenerator.alphaNumeric(12);
  await api.functional.auth.systemAdmin.join(connection, {
    body: {
      email: admin_email,
      external_admin_id: admin_external_id,
      actor_type: "systemAdmin",
    },
  });
  await api.functional.auth.systemAdmin.login(connection, {
    body: {
      email: admin_email,
      external_admin_id: admin_external_id,
    },
  });

  // 2. Register and login as an authenticated user
  const user_email = RandomGenerator.alphabets(10) + "@demo.test";
  const user_external_id = RandomGenerator.alphaNumeric(14);
  await api.functional.auth.authenticatedUser.join(connection, {
    body: {
      email: user_email,
      external_user_id: user_external_id,
      actor_type: "authenticatedUser",
    },
  });
  await api.functional.auth.authenticatedUser.login(connection, {
    body: {
      email: user_email,
      external_user_id: user_external_id,
    },
  });

  // 3. User creates a story
  const storyTitle = RandomGenerator.paragraph({ sentences: 3 });
  const storyMainPlot = RandomGenerator.paragraph({ sentences: 5 });
  const storyLanguage = "ko-KR";
  const story =
    await api.functional.storyfieldAi.authenticatedUser.stories.create(
      connection,
      {
        body: {
          title: storyTitle,
          main_plot: storyMainPlot,
          language: storyLanguage,
        },
      },
    );
  typia.assert(story);

  // 4. User creates a story page
  const page =
    await api.functional.storyfieldAi.authenticatedUser.stories.pages.create(
      connection,
      {
        storyId: story.id,
        body: {
          page_number: 1,
          text: RandomGenerator.content({
            paragraphs: 1,
            sentenceMin: 5,
            sentenceMax: 10,
          }),
        },
      },
    );
  typia.assert(page);

  // 5. Switch back to system admin session
  await api.functional.auth.systemAdmin.login(connection, {
    body: {
      email: admin_email,
      external_admin_id: admin_external_id,
    },
  });

  // 6. Admin fetches the story page detail
  const pageRead =
    await api.functional.storyfieldAi.systemAdmin.stories.pages.at(connection, {
      storyId: story.id,
      pageId: page.id,
    });
  typia.assert(pageRead);
  TestValidator.equals(
    "page fields: id, storyfield_ai_story_id, page_number, text",
    pageRead.id,
    page.id,
  );
  TestValidator.equals(
    "story-page parent matches",
    pageRead.storyfield_ai_story_id,
    story.id,
  );
  TestValidator.equals(
    "page_number matches",
    pageRead.page_number,
    page.page_number,
  );
  TestValidator.equals("text matches", pageRead.text, page.text);
  TestValidator.equals(
    "created_at is present",
    typeof pageRead.created_at,
    "string",
  );
  TestValidator.equals(
    "updated_at is present",
    typeof pageRead.updated_at,
    "string",
  );
  // deleted_at is nullable/optional; default should be absent/null
  TestValidator.equals(
    "deleted_at should be null or undefined",
    pageRead.deleted_at ?? null,
    null,
  );

  // 7. Try fetching a non-existent page (random uuid)
  await TestValidator.error(
    "admin: non-existent pageId returns error",
    async () => {
      await api.functional.storyfieldAi.systemAdmin.stories.pages.at(
        connection,
        {
          storyId: story.id,
          pageId: typia.random<string & tags.Format<"uuid">>(),
        },
      );
    },
  );

  await TestValidator.error(
    "admin: non-existent storyId returns error",
    async () => {
      await api.functional.storyfieldAi.systemAdmin.stories.pages.at(
        connection,
        {
          storyId: typia.random<string & tags.Format<"uuid">>(),
          pageId: page.id,
        },
      );
    },
  );

  // Optionally: Soft delete logic could go here if supported
  // If an API for deleting story pages existed, we would soft delete then attempt read as admin again
  // (No such endpoint in current materials.)
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IStoryfieldAiAuthenticatedUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiAuthenticatedUser";
import type { IStoryfieldAiStory } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStory";
import type { IStoryfieldAiStoryPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStoryPage";

/**
 * Test story page creation and business validation.
 *
 * 1. Register and login a user (UserA)
 * 2. UserA creates a story
 * 3. UserA creates a valid page for their story. Verify association (story_id
 *    match), page_number/text correct, and audit fields are present.
 * 4. Attempt to add page to non-existent story; expect error.
 * 5. Register/login a second user (UserB), create a story with UserB. Switch back
 *    to UserA and try to add page to UserB's storyâ€”expect error.
 * 6. Duplicate page_number for same story: expect error.
 */
export async function test_api_story_page_creation_and_validation(
  connection: api.IConnection,
) {
  // UserA registration and login
  const userA_external_id = RandomGenerator.alphaNumeric(16);
  const userA_email = `${RandomGenerator.name(1)}${RandomGenerator.alphaNumeric(2)}@test.com`;
  await api.functional.auth.authenticatedUser.join(connection, {
    body: {
      external_user_id: userA_external_id,
      email: userA_email,
      actor_type: "authenticatedUser",
    } satisfies IStoryfieldAiAuthenticatedUser.ICreate,
  });
  await api.functional.auth.authenticatedUser.login(connection, {
    body: {
      external_user_id: userA_external_id,
      email: userA_email,
    } satisfies IStoryfieldAiAuthenticatedUser.ILogin,
  });

  // UserA creates a story
  const story =
    await api.functional.storyfieldAi.authenticatedUser.stories.create(
      connection,
      {
        body: {
          title: RandomGenerator.paragraph({ sentences: 2 }),
          language: RandomGenerator.pick(["ko-KR", "en", "ja", "zh"] as const),
          main_plot: RandomGenerator.paragraph(),
        } satisfies IStoryfieldAiStory.ICreate,
      },
    );
  typia.assert(story);

  // 1. UserA adds a valid story page
  const page_number = 1; // start with first page
  const page_text = RandomGenerator.content({
    paragraphs: 1,
    sentenceMin: 5,
    sentenceMax: 8,
  });
  const page =
    await api.functional.storyfieldAi.authenticatedUser.stories.pages.create(
      connection,
      {
        storyId: story.id,
        body: {
          page_number,
          text: page_text,
        } satisfies IStoryfieldAiStoryPage.ICreate,
      },
    );
  typia.assert(page);
  TestValidator.equals(
    "page is associated with the correct story",
    page.storyfield_ai_story_id,
    story.id,
  );
  TestValidator.equals("page_number is correct", page.page_number, page_number);
  TestValidator.equals("text is correct", page.text, page_text);
  TestValidator.predicate(
    "created_at is ISO 8601",
    typeof page.created_at === "string" && /T.*Z$/.test(page.created_at),
  );
  TestValidator.predicate(
    "updated_at is ISO 8601",
    typeof page.updated_at === "string" && /T.*Z$/.test(page.updated_at),
  );
  TestValidator.equals(
    "deleted_at should be null or undefined",
    page.deleted_at,
    null,
  );

  // 2. Attempt to add page to non-existent story
  await TestValidator.error(
    "cannot add page to non-existent story",
    async () => {
      await api.functional.storyfieldAi.authenticatedUser.stories.pages.create(
        connection,
        {
          storyId: typia.random<string & tags.Format<"uuid">>(),
          body: {
            page_number: 2,
            text: RandomGenerator.content(),
          } satisfies IStoryfieldAiStoryPage.ICreate,
        },
      );
    },
  );

  // 3. Create UserB and corresponding story
  const userB_external_id = RandomGenerator.alphaNumeric(16);
  const userB_email = `${RandomGenerator.name(1)}${RandomGenerator.alphaNumeric(2)}@test.com`;
  await api.functional.auth.authenticatedUser.join(connection, {
    body: {
      external_user_id: userB_external_id,
      email: userB_email,
      actor_type: "authenticatedUser",
    } satisfies IStoryfieldAiAuthenticatedUser.ICreate,
  });
  await api.functional.auth.authenticatedUser.login(connection, {
    body: {
      external_user_id: userB_external_id,
      email: userB_email,
    } satisfies IStoryfieldAiAuthenticatedUser.ILogin,
  });
  const storyB =
    await api.functional.storyfieldAi.authenticatedUser.stories.create(
      connection,
      {
        body: {
          title: RandomGenerator.paragraph({ sentences: 2 }),
          language: RandomGenerator.pick(["ko-KR", "en", "ja", "zh"] as const),
          main_plot: RandomGenerator.paragraph(),
        } satisfies IStoryfieldAiStory.ICreate,
      },
    );
  typia.assert(storyB);

  // Switch back to UserA
  await api.functional.auth.authenticatedUser.login(connection, {
    body: {
      external_user_id: userA_external_id,
      email: userA_email,
    } satisfies IStoryfieldAiAuthenticatedUser.ILogin,
  });

  // Try to add page to UserB's story
  await TestValidator.error(
    "cannot add page to a story owned by another user",
    async () => {
      await api.functional.storyfieldAi.authenticatedUser.stories.pages.create(
        connection,
        {
          storyId: storyB.id,
          body: {
            page_number: 1,
            text: RandomGenerator.content({ paragraphs: 1 }),
          } satisfies IStoryfieldAiStoryPage.ICreate,
        },
      );
    },
  );

  // 4. Duplicate page_number for the same story
  await TestValidator.error(
    "cannot add page with duplicate page_number to same story",
    async () => {
      await api.functional.storyfieldAi.authenticatedUser.stories.pages.create(
        connection,
        {
          storyId: story.id,
          body: {
            page_number, // already used for existing page
            text: RandomGenerator.content({ paragraphs: 1 }),
          } satisfies IStoryfieldAiStoryPage.ICreate,
        },
      );
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IStoryfieldAiAuthenticatedUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiAuthenticatedUser";
import type { IStoryfieldAiStory } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStory";
import type { IStoryfieldAiStoryPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStoryPage";
import type { IStoryfieldAiSystemAdmin } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiSystemAdmin";

/**
 * Validate system admin soft-deletion of a user's story page, including full
 * access and authorization scenarios. Covers admin deletion, error flow for
 * repeated deletion, non-existent page deletion, and non-admin attempts. No
 * detail query for post-soft-deletion is possible (no SDK endpoint) but full
 * API error/permission flows are validated.
 *
 * Steps:
 *
 * 1. Register systemAdmin and authenticatedUser test actors (unique per test)
 * 2. As authenticatedUser, create a new story and page
 * 3. Switch to systemAdmin, soft-delete the page by erase endpoint
 * 4. Attempt to delete again (should fail: already deleted)
 * 5. Attempt to delete random (non-existent) page (should fail)
 * 6. Switch to authenticatedUser, attempt deletion (should fail: not authorized)
 */
export async function test_api_system_admin_soft_delete_story_page_success(
  connection: api.IConnection,
) {
  // 1. Register actors
  const external_admin_id: string = RandomGenerator.alphaNumeric(12);
  const admin_email: string = `${RandomGenerator.name(1)}@admin.com`;
  const external_user_id: string = RandomGenerator.alphaNumeric(12);
  const user_email: string = `${RandomGenerator.name(1)}@user.com`;

  // Register & login as systemAdmin
  const admin: IStoryfieldAiSystemAdmin.IAuthorized =
    await api.functional.auth.systemAdmin.join(connection, {
      body: {
        external_admin_id,
        email: admin_email,
        actor_type: "systemAdmin",
      },
    });
  typia.assert(admin);

  // Register & login as authenticated user
  const user: IStoryfieldAiAuthenticatedUser.IAuthorized =
    await api.functional.auth.authenticatedUser.join(connection, {
      body: {
        external_user_id,
        email: user_email,
        actor_type: "authenticatedUser",
      },
    });
  typia.assert(user);

  // Login as authenticated user (to set connection context)
  await api.functional.auth.authenticatedUser.login(connection, {
    body: {
      external_user_id,
      email: user_email,
    },
  });

  // 2. Create story and a page as user
  const storyBody = {
    title: RandomGenerator.name(2),
    main_plot: RandomGenerator.paragraph(),
    language: "ko",
  } satisfies IStoryfieldAiStory.ICreate;
  const story: IStoryfieldAiStory =
    await api.functional.storyfieldAi.authenticatedUser.stories.create(
      connection,
      {
        body: storyBody,
      },
    );
  typia.assert(story);

  const pageBody = {
    page_number: 1,
    text: RandomGenerator.paragraph({ sentences: 5 }),
  } satisfies IStoryfieldAiStoryPage.ICreate;
  const page: IStoryfieldAiStoryPage =
    await api.functional.storyfieldAi.authenticatedUser.stories.pages.create(
      connection,
      {
        storyId: story.id,
        body: pageBody,
      },
    );
  typia.assert(page);

  // 3. Switch to systemAdmin, soft-delete the page
  await api.functional.auth.systemAdmin.login(connection, {
    body: {
      external_admin_id,
      email: admin_email,
    },
  });

  await api.functional.storyfieldAi.systemAdmin.stories.pages.erase(
    connection,
    {
      storyId: story.id,
      pageId: page.id,
    },
  );

  // 4. Attempt to delete again (already soft-deleted)
  await TestValidator.error(
    "systemAdmin cannot delete already soft-deleted page",
    async () => {
      await api.functional.storyfieldAi.systemAdmin.stories.pages.erase(
        connection,
        {
          storyId: story.id,
          pageId: page.id,
        },
      );
    },
  );

  // 5. Attempt to delete non-existent page
  const fakePageId = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.error(
    "systemAdmin cannot delete non-existent page",
    async () => {
      await api.functional.storyfieldAi.systemAdmin.stories.pages.erase(
        connection,
        {
          storyId: story.id,
          pageId: fakePageId,
        },
      );
    },
  );

  // 6. Switch to authenticatedUser, attempt deletion (should fail)
  await api.functional.auth.authenticatedUser.login(connection, {
    body: {
      external_user_id,
      email: user_email,
    },
  });
  await TestValidator.error(
    "authenticatedUser cannot soft-delete story page as non-admin",
    async () => {
      await api.functional.storyfieldAi.systemAdmin.stories.pages.erase(
        connection,
        {
          storyId: story.id,
          pageId: page.id,
        },
      );
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";
import type { IPageIStoryfieldAiStoryImage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIStoryfieldAiStoryImage";
import type { IStoryfieldAiAuthenticatedUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiAuthenticatedUser";
import type { IStoryfieldAiStory } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStory";
import type { IStoryfieldAiStoryImage } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStoryImage";
import type { IStoryfieldAiStoryPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStoryPage";

/**
 * This test verifies the paginated and filtered image listing for an
 * authenticated user's story. Steps:
 *
 * 1. Register and login as a new authenticated user
 * 2. Create a story
 * 3. Create a page for the story
 * 4. Upload two images: one story-wide, one page-specific
 * 5. List all images and verify both are present
 * 6. List images filtered by page and verify only the page image is present
 * 7. Attempt to list images for invalid story or page (error or empty as
 *    appropriate)
 * 8. Attempt to get image list for a different user's story (should fail)
 * 9. Test pagination logic with limit=1
 * 10. Soft-delete exclusion cannot be tested (API lacks delete), so is skipped
 */
export async function test_api_authenticated_user_story_images_list_with_story_and_page_filter(
  connection: api.IConnection,
) {
  // 1. Register and login
  const userExternalId = RandomGenerator.alphaNumeric(8);
  const userEmail = `${RandomGenerator.name(1)}_${RandomGenerator.alphaNumeric(5)}@domain.com`;
  const join = await api.functional.auth.authenticatedUser.join(connection, {
    body: {
      external_user_id: userExternalId,
      email: userEmail,
      actor_type: "authenticatedUser",
    },
  });
  typia.assert(join);

  const login = await api.functional.auth.authenticatedUser.login(connection, {
    body: {
      external_user_id: userExternalId,
      email: userEmail,
    },
  });
  typia.assert(login);

  // 2. Create a story
  const story =
    await api.functional.storyfieldAi.authenticatedUser.stories.create(
      connection,
      {
        body: {
          title: RandomGenerator.paragraph({ sentences: 2 }),
          main_plot: RandomGenerator.paragraph({ sentences: 4 }),
          language: RandomGenerator.pick(["ko", "en"] as const),
        },
      },
    );
  typia.assert(story);

  // 3. Create a page in the story
  const page =
    await api.functional.storyfieldAi.authenticatedUser.stories.pages.create(
      connection,
      {
        storyId: story.id,
        body: {
          page_number: 1,
          text: RandomGenerator.paragraph({ sentences: 5 }),
        },
      },
    );
  typia.assert(page);

  // 4. Add two images: story-wide and page-specific
  const imgA =
    await api.functional.storyfieldAi.authenticatedUser.stories.images.create(
      connection,
      {
        storyId: story.id,
        body: {
          storyfield_ai_story_id: story.id,
          image_uri: `https://image.cdn/${RandomGenerator.alphaNumeric(10)}.jpg`,
          description: "Story-wide illustration",
        },
      },
    );
  typia.assert(imgA);

  const imgB =
    await api.functional.storyfieldAi.authenticatedUser.stories.images.create(
      connection,
      {
        storyId: story.id,
        body: {
          storyfield_ai_story_id: story.id,
          storyfield_ai_story_page_id: page.id,
          image_uri: `https://image.cdn/${RandomGenerator.alphaNumeric(10)}.jpg`,
          description: "Page-specific illustration",
        },
      },
    );
  typia.assert(imgB);

  // 5. Unfiltered image list (should contain both images)
  const pageUnfiltered =
    await api.functional.storyfieldAi.authenticatedUser.stories.images.index(
      connection,
      {
        storyId: story.id,
        body: {},
      },
    );
  typia.assert(pageUnfiltered);
  TestValidator.predicate(
    "image A is included in full image list",
    pageUnfiltered.data.some((img) => img.id === imgA.id),
  );
  TestValidator.predicate(
    "image B is included in full image list",
    pageUnfiltered.data.some((img) => img.id === imgB.id),
  );

  // 6. Image list filtered by page (only imgB expected)
  const paged =
    await api.functional.storyfieldAi.authenticatedUser.stories.images.index(
      connection,
      {
        storyId: story.id,
        body: {
          storyfield_ai_story_page_id: page.id,
        },
      },
    );
  typia.assert(paged);
  TestValidator.predicate(
    "only page-specific image is in page filter list",
    paged.data.length === 1 && paged.data[0].id === imgB.id,
  );

  // 7. Invalid storyId returns error
  await TestValidator.error(
    "listing images with invalid story id fails",
    async () => {
      await api.functional.storyfieldAi.authenticatedUser.stories.images.index(
        connection,
        {
          storyId: typia.random<string & tags.Format<"uuid">>(),
          body: {},
        },
      );
    },
  );

  // 8. Invalid page id returns empty results
  const invalidPage =
    await api.functional.storyfieldAi.authenticatedUser.stories.images.index(
      connection,
      {
        storyId: story.id,
        body: {
          storyfield_ai_story_page_id: typia.random<
            string & tags.Format<"uuid">
          >(),
        },
      },
    );
  typia.assert(invalidPage);
  TestValidator.equals(
    "filtering by random page id yields no results",
    invalidPage.data.length,
    0,
  );

  // 9. Switch to a new user and verify cannot see first user's story images
  const user2_extid = RandomGenerator.alphaNumeric(8);
  const user2_email = `${RandomGenerator.name(1)}_${RandomGenerator.alphaNumeric(5)}@domain.com`;
  await api.functional.auth.authenticatedUser.join(connection, {
    body: {
      external_user_id: user2_extid,
      email: user2_email,
      actor_type: "authenticatedUser",
    },
  });
  await api.functional.auth.authenticatedUser.login(connection, {
    body: {
      external_user_id: user2_extid,
      email: user2_email,
    },
  });
  await TestValidator.error(
    "2nd user cannot access another user's story images",
    async () => {
      await api.functional.storyfieldAi.authenticatedUser.stories.images.index(
        connection,
        {
          storyId: story.id,
          body: {},
        },
      );
    },
  );

  // 10. Test pagination with limit=1
  await api.functional.auth.authenticatedUser.login(connection, {
    body: {
      external_user_id: userExternalId,
      email: userEmail,
    },
  });
  const paginated =
    await api.functional.storyfieldAi.authenticatedUser.stories.images.index(
      connection,
      {
        storyId: story.id,
        body: {
          limit: 1,
        },
      },
    );
  typia.assert(paginated);
  TestValidator.equals(
    "pagination with limit returns single image",
    paginated.data.length,
    1,
  );
  TestValidator.equals(
    "pagination meta limit equals requested limit",
    paginated.pagination.limit,
    1,
  );
  TestValidator.equals(
    "pagination meta 'current' is 0 (first page)",
    paginated.pagination.current,
    0,
  );
  // Cannot test soft delete exclusion (API lacks delete)
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IStoryfieldAiAuthenticatedUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiAuthenticatedUser";
import type { IStoryfieldAiStory } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStory";
import type { IStoryfieldAiStoryImage } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStoryImage";

/**
 * Validates authenticated user's ability to get their own story image details,
 * cross-ownership denial, and error handling.
 *
 * 1. Register and login as user A.
 * 2. Create a story as user A.
 * 3. Add a story image as user A.
 * 4. Retrieve the detail for the created image by user A (should succeed).
 * 5. Register and login as user B (other user).
 * 6. Attempt to retrieve the image via user B's credentials (should fail).
 * 7. Attempt to retrieve a non-existent imageId (should fail).
 * 8. [Optional] Simulate soft-delete scenario and confirm access is denied to user
 *    A (if API returns error).
 */
export async function test_api_authenticated_user_view_story_image_detail_self_access_and_ownership(
  connection: api.IConnection,
) {
  // Step 1: Register user A
  const userAExternalId = RandomGenerator.alphaNumeric(16);
  const userAEmail = `${RandomGenerator.name(1)}@storyfield.com`;
  const userACreate = {
    external_user_id: userAExternalId,
    email: userAEmail,
    actor_type: "authenticatedUser",
  } satisfies IStoryfieldAiAuthenticatedUser.ICreate;
  const userAAuthorized = await api.functional.auth.authenticatedUser.join(
    connection,
    { body: userACreate },
  );
  typia.assert(userAAuthorized);

  // Step 2: Login as user A (session update)
  const userALogin = {
    external_user_id: userAExternalId,
    email: userAEmail,
  } satisfies IStoryfieldAiAuthenticatedUser.ILogin;
  const userALoginAuthorized =
    await api.functional.auth.authenticatedUser.login(connection, {
      body: userALogin,
    });
  typia.assert(userALoginAuthorized);

  // Step 3: User A creates a story
  const storyCreate = {
    title: RandomGenerator.paragraph({ sentences: 3 }),
    main_plot: RandomGenerator.content({ paragraphs: 1 }),
    language: "ko-KR",
  } satisfies IStoryfieldAiStory.ICreate;
  const story =
    await api.functional.storyfieldAi.authenticatedUser.stories.create(
      connection,
      { body: storyCreate },
    );
  typia.assert(story);

  // Step 4: User A adds an image to the story
  const imageUri = `https://cdn.storyfield.ai/${RandomGenerator.alphaNumeric(16)}.png`;
  const imageCreate = {
    storyfield_ai_story_id: story.id,
    image_uri: imageUri,
    description: RandomGenerator.paragraph({ sentences: 5 }),
  } satisfies IStoryfieldAiStoryImage.ICreate;
  const createdImage =
    await api.functional.storyfieldAi.authenticatedUser.stories.images.create(
      connection,
      {
        storyId: story.id,
        body: imageCreate,
      },
    );
  typia.assert(createdImage);

  // Step 5: Retrieve the image detail as user A
  const imageDetail =
    await api.functional.storyfieldAi.authenticatedUser.stories.images.at(
      connection,
      {
        storyId: story.id,
        imageId: createdImage.id,
      },
    );
  typia.assert(imageDetail);
  TestValidator.equals(
    "story id matches",
    imageDetail.storyfield_ai_story_id,
    story.id,
  );
  TestValidator.equals("image id matches", imageDetail.id, createdImage.id);
  TestValidator.equals("image uri matches", imageDetail.image_uri, imageUri);
  TestValidator.equals(
    "description matches",
    imageDetail.description,
    imageCreate.description,
  );
  TestValidator.predicate(
    "created_at set",
    typeof imageDetail.created_at === "string" &&
      imageDetail.created_at.length > 0,
  );
  TestValidator.predicate(
    "updated_at set",
    typeof imageDetail.updated_at === "string" &&
      imageDetail.updated_at.length > 0,
  );
  TestValidator.equals("not deleted", imageDetail.deleted_at, null);

  // Step 6: Register and login as user B (other user)
  const userBExternalId = RandomGenerator.alphaNumeric(16);
  const userBEmail = `${RandomGenerator.name(1)}@storyfield.com`;
  const userBCreate = {
    external_user_id: userBExternalId,
    email: userBEmail,
    actor_type: "authenticatedUser",
  } satisfies IStoryfieldAiAuthenticatedUser.ICreate;
  await api.functional.auth.authenticatedUser.join(connection, {
    body: userBCreate,
  });

  const userBLogin = {
    external_user_id: userBExternalId,
    email: userBEmail,
  } satisfies IStoryfieldAiAuthenticatedUser.ILogin;
  await api.functional.auth.authenticatedUser.login(connection, {
    body: userBLogin,
  });

  // Step 7: Attempt to fetch the image as user B (should fail)
  await TestValidator.error(
    "cross-user cannot fetch other user's story image detail",
    async () => {
      await api.functional.storyfieldAi.authenticatedUser.stories.images.at(
        connection,
        {
          storyId: story.id,
          imageId: createdImage.id,
        },
      );
    },
  );

  // Step 8: Attempt to fetch an invalid/non-existent imageId (should fail)
  await TestValidator.error(
    "non-existent imageId cannot be fetched",
    async () => {
      await api.functional.storyfieldAi.authenticatedUser.stories.images.at(
        connection,
        {
          storyId: story.id,
          imageId: typia.random<string & tags.Format<"uuid">>(),
        },
      );
    },
  );

  // [Optional] Additional: TODO if API allows, try to simulate soft-deletion and fetch to confirm denial, else skip.
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IStoryfieldAiAuthenticatedUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiAuthenticatedUser";
import type { IStoryfieldAiStory } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStory";

/**
 * Validate permanent deletion (hard delete) of a story by owner authenticated
 * user.
 *
 * Steps:
 *
 * 1. Register and login as authenticated user A (story owner).
 * 2. Create a new AI-generated story as user A.
 * 3. Delete the story as user A (should succeed).
 * 4. Attempt to delete the same story again as user A (should fail: already
 *    deleted).
 * 5. Register/login as a different user B (not owner).
 * 6. Attempt to delete the same story as user B (should fail: forbidden).
 */
export async function test_api_story_deletion_by_owner_authenticated_user(
  connection: api.IConnection,
) {
  // 1. Register user A
  const userACreate = {
    external_user_id: RandomGenerator.alphaNumeric(12),
    email: `${RandomGenerator.alphaNumeric(6)}@test-owner.com`,
    actor_type: "authenticatedUser",
  } satisfies IStoryfieldAiAuthenticatedUser.ICreate;
  const userAAuthorized = await api.functional.auth.authenticatedUser.join(
    connection,
    { body: userACreate },
  );
  typia.assert(userAAuthorized);

  // 2. Login as user A
  const userALogin = {
    external_user_id: userACreate.external_user_id,
    email: userACreate.email,
  } satisfies IStoryfieldAiAuthenticatedUser.ILogin;
  const userALoggedIn = await api.functional.auth.authenticatedUser.login(
    connection,
    { body: userALogin },
  );
  typia.assert(userALoggedIn);

  // 3. Create story as user A
  const storyCreate = {
    title: RandomGenerator.paragraph({ sentences: 2 }),
    main_plot: RandomGenerator.paragraph(),
    language: "en",
  } satisfies IStoryfieldAiStory.ICreate;
  const story =
    await api.functional.storyfieldAi.authenticatedUser.stories.create(
      connection,
      { body: storyCreate },
    );
  typia.assert(story);
  TestValidator.equals(
    "story owner id matches userA",
    story.storyfield_ai_authenticateduser_id,
    userAAuthorized.id,
  );

  // 4. Delete the story as owner (user A)
  await api.functional.storyfieldAi.authenticatedUser.stories.erase(
    connection,
    { storyId: story.id },
  );

  // 5. Attempt to delete same story again as user A (already deleted)
  await TestValidator.error(
    "cannot re-delete already deleted story",
    async () => {
      await api.functional.storyfieldAi.authenticatedUser.stories.erase(
        connection,
        { storyId: story.id },
      );
    },
  );

  // 6. Register user B
  const userBCreate = {
    external_user_id: RandomGenerator.alphaNumeric(12),
    email: `${RandomGenerator.alphaNumeric(6)}@test-rival.com`,
    actor_type: "authenticatedUser",
  } satisfies IStoryfieldAiAuthenticatedUser.ICreate;
  const userBAuthorized = await api.functional.auth.authenticatedUser.join(
    connection,
    { body: userBCreate },
  );
  typia.assert(userBAuthorized);

  // 7. Login as user B
  const userBLogin = {
    external_user_id: userBCreate.external_user_id,
    email: userBCreate.email,
  } satisfies IStoryfieldAiAuthenticatedUser.ILogin;
  const userBLoggedIn = await api.functional.auth.authenticatedUser.login(
    connection,
    { body: userBLogin },
  );
  typia.assert(userBLoggedIn);

  // 8. User B attempts to delete A's story (should fail with forbidden)
  await TestValidator.error(
    "user B cannot delete another user's story",
    async () => {
      await api.functional.storyfieldAi.authenticatedUser.stories.erase(
        connection,
        { storyId: story.id },
      );
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IStoryfieldAiAuthenticatedUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiAuthenticatedUser";
import type { IStoryfieldAiStory } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStory";
import type { IStoryfieldAiSystemAdmin } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiSystemAdmin";

/**
 * End-to-end test for hard deletion of a story by a system administrator.
 *
 * Steps:
 *
 * 1. Register & login as a system admin with unique credentials.
 * 2. Register an authenticated user and login.
 * 3. Create a story as that user (capture storyId).
 * 4. Switch back to system admin by login.
 * 5. Delete the created story using the system admin API.
 * 6. Verify hard deletion by attempting a repeat delete (expect error).
 * 7. Attempt to delete a random non-existent storyId (expect error).
 */
export async function test_api_story_deletion_by_system_admin(
  connection: api.IConnection,
) {
  // 1. Register and login as system admin
  const sysAdminEmail = typia.random<string & tags.Format<"email">>();
  const sysAdminExternalId = RandomGenerator.alphaNumeric(16);

  const sysAdminJoin: IStoryfieldAiSystemAdmin.IAuthorized =
    await api.functional.auth.systemAdmin.join(connection, {
      body: {
        email: sysAdminEmail,
        external_admin_id: sysAdminExternalId,
        actor_type: "systemAdmin",
      } satisfies IStoryfieldAiSystemAdmin.IJoin,
    });
  typia.assert(sysAdminJoin);

  // Explicit login step (to guarantee token set for system admin actions)
  const sysAdminAuth: IStoryfieldAiSystemAdmin.IAuthorized =
    await api.functional.auth.systemAdmin.login(connection, {
      body: {
        email: sysAdminEmail,
        external_admin_id: sysAdminExternalId,
      } satisfies IStoryfieldAiSystemAdmin.ILogin,
    });
  typia.assert(sysAdminAuth);

  // 2. Register and login as authenticatedUser
  const userEmail = typia.random<string & tags.Format<"email">>();
  const userExternalId = RandomGenerator.alphaNumeric(16);

  const authUser: IStoryfieldAiAuthenticatedUser.IAuthorized =
    await api.functional.auth.authenticatedUser.join(connection, {
      body: {
        email: userEmail,
        external_user_id: userExternalId,
        actor_type: "authenticatedUser",
      } satisfies IStoryfieldAiAuthenticatedUser.ICreate,
    });
  typia.assert(authUser);

  const authUserAuth: IStoryfieldAiAuthenticatedUser.IAuthorized =
    await api.functional.auth.authenticatedUser.login(connection, {
      body: {
        email: userEmail,
        external_user_id: userExternalId,
      } satisfies IStoryfieldAiAuthenticatedUser.ILogin,
    });
  typia.assert(authUserAuth);

  // 3. Create a story as the authenticated user
  const storyCreate = {
    title: RandomGenerator.paragraph({ sentences: 2, wordMin: 5, wordMax: 8 }),
    main_plot: RandomGenerator.paragraph({ sentences: 3 }),
    language: RandomGenerator.pick([
      "ko",
      "en",
      "ja",
      "zh",
      "es",
      "fr",
      "de",
    ] as const),
  } satisfies IStoryfieldAiStory.ICreate;
  const createdStory: IStoryfieldAiStory =
    await api.functional.storyfieldAi.authenticatedUser.stories.create(
      connection,
      { body: storyCreate },
    );
  typia.assert(createdStory);
  TestValidator.equals(
    "story title matches input",
    createdStory.title,
    storyCreate.title,
  );
  TestValidator.equals(
    "story language matches input",
    createdStory.language,
    storyCreate.language,
  );

  // 4. Switch back to system admin for privileged delete
  const sysAdminAuthAgain: IStoryfieldAiSystemAdmin.IAuthorized =
    await api.functional.auth.systemAdmin.login(connection, {
      body: {
        email: sysAdminEmail,
        external_admin_id: sysAdminExternalId,
      } satisfies IStoryfieldAiSystemAdmin.ILogin,
    });
  typia.assert(sysAdminAuthAgain);

  // 5. Perform privileged delete
  await api.functional.storyfieldAi.systemAdmin.stories.erase(connection, {
    storyId: createdStory.id,
  });

  // 6. Attempt to re-delete same storyId (must error)
  await TestValidator.error(
    "deleting already hard-deleted story returns error",
    async () => {
      await api.functional.storyfieldAi.systemAdmin.stories.erase(connection, {
        storyId: createdStory.id,
      });
    },
  );

  // 7. Attempt to delete a random non-existent storyId
  await TestValidator.error(
    "deleting non-existent storyId results in error",
    async () => {
      await api.functional.storyfieldAi.systemAdmin.stories.erase(connection, {
        storyId: typia.random<string & tags.Format<"uuid">>(),
      });
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";
import type { IPageIStoryfieldAiStoryPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIStoryfieldAiStoryPage";
import type { IStoryfieldAiAuthenticatedUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiAuthenticatedUser";
import type { IStoryfieldAiStory } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStory";
import type { IStoryfieldAiStoryPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStoryPage";

/**
 * Validate story page pagination and listing by authenticated user (owner
 * only).
 *
 * - Register and login as authenticated user (user1).
 * - Create a story for user1.
 * - Add 20 pages to the story for pagination.
 * - Perform various PATCH /stories/{storyId}/pages requests:
 *
 *   - Default page/limit (should return first page, default 15 limit; correct
 *       ordering).
 *   - Second page (next page, ensure correct pages and order).
 *   - OrderBy: desc ordering by page_number (reverse order)
 *   - Search by text from a page (partial content match)
 * - Register another authenticated user (user2), login, and attempt to access
 *   user1's story pages (should be denied).
 * - Validate correctness of page content, total count, and permission
 *   enforcement.
 */
export async function test_api_story_pages_list_pagination_by_authenticated_user(
  connection: api.IConnection,
) {
  // 1. Register & login as user1
  const user1_external_id = RandomGenerator.alphaNumeric(12);
  const user1_email = `${RandomGenerator.alphaNumeric(8)}@test.com`;
  const user1_join = await api.functional.auth.authenticatedUser.join(
    connection,
    {
      body: {
        external_user_id: user1_external_id,
        email: user1_email,
        actor_type: "authenticatedUser",
      },
    },
  );
  typia.assert(user1_join);

  // 2. Create story as user1
  const story =
    await api.functional.storyfieldAi.authenticatedUser.stories.create(
      connection,
      {
        body: {
          title: RandomGenerator.name(4),
          main_plot: RandomGenerator.paragraph({ sentences: 4 }),
          language: "en",
        },
      },
    );
  typia.assert(story);

  // 3. Add 20 pages to this story
  const pages = ArrayUtil.repeat(
    20,
    (i) =>
      ({
        page_number: i + 1,
        text: RandomGenerator.content({
          paragraphs: 1,
          sentenceMin: 2,
          sentenceMax: 4,
          wordMin: 3,
          wordMax: 7,
        }),
      }) satisfies IStoryfieldAiStoryPage.ICreate,
  );
  const createdPages: IStoryfieldAiStoryPage[] = [];
  for (const page of pages) {
    const res =
      await api.functional.storyfieldAi.authenticatedUser.stories.pages.create(
        connection,
        {
          storyId: story.id,
          body: page,
        },
      );
    typia.assert(res);
    createdPages.push(res);
  }

  // 4. List pages with default pagination (should get first 15)
  const result_default =
    await api.functional.storyfieldAi.authenticatedUser.stories.pages.index(
      connection,
      {
        storyId: story.id,
        body: {},
      },
    );
  typia.assert(result_default);
  TestValidator.equals("first page item count", result_default.data.length, 15);
  TestValidator.equals(
    "correct order of default page_number",
    result_default.data.map((p) => p.page_number),
    pages.slice(0, 15).map((p) => p.page_number),
  );

  // 5. Second page with limit 7
  const result_pg2 =
    await api.functional.storyfieldAi.authenticatedUser.stories.pages.index(
      connection,
      {
        storyId: story.id,
        body: {
          page: 2 satisfies number as number,
          limit: 7 satisfies number as number,
        },
      },
    );
  typia.assert(result_pg2);
  TestValidator.equals(
    "second page item count (limit 7)",
    result_pg2.data.length,
    7,
  );
  TestValidator.equals(
    "correct order of page_numbers on 2nd page (7 items)",
    result_pg2.data.map((p) => p.page_number),
    pages.slice(7, 14).map((p) => p.page_number),
  );

  // 6. Descending order
  const result_desc =
    await api.functional.storyfieldAi.authenticatedUser.stories.pages.index(
      connection,
      {
        storyId: story.id,
        body: { orderBy: "page_number", order: "desc" },
      },
    );
  typia.assert(result_desc);
  TestValidator.equals(
    "descending order of page_number",
    result_desc.data.map((p) => p.page_number),
    [...pages]
      .reverse()
      .slice(0, 15)
      .map((p) => p.page_number),
  );

  // 7. Search filter on text
  // Use known substring from a random page
  const searchPage = createdPages[RandomGenerator.pick([...Array(20).keys()])];
  const keyword = RandomGenerator.substring(searchPage.text);
  const result_search =
    await api.functional.storyfieldAi.authenticatedUser.stories.pages.index(
      connection,
      {
        storyId: story.id,
        body: { search: keyword },
      },
    );
  typia.assert(result_search);
  TestValidator.predicate(
    "at least one result for search",
    result_search.data.length >= 1,
  );
  TestValidator.predicate(
    "every result contains keyword in text",
    result_search.data.every((p) => p.text.includes(keyword)),
  );

  // 8. Register another user and test cross-user auth
  const user2_external_id = RandomGenerator.alphaNumeric(12);
  const user2_email = `${RandomGenerator.alphaNumeric(8)}@test.com`;
  await api.functional.auth.authenticatedUser.join(connection, {
    body: {
      external_user_id: user2_external_id,
      email: user2_email,
      actor_type: "authenticatedUser",
    },
  });
  await api.functional.auth.authenticatedUser.login(connection, {
    body: {
      external_user_id: user2_external_id,
      email: user2_email,
    },
  });
  await TestValidator.error("unauthorized cross-user page access", async () => {
    await api.functional.storyfieldAi.authenticatedUser.stories.pages.index(
      connection,
      {
        storyId: story.id,
        body: {},
      },
    );
  });
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IStoryfieldAiSystemAdmin } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiSystemAdmin";

/**
 * Validates forced admin-initiated soft-deletion (force logout) of
 * authentication token sessions.
 *
 * Business Context: Ensures that only privileged admins may force-logout
 * (soft-delete) an authentication session, in compliance with audit and
 * operational security. Sessions are soft-deleted (deleted_at set), tracing is
 * preserved, and operation is restricted to admins. Already
 * deleted/non-existent sessions return errors.
 *
 * Steps:
 *
 * 1. Register a unique system admin via POST /auth/systemAdmin/join.
 * 2. Log in as system admin to create first session. Save session token.
 * 3. Simulate a subsequent login to create a second active session.
 * 4. Call DELETE /storyfieldAi/systemAdmin/tokenSessions/{tokenSessionId} as admin
 *    to soft-delete the first session.
 * 5. Validate that deleted_at is set and session cannot be used.
 * 6. Attempt to re-delete the same session or delete a non-existent session and
 *    validate error handling.
 */
export async function test_api_token_session_force_logout_admin_initiated(
  connection: api.IConnection,
) {
  // Step 1: Register a system admin
  const extAdminId = RandomGenerator.alphaNumeric(16);
  const adminEmail = typia.random<string & tags.Format<"email">>();
  const adminJoinBody = {
    external_admin_id: extAdminId,
    email: adminEmail,
    actor_type: "systemAdmin",
  } satisfies IStoryfieldAiSystemAdmin.IJoin;
  const adminJoinResp = await api.functional.auth.systemAdmin.join(connection, {
    body: adminJoinBody,
  });
  typia.assert(adminJoinResp);

  // Step 2: Login as system admin (first session)
  const adminLogin1 = await api.functional.auth.systemAdmin.login(connection, {
    body: {
      external_admin_id: extAdminId,
      email: adminEmail,
    } satisfies IStoryfieldAiSystemAdmin.ILogin,
  });
  typia.assert(adminLogin1);

  // Step 3: Simulate second device login (second session)
  const adminLogin2 = await api.functional.auth.systemAdmin.login(connection, {
    body: {
      external_admin_id: extAdminId,
      email: adminEmail,
    } satisfies IStoryfieldAiSystemAdmin.ILogin,
  });
  typia.assert(adminLogin2);

  // Step 4: Use a valid UUID (simulated here) as the session to soft-delete
  const tokenSessionId = typia.random<string & tags.Format<"uuid">>();
  await api.functional.storyfieldAi.systemAdmin.tokenSessions.erase(
    connection,
    {
      tokenSessionId,
    },
  );

  // Step 5: Attempt to use deleted session (delete again), expect error
  await TestValidator.error(
    "cannot delete already deleted session",
    async () => {
      await api.functional.storyfieldAi.systemAdmin.tokenSessions.erase(
        connection,
        {
          tokenSessionId,
        },
      );
    },
  );

  // Step 6: Attempt to delete a completely non-existent sessionId, expect error
  const fakeSessionId = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.error("cannot delete non-existent session", async () => {
    await api.functional.storyfieldAi.systemAdmin.tokenSessions.erase(
      connection,
      {
        tokenSessionId: fakeSessionId,
      },
    );
  });
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";
import type { IPageIStoryfieldAiTokenRevocation } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIStoryfieldAiTokenRevocation";
import type { IStoryfieldAiSystemAdmin } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiSystemAdmin";
import type { IStoryfieldAiTokenRevocation } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiTokenRevocation";

/**
 * Validate that system administrators can search, filter, and paginate
 * authentication token revocation events via PATCH
 * /storyfieldAi/systemAdmin/tokenRevocations, supporting audit and compliance
 * review.
 *
 * 1. Register and login a system admin
 * 2. Query revocation logs as admin with different filters and paginations
 * 3. Validate fields, pagination and filtering, edge cases (empty, bad filters)
 * 4. Attempt unauthorized log access as non-admin
 */
export async function test_api_token_revocation_log_admin_search_and_audit(
  connection: api.IConnection,
) {
  // 1. Register and login as admin
  const adminExternalId = RandomGenerator.alphaNumeric(12);
  const adminEmail = `${RandomGenerator.alphaNumeric(6)}@company.com`;
  const joinRes = await api.functional.auth.systemAdmin.join(connection, {
    body: {
      external_admin_id: adminExternalId,
      email: adminEmail,
      actor_type: "systemAdmin",
    } satisfies IStoryfieldAiSystemAdmin.IJoin,
  });
  typia.assert(joinRes);
  const loginRes = await api.functional.auth.systemAdmin.login(connection, {
    body: {
      external_admin_id: adminExternalId,
      email: adminEmail,
    } satisfies IStoryfieldAiSystemAdmin.ILogin,
  });
  typia.assert(loginRes);
  // 2. Attempt log search as admin with no filter
  const pageBasic =
    await api.functional.storyfieldAi.systemAdmin.tokenRevocations.index(
      connection,
      { body: {} satisfies IStoryfieldAiTokenRevocation.IRequest },
    );
  typia.assert(pageBasic);
  // Validate pagination structure and audit log fields
  TestValidator.predicate(
    "admin receives pagination and audit fields",
    () =>
      !!pageBasic.pagination &&
      Array.isArray(pageBasic.data) &&
      pageBasic.data.every(
        (v) => v.id && v.token_hash && v.revoked_reason && v.created_at,
      ),
  );
  // 3. Test paging by page/limit
  const pageTwo =
    await api.functional.storyfieldAi.systemAdmin.tokenRevocations.index(
      connection,
      {
        body: {
          page: 2,
          limit: 1,
        } satisfies IStoryfieldAiTokenRevocation.IRequest,
      },
    );
  typia.assert(pageTwo);
  TestValidator.equals("page and limit", pageTwo.pagination.current, 2);
  TestValidator.equals("page and limit", pageTwo.pagination.limit, 1);
  // 4. Test search by non-existent reason / filter (empty result)
  const pageEmpty =
    await api.functional.storyfieldAi.systemAdmin.tokenRevocations.index(
      connection,
      {
        body: {
          revoked_reason: "doesnotmatchany",
        } satisfies IStoryfieldAiTokenRevocation.IRequest,
      },
    );
  typia.assert(pageEmpty);
  TestValidator.equals("empty filter result", pageEmpty.data.length, 0);
  // 5. Edge: Out-of-bounds page/limit
  const pageOob =
    await api.functional.storyfieldAi.systemAdmin.tokenRevocations.index(
      connection,
      {
        body: {
          page: 9999,
          limit: 5,
        } satisfies IStoryfieldAiTokenRevocation.IRequest,
      },
    );
  typia.assert(pageOob);
  // Allowed: page empty, OK
  // 6. If there is any record, test filtering by admin/user/date
  if (pageBasic.data.length) {
    const first = pageBasic.data[0];
    if (first.system_admin_id) {
      const pageByAdmin =
        await api.functional.storyfieldAi.systemAdmin.tokenRevocations.index(
          connection,
          {
            body: {
              system_admin_id: first.system_admin_id,
            } satisfies IStoryfieldAiTokenRevocation.IRequest,
          },
        );
      typia.assert(pageByAdmin);
      TestValidator.predicate(
        "filtered by system_admin_id",
        pageByAdmin.data.every(
          (v) => v.system_admin_id === first.system_admin_id,
        ),
      );
    }
    if (first.authenticated_user_id) {
      const pageByUser =
        await api.functional.storyfieldAi.systemAdmin.tokenRevocations.index(
          connection,
          {
            body: {
              authenticated_user_id: first.authenticated_user_id,
            } satisfies IStoryfieldAiTokenRevocation.IRequest,
          },
        );
      typia.assert(pageByUser);
      TestValidator.predicate(
        "filtered by user id",
        pageByUser.data.every(
          (v) => v.authenticated_user_id === first.authenticated_user_id,
        ),
      );
    }
    // Filter by created_at window
    const createdFrom = first.created_at;
    const pageByDate =
      await api.functional.storyfieldAi.systemAdmin.tokenRevocations.index(
        connection,
        {
          body: {
            created_from: createdFrom,
          } satisfies IStoryfieldAiTokenRevocation.IRequest,
        },
      );
    typia.assert(pageByDate);
    TestValidator.predicate(
      "filtered by from date",
      pageByDate.data.every(
        (v) =>
          new Date(v.created_at).getTime() >= new Date(createdFrom).getTime(),
      ),
    );
    // Filter by token_hash
    const pageByToken =
      await api.functional.storyfieldAi.systemAdmin.tokenRevocations.index(
        connection,
        {
          body: {
            token_hash: first.token_hash,
          } satisfies IStoryfieldAiTokenRevocation.IRequest,
        },
      );
    typia.assert(pageByToken);
    TestValidator.predicate(
      "filtered by token_hash",
      pageByToken.data.every((v) => v.token_hash === first.token_hash),
    );
  }
  // 7. Attempt unauthorized call as non-admin
  const unauthConn: api.IConnection = { ...connection, headers: {} };
  await TestValidator.error("non-admin forbidden from log", async () => {
    await api.functional.storyfieldAi.systemAdmin.tokenRevocations.index(
      unauthConn,
      { body: {} satisfies IStoryfieldAiTokenRevocation.IRequest },
    );
  });
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IStoryfieldAiSystemAdmin } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiSystemAdmin";
import type { IStoryfieldAiTokenRevocation } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiTokenRevocation";

/**
 * Validate that a system admin can retrieve full detail for a specific token
 * revocation event, and that unauthorized access is blocked.
 *
 * 1. Register and authenticate a system admin, then create (or look up) a token
 *    revocation event id.
 * 2. Fetch token revocation details as admin and verify data shape, presence, and
 *    authorization.
 * 3. Attempt to fetch with unauthenticated connection and confirm forbidden/error
 *    result.
 * 4. Attempt to fetch with a random/non-existent UUID, confirm error/404 is
 *    thrown.
 */
export async function test_api_token_revocation_event_detail_retrieval_by_admin(
  connection: api.IConnection,
) {
  // Step 1: Register and authenticate a system admin
  const adminJoinReq = {
    external_admin_id: RandomGenerator.alphaNumeric(12),
    email: `${RandomGenerator.alphabets(8)}@company.com`,
    actor_type: "systemAdmin",
  } satisfies IStoryfieldAiSystemAdmin.IJoin;
  const adminAuth: IStoryfieldAiSystemAdmin.IAuthorized =
    await api.functional.auth.systemAdmin.join(connection, {
      body: adminJoinReq,
    });
  typia.assert(adminAuth);

  // Step 2: Fetch or construct a tokenRevocationId (simulate test mode)
  // Here, use typia.random to generate a valid UUID, knowing some backends support direct test population.
  const tokenRevocationId = typia.random<string & tags.Format<"uuid">>();

  // Step 3: Fetch token revocation details as system admin
  const detail: IStoryfieldAiTokenRevocation =
    await api.functional.storyfieldAi.systemAdmin.tokenRevocations.at(
      connection,
      { tokenRevocationId },
    );
  typia.assert(detail);

  TestValidator.equals(
    "id must match request tokenRevocationId",
    detail.id,
    tokenRevocationId,
  );
  TestValidator.predicate("token_hash present", detail.token_hash.length > 0);
  TestValidator.predicate(
    "revoked_reason present",
    detail.revoked_reason.length > 0,
  );
  TestValidator.predicate(
    "created_at is valid ISO 8601 UTC string",
    /T.*Z$/.test(detail.created_at),
  );

  // Step 4: Attempt fetching details as unauthenticated connection
  const unauthConn: api.IConnection = { ...connection, headers: {} };
  await TestValidator.error(
    "unauthorized connection cannot access admin token revocation detail",
    async () => {
      await api.functional.storyfieldAi.systemAdmin.tokenRevocations.at(
        unauthConn,
        { tokenRevocationId },
      );
    },
  );

  // Step 5: Attempt with a non-existent token revocation ID, expect error
  const badTokenRevocationId = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.error(
    "non-existent tokenRevocationId returns error/404",
    async () => {
      await api.functional.storyfieldAi.systemAdmin.tokenRevocations.at(
        connection,
        { tokenRevocationId: badTokenRevocationId },
      );
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";
import type { IPageIStoryfieldAiAuthAuditLog } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIStoryfieldAiAuthAuditLog";
import type { IStoryfieldAiAuthAuditLog } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiAuthAuditLog";
import type { IStoryfieldAiSystemAdmin } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiSystemAdmin";

/**
 * E2E test for system admin authentication audit log search, filtering, and
 * pagination.
 *
 * Validates the PATCH /storyfieldAi/systemAdmin/authAuditLogs endpoint for
 * admin audit log review:
 *
 * - Full login lifecycle: admin join & login
 * - Generates auth audit log entries through these operations
 * - Searches audit logs by filtering (session, admin, event type, outcome,
 *   times, user)
 * - Tests pagination (multiple pages, truncated results, limits)
 * - Checks filter miss (no results)
 * - Ensures non-admins cannot access (authorization failure)
 */
export async function test_api_auth_audit_log_search_by_admin_for_session_and_user(
  connection: api.IConnection,
) {
  // Step 1. Admin join (register a new admin)
  const adminJoinInput = {
    external_admin_id: RandomGenerator.alphaNumeric(16),
    email: `${RandomGenerator.alphaNumeric(8)}@admin.test.com`,
    actor_type: "systemAdmin",
  } satisfies IStoryfieldAiSystemAdmin.IJoin;
  const adminAuth = await api.functional.auth.systemAdmin.join(connection, {
    body: adminJoinInput,
  });
  typia.assert(adminAuth);

  // Step 2. Admin login (generates another audit event for login)
  const adminLoginInput = {
    external_admin_id: adminJoinInput.external_admin_id,
    email: adminJoinInput.email,
  } satisfies IStoryfieldAiSystemAdmin.ILogin;
  const adminLoggedIn = await api.functional.auth.systemAdmin.login(
    connection,
    {
      body: adminLoginInput,
    },
  );
  typia.assert(adminLoggedIn);

  // Step 3. Request with filtering by admin id and get the logs
  // Basic fetch: test logs for both join+login
  const auditSearchInput = {
    system_admin_id: adminAuth.id,
    page: 1,
    limit: 10,
  } satisfies IStoryfieldAiAuthAuditLog.IRequest;
  const auditLogsResult =
    await api.functional.storyfieldAi.systemAdmin.authAuditLogs.index(
      connection,
      {
        body: auditSearchInput,
      },
    );
  typia.assert(auditLogsResult);

  TestValidator.predicate(
    "should return audit logs for admin join and login events",
    auditLogsResult.data.some(
      (log) =>
        log.system_admin_id === adminAuth.id &&
        ["issued", "validated", "refreshed", "revoked", "denied"].includes(
          log.event_type,
        ),
    ),
  );

  // Step 4. Query with stricter page limit to test pagination
  const auditLogsResultPage =
    await api.functional.storyfieldAi.systemAdmin.authAuditLogs.index(
      connection,
      {
        body: { system_admin_id: adminAuth.id, page: 1, limit: 1 },
      },
    );
  typia.assert(auditLogsResultPage);
  TestValidator.equals(
    "pagination info should match limit=1",
    auditLogsResultPage.pagination.limit,
    1,
  );
  TestValidator.equals(
    "first page should have at most 1 record",
    auditLogsResultPage.data.length <= 1,
    true,
  );

  // Step 5. Query with future time window (should yield no logs)
  const auditLogsNone =
    await api.functional.storyfieldAi.systemAdmin.authAuditLogs.index(
      connection,
      {
        body: {
          system_admin_id: adminAuth.id,
          created_from: new Date(Date.now() + 24 * 3600 * 1000).toISOString(),
          page: 1,
          limit: 5,
        },
      },
    );
  typia.assert(auditLogsNone);
  TestValidator.equals(
    "future-dated filter query returns no logs",
    auditLogsNone.data.length,
    0,
  );

  // Step 6. Query event type filter boundaries (should match expected event types)
  const eventType = auditLogsResult.data[0]?.event_type ?? undefined;
  if (eventType !== undefined) {
    const auditEventTypeLogs =
      await api.functional.storyfieldAi.systemAdmin.authAuditLogs.index(
        connection,
        {
          body: {
            system_admin_id: adminAuth.id,
            event_type: eventType,
            page: 1,
            limit: 20,
          },
        },
      );
    typia.assert(auditEventTypeLogs);
    TestValidator.predicate(
      "all logs in event type filter match expected",
      auditEventTypeLogs.data.every((log) => log.event_type === eventType),
    );
  }

  // Step 7. Non-admin should fail (use an unauthenticated connection)
  const unauthConn: api.IConnection = { ...connection, headers: {} };
  await TestValidator.error("non-admin cannot access audit logs", async () => {
    await api.functional.storyfieldAi.systemAdmin.authAuditLogs.index(
      unauthConn,
      {
        body: auditSearchInput,
      },
    );
  });
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IStoryfieldAiAuthenticatedUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiAuthenticatedUser";
import type { IStoryfieldAiStory } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStory";
import type { IStoryfieldAiStoryPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStoryPage";
import type { IStoryfieldAiSystemAdmin } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiSystemAdmin";
import type { IStoryfieldAiTtsResult } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiTtsResult";

/**
 * Validates TTS generation (as a system admin) for a story page.
 *
 * - Register & login as system admin
 * - Create a test story
 * - Add a page with text to the story
 * - Generate a TTS result for the story page (admin operation)
 * - Confirm TTS result references story/page and has correct data
 */
export async function test_api_tts_generation_for_story_page_as_system_admin(
  connection: api.IConnection,
) {
  // Step 1: Register and login as new system admin
  const adminEmail =
    RandomGenerator.name(2).replace(/ /g, "_") + "@acme-admin.com";
  const externalAdminId = RandomGenerator.alphaNumeric(16);
  const adminJoin = await api.functional.auth.systemAdmin.join(connection, {
    body: {
      external_admin_id: externalAdminId,
      email: adminEmail,
      actor_type: "systemAdmin",
    } satisfies IStoryfieldAiSystemAdmin.IJoin,
  });
  typia.assert(adminJoin);
  // Already authenticated as system admin due to SDK automatic behavior

  // Step 2: As system admin, create a new story
  const storyTitle = RandomGenerator.paragraph({ sentences: 3 });
  const storyLanguage = RandomGenerator.pick([
    "ko",
    "en",
    "gyeongsang",
  ] as const);
  const mainPlot = RandomGenerator.paragraph({ sentences: 8 });
  const story =
    await api.functional.storyfieldAi.authenticatedUser.stories.create(
      connection,
      {
        body: {
          title: storyTitle,
          main_plot: mainPlot,
          language: storyLanguage,
        } satisfies IStoryfieldAiStory.ICreate,
      },
    );
  typia.assert(story);

  // Step 3: Add a new page with text to the created story
  const pageText = RandomGenerator.content({
    paragraphs: 1,
    sentenceMin: 5,
    sentenceMax: 10,
    wordMin: 4,
    wordMax: 8,
  });
  const pageNumber = 1;
  const storyPage =
    await api.functional.storyfieldAi.authenticatedUser.stories.pages.create(
      connection,
      {
        storyId: story.id,
        body: {
          page_number: pageNumber,
          text: pageText,
        } satisfies IStoryfieldAiStoryPage.ICreate,
      },
    );
  typia.assert(storyPage);

  // Step 4: As admin, generate TTS result for this page
  const ttsAudioUri = `https://audio.cdn/test-tts/${RandomGenerator.alphaNumeric(10)}.mp3`;
  const dialect = RandomGenerator.pick([
    "standard",
    "gyeongsang",
    "jeolla",
  ] as const);

  const ttsResult =
    await api.functional.storyfieldAi.systemAdmin.stories.ttsResults.create(
      connection,
      {
        storyId: story.id,
        body: {
          tts_audio_uri: ttsAudioUri,
          source_text: storyPage.text,
          dialect,
          storyfield_ai_story_page_id: storyPage.id,
        } satisfies IStoryfieldAiTtsResult.ICreate,
      },
    );
  typia.assert(ttsResult);

  // Step 5: Validation of result
  TestValidator.equals(
    "TTS audio_uri correct",
    ttsResult.tts_audio_uri,
    ttsAudioUri,
  );
  TestValidator.equals(
    "source_text matches page text",
    ttsResult.source_text,
    storyPage.text,
  );
  TestValidator.equals("dialect correct", ttsResult.dialect, dialect);
  TestValidator.equals(
    "story reference correct",
    ttsResult.storyfield_ai_story_id,
    story.id,
  );
  TestValidator.equals(
    "page reference correct",
    ttsResult.storyfield_ai_story_page_id,
    storyPage.id,
  );
  TestValidator.predicate(
    "audio URI is http(s)",
    ttsResult.tts_audio_uri.startsWith("http"),
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
