        storyId: createStory.id,
        body: {
          tts_audio_uri: `https://mock-s3.e2e/${RandomGenerator.alphaNumeric(24)}.mp3`,
          source_text: page.text,
          dialect: "ko",
          storyfield_ai_story_page_id: page.id,
        } satisfies IStoryfieldAiTtsResult.ICreate,
      },
    );
  typia.assert(ttsResult);
  TestValidator.equals(
    "ttsResult is attached to correct story page",
    ttsResult.storyfield_ai_story_page_id,
    page.id,
  );
  TestValidator.equals(
    "ttsResult ownership",
    ttsResult.storyfield_ai_story_id,
    createStory.id,
  );
  TestValidator.equals("ttsResult dialect initial", ttsResult.dialect, "ko");

  // 6. Owner successfully updates TTS result
  const updateInput = {
    tts_audio_uri: `https://mock-s3.e2e/${RandomGenerator.alphaNumeric(22)}_update.mp3`,
    source_text: RandomGenerator.content({
      paragraphs: 1,
      sentenceMin: 8,
      sentenceMax: 12,
    }),
    dialect: RandomGenerator.pick([
      "gyeongsang",
      "jeolla",
      "en",
      "ko",
    ] as const), // use a different dialect
    storyfield_ai_story_page_id: page.id,
  } satisfies IStoryfieldAiTtsResult.IUpdate;
  await new Promise((resolve) => setTimeout(resolve, 10)); // fudge audit times
  const updated =
    await api.functional.storyfieldAi.authenticatedUser.stories.ttsResults.update(
      connection,
      {
        storyId: createStory.id,
        ttsResultId: ttsResult.id,
        body: updateInput,
      },
    );
  typia.assert(updated);
  TestValidator.equals(
    "ttsResult id must not change after update",
    updated.id,
    ttsResult.id,
  );
  TestValidator.equals(
    "updated dialect is reflected",
    updated.dialect,
    updateInput.dialect,
  );
  TestValidator.notEquals(
    "updated_at changes after update",
    updated.updated_at,
    ttsResult.updated_at,
  );
  TestValidator.equals(
    "created_at remains original",
    updated.created_at,
    ttsResult.created_at,
  );

  // 7. Forbidden update by different user
  // -- register a different user and attempt forbidden update
  const extUserId2 = RandomGenerator.alphaNumeric(12);
  const email2 = `${RandomGenerator.alphabets(8)}@e2e-unauth.com`;
  const join2 = await api.functional.auth.authenticatedUser.join(connection, {
    body: {
      external_user_id: extUserId2,
      email: email2,
      actor_type: "authenticatedUser",
    } satisfies IStoryfieldAiAuthenticatedUser.ICreate,
  });
  typia.assert(join2);

  // Switch token (login as second user)
  const login2 = await api.functional.auth.authenticatedUser.login(connection, {
    body: {
      external_user_id: extUserId2,
      email: email2,
    } satisfies IStoryfieldAiAuthenticatedUser.ILogin,
  });
  typia.assert(login2);
  // Attempt update (should fail: forbidden)
  await TestValidator.error(
    "Forbidden update attempt by non-owner should be rejected",
    async () => {
      await api.functional.storyfieldAi.authenticatedUser.stories.ttsResults.update(
        connection,
        {
          storyId: createStory.id,
          ttsResultId: ttsResult.id,
          body: updateInput,
        },
      );
    },
  );

  // 8. Field value business error: set required string fields to empty
  // (simulate missing TTS source_text or empty dialect)
  await api.functional.auth.authenticatedUser.login(connection, {
    body: {
      external_user_id: extUserId,
      email,
    },
  });
  await TestValidator.error(
    "Empty source_text in TTS result update should be rejected",
    async () => {
      await api.functional.storyfieldAi.authenticatedUser.stories.ttsResults.update(
        connection,
        {
          storyId: createStory.id,
          ttsResultId: ttsResult.id,
          body: {
            source_text: "",
          },
        },
      );
    },
  );

  await TestValidator.error(
    "Empty dialect in TTS result update should be rejected",
    async () => {
      await api.functional.storyfieldAi.authenticatedUser.stories.ttsResults.update(
        connection,
        {
          storyId: createStory.id,
          ttsResultId: ttsResult.id,
          body: {
            dialect: "",
          },
        },
      );
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IStoryfieldAiAuthenticatedUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiAuthenticatedUser";
import type { IStoryfieldAiStory } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStory";
import type { IStoryfieldAiStoryPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStoryPage";
import type { IStoryfieldAiSystemAdmin } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiSystemAdmin";
import type { IStoryfieldAiTtsResult } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiTtsResult";

/**
 * Validate that a system administrator can update a TTS result generated by/for
 * a user story (cross-role privilege) and changes are effective.
 *
 * 1. Register and login as an authenticated user
 * 2. Create a story, story page, and a TTS result as that user
 * 3. Register and login as system admin
 * 4. Update the TTS result as system admin (change dialect/source_text)
 * 5. Confirm update is effective (fields changed, updated_at changes)
 * 6. Negative: Try update with invalid TTS result id and expect business error.
 */
export async function test_api_tts_update_by_system_admin_on_user_story(
  connection: api.IConnection,
) {
  // Register as Authenticated User
  const userExternalId = RandomGenerator.alphaNumeric(10);
  const userEmail = `user_${RandomGenerator.alphaNumeric(8)}@company.com`;
  const userCreate = {
    external_user_id: userExternalId,
    email: userEmail,
    actor_type: "authenticatedUser",
  } satisfies IStoryfieldAiAuthenticatedUser.ICreate;

  const userAuth = await api.functional.auth.authenticatedUser.join(
    connection,
    { body: userCreate },
  );
  typia.assert(userAuth);

  // Login as user (refresh session, simulate real flow)
  const userLogin = await api.functional.auth.authenticatedUser.login(
    connection,
    {
      body: {
        external_user_id: userExternalId,
        email: userEmail,
      } satisfies IStoryfieldAiAuthenticatedUser.ILogin,
    },
  );
  typia.assert(userLogin);

  // Create story as user
  const storyCreate = {
    title: RandomGenerator.paragraph({ sentences: 4 }),
    main_plot: RandomGenerator.paragraph({ sentences: 8 }),
    language: RandomGenerator.pick(["ko-KR", "en", "gyeongsang", "jeolla"]), // simulating dialect choice
  } satisfies IStoryfieldAiStory.ICreate;
  const story =
    await api.functional.storyfieldAi.authenticatedUser.stories.create(
      connection,
      { body: storyCreate },
    );
  typia.assert(story);

  // Create a story page as user
  const pageCreate = {
    page_number: 1,
    text: RandomGenerator.content({
      paragraphs: 1,
      sentenceMin: 3,
      sentenceMax: 5,
    }),
  } satisfies IStoryfieldAiStoryPage.ICreate;
  const storyPage =
    await api.functional.storyfieldAi.authenticatedUser.stories.pages.create(
      connection,
      {
        storyId: story.id,
        body: pageCreate,
      },
    );
  typia.assert(storyPage);

  // Create TTS result as user (associate with story + story page)
  const ttsCreate = {
    tts_audio_uri: `https://storage.example.com/tts/${RandomGenerator.alphaNumeric(8)}.mp3`,
    source_text: storyPage.text,
    dialect: story.language,
    storyfield_ai_story_page_id: storyPage.id,
  } satisfies IStoryfieldAiTtsResult.ICreate;
  const ttsResult =
    await api.functional.storyfieldAi.authenticatedUser.stories.ttsResults.create(
      connection,
      {
        storyId: story.id,
        body: ttsCreate,
      },
    );
  typia.assert(ttsResult);

  // Register as system admin
  const adminExternalId = RandomGenerator.alphaNumeric(10);
  const adminEmail = `admin_${RandomGenerator.alphaNumeric(8)}@company.com`;
  const adminCreate = {
    external_admin_id: adminExternalId,
    email: adminEmail,
    actor_type: "systemAdmin",
  } satisfies IStoryfieldAiSystemAdmin.IJoin;

  const sysAdminAuth = await api.functional.auth.systemAdmin.join(connection, {
    body: adminCreate,
  });
  typia.assert(sysAdminAuth);

  // Login as system admin
  const sysAdminLogin = await api.functional.auth.systemAdmin.login(
    connection,
    {
      body: {
        external_admin_id: adminExternalId,
        email: adminEmail,
      } satisfies IStoryfieldAiSystemAdmin.ILogin,
    },
  );
  typia.assert(sysAdminLogin);

  // As system admin, update the user's TTS result (change dialect and source text)
  const updatedDialect = RandomGenerator.pick([
    "jeolla",
    "gyeongsang",
    "en",
    "ko-KR",
  ]);
  const updatedSource = RandomGenerator.paragraph({ sentences: 2 });

  const updateBody = {
    dialect: updatedDialect,
    source_text: updatedSource,
  } satisfies IStoryfieldAiTtsResult.IUpdate;

  const updatedTts =
    await api.functional.storyfieldAi.systemAdmin.stories.ttsResults.update(
      connection,
      {
        storyId: story.id,
        ttsResultId: ttsResult.id,
        body: updateBody,
      },
    );
  typia.assert(updatedTts);
  // Validate that dialect and source_text changed
  TestValidator.equals(
    "TTS dialect is updated",
    updatedTts.dialect,
    updatedDialect,
  );
  TestValidator.equals(
    "TTS source_text is updated",
    updatedTts.source_text,
    updatedSource,
  );
  TestValidator.notEquals(
    "TTS updated_at changed",
    updatedTts.updated_at,
    ttsResult.updated_at,
  );

  // Negative: Try updating non-existent TTS result as admin
  const invalidResultId = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.error(
    "Admin update to non-existent TTS result fails",
    async () => {
      await api.functional.storyfieldAi.systemAdmin.stories.ttsResults.update(
        connection,
        {
          storyId: story.id,
          ttsResultId: invalidResultId,
          body: updateBody,
        },
      );
    },
  );
  // (Placeholder) Optionally: Validate audit/compliance log record exists if API exposes audit endpoints.
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IStoryfieldAiAuthenticatedUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiAuthenticatedUser";
import type { IStoryfieldAiStory } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStory";
import type { IStoryfieldAiStoryPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStoryPage";
import type { IStoryfieldAiTtsResult } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiTtsResult";

/**
 * Test that an authenticated user (story owner) can soft-delete
 * (deactivate) a TTS result from their own story.
 *
 * Business context:
 *
 * - Only the owner (authenticated user) of a story can delete/soft-delete
 *   (deactivate) their own TTS results.
 * - The deletion is performed as a soft-delete, setting deleted_at instead of
 *   physical removal.
 * - After deletion, the TTS result is inaccessible through normal list/fetch
 *   by business users.
 * - All deletion operations are audit/policy compliant (backend guarantees
 *   record retention).
 *
 * Steps:
 *
 * 1. Register and login as an authenticated user (with unique
 *    external_user_id/email).
 * 2. Create a new story as this user.
 * 3. Add a page to the story.
 * 4. Generate a TTS result for the page.
 * 5. Soft delete (erase) that TTS result.
 * 6. Attempt to delete again (should fail with appropriate business/policy
 *    error).
 * 7. Attempt to delete a non-existent TTS result (should fail with an error).
 * 8. (Optionally) Attempt deletion as a different (unauthorized) user (should
 *    fail with policy error).
 *
 * Validation:
 *
 * - After first deletion, TTS result is no longer available through normal
 *   API for that user.
 * - Soft deletion is enforced, not hard delete.
 * - Unauthorized/error cases are handled gracefully, with business logic
 *   validation.
 */
export async function test_api_tts_soft_delete_by_story_owner(
  connection: api.IConnection,
) {
  // 1. Register as authenticated user with a unique external_user_id and email
  const externalUserId: string = RandomGenerator.alphaNumeric(16);
  const email: string = `${RandomGenerator.alphaNumeric(6)}@example.com`;
  const joinBody = {
    external_user_id: externalUserId,
    email,
    actor_type: "authenticatedUser",
  } satisfies IStoryfieldAiAuthenticatedUser.ICreate;

  const user: IStoryfieldAiAuthenticatedUser.IAuthorized =
    await api.functional.auth.authenticatedUser.join(connection, {
      body: joinBody,
    });
  typia.assert(user);

  // 2. Login as authenticated user
  const login: IStoryfieldAiAuthenticatedUser.IAuthorized =
    await api.functional.auth.authenticatedUser.login(connection, {
      body: {
        external_user_id: externalUserId,
        email,
      } satisfies IStoryfieldAiAuthenticatedUser.ILogin,
    });
  typia.assert(login);

  // 3. Create a new story as this user
  const storyBody = {
    title: RandomGenerator.paragraph({ sentences: 3 }),
    main_plot: RandomGenerator.paragraph({ sentences: 5 }),
    language: "ko-KR",
  } satisfies IStoryfieldAiStory.ICreate;
  const story: IStoryfieldAiStory =
    await api.functional.storyfieldAi.authenticatedUser.stories.create(
      connection,
      { body: storyBody },
    );
  typia.assert(story);

  // 4. Add a page to the story
  const pageBody = {
    page_number: 1,
    text: RandomGenerator.content({ paragraphs: 1 }),
  } satisfies IStoryfieldAiStoryPage.ICreate;
  const page: IStoryfieldAiStoryPage =
    await api.functional.storyfieldAi.authenticatedUser.stories.pages.create(
      connection,
      { storyId: story.id, body: pageBody },
    );
  typia.assert(page);

  // 5. Generate a TTS result for that page
  const ttsBody = {
    tts_audio_uri: `https://storage.example.com/audio/${RandomGenerator.alphaNumeric(12)}.mp3`,
    source_text: page.text,
    dialect: story.language,
    storyfield_ai_story_page_id: page.id,
  } satisfies IStoryfieldAiTtsResult.ICreate;

  const ttsResult: IStoryfieldAiTtsResult =
    await api.functional.storyfieldAi.authenticatedUser.stories.ttsResults.create(
      connection,
      {
        storyId: story.id,
        body: ttsBody,
      },
    );
  typia.assert(ttsResult);

  // 6. Soft delete (erase) the TTS result
  await api.functional.storyfieldAi.authenticatedUser.stories.ttsResults.erase(
    connection,
    {
      storyId: story.id,
      ttsResultId: ttsResult.id,
    },
  );

  // No direct read endpoint for a single ttsResult; an attempt to delete again should result in error
  await TestValidator.error(
    "Deleting an already deleted TTS result should fail",
    async () => {
      await api.functional.storyfieldAi.authenticatedUser.stories.ttsResults.erase(
        connection,
        {
          storyId: story.id,
          ttsResultId: ttsResult.id,
        },
      );
    },
  );

  // Attempt to delete a non-existent TTS result
  await TestValidator.error(
    "Deleting a non-existent TTS result should fail",
    async () => {
      await api.functional.storyfieldAi.authenticatedUser.stories.ttsResults.erase(
        connection,
        {
          storyId: story.id,
          ttsResultId: typia.random<string & tags.Format<"uuid">>(),
        },
      );
    },
  );

  // Optionally, create a second user, login, and attempt to delete the first user's ttsResult (should fail)
  const externalUserId2: string = RandomGenerator.alphaNumeric(16);
  const email2: string = `${RandomGenerator.alphaNumeric(6)}@example.com`;
  const joinBody2 = {
    external_user_id: externalUserId2,
    email: email2,
    actor_type: "authenticatedUser",
  } satisfies IStoryfieldAiAuthenticatedUser.ICreate;
  await api.functional.auth.authenticatedUser.join(connection, {
    body: joinBody2,
  });
  await api.functional.auth.authenticatedUser.login(connection, {
    body: {
      external_user_id: externalUserId2,
      email: email2,
    } satisfies IStoryfieldAiAuthenticatedUser.ILogin,
  });

  await TestValidator.error(
    "Unauthorized user cannot delete another's TTS result",
    async () => {
      await api.functional.storyfieldAi.authenticatedUser.stories.ttsResults.erase(
        connection,
        {
          storyId: story.id,
          ttsResultId: ttsResult.id,
        },
      );
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IStoryfieldAiAuthenticatedUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiAuthenticatedUser";
import type { IStoryfieldAiStory } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStory";
import type { IStoryfieldAiStoryPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiStoryPage";
import type { IStoryfieldAiSystemAdmin } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiSystemAdmin";

/**
 * Validates that a system administrator can create a new page for an
 * existing AI-generated story, including required authentication and edge
 * case handling for duplicate page numbers.
 *
 * 1. Register as an authenticated user (to own the initial story).
 * 2. Log in as the authenticated user.
 * 3. Create a new story as that user (using IStoryfieldAiStory.ICreate).
 * 4. Register as a system administrator (using
 *    IStoryfieldAiSystemAdmin.IJoin).
 * 5. Log in as the system administrator.
 * 6. Add a new page to the story with a valid page number and content (using
 *    IStoryfieldAiStoryPage.ICreate).
 * 7. Validate the response: page is created, correct story ID, page number
 *    matches, text content matches, audit fields (created_at, updated_at)
 *    exist.
 * 8. Edge case: attempt to add a page with the same page number to the same
 *    story and expect a business error (prevent duplicate pages).
 */
export async function test_api_story_page_creation_system_admin_success(
  connection: api.IConnection,
) {
  // 1. Register & log in as authenticated user
  const userExternalId = RandomGenerator.alphaNumeric(12);
  const userEmail = `${RandomGenerator.alphabets(6)}@storyfield.com`;
  await api.functional.auth.authenticatedUser.join(connection, {
    body: {
      external_user_id: userExternalId,
      email: userEmail,
      actor_type: "authenticatedUser",
    },
  });
  await api.functional.auth.authenticatedUser.login(connection, {
    body: {
      external_user_id: userExternalId,
      email: userEmail,
    },
  });

  // 2. Create a new story as this user
  const storyCreateBody = {
    title: RandomGenerator.paragraph({ sentences: 4 }),
    main_plot: RandomGenerator.paragraph({ sentences: 2 }),
    language: "ko-KR",
  } satisfies IStoryfieldAiStory.ICreate;
  const story =
    await api.functional.storyfieldAi.authenticatedUser.stories.create(
      connection,
      {
        body: storyCreateBody,
      },
    );
  typia.assert(story);

  // 3. Register as and log in as system administrator
  const adminExternalId = RandomGenerator.alphaNumeric(14);
  const adminEmail = `${RandomGenerator.alphabets(8)}@admin.storyfield.com`;
  await api.functional.auth.systemAdmin.join(connection, {
    body: {
      external_admin_id: adminExternalId,
      email: adminEmail,
      actor_type: "systemAdmin",
    },
  });
  await api.functional.auth.systemAdmin.login(connection, {
    body: {
      external_admin_id: adminExternalId,
      email: adminEmail,
    },
  });

  // 4. Create a page for the story (system admin context)
  const newPageBody = {
    page_number: 1,
    text: RandomGenerator.content({
      paragraphs: 1,
      sentenceMin: 6,
      sentenceMax: 10,
      wordMin: 3,
      wordMax: 7,
    }),
  } satisfies IStoryfieldAiStoryPage.ICreate;
  const newPage =
    await api.functional.storyfieldAi.systemAdmin.stories.pages.create(
      connection,
      {
        storyId: story.id,
        body: newPageBody,
      },
    );
  typia.assert(newPage);
  TestValidator.equals(
    "story id on created page matches",
    newPage.storyfield_ai_story_id,
    story.id,
  );
  TestValidator.equals(
    "page number matches",
    newPage.page_number,
    newPageBody.page_number,
  );
  TestValidator.equals("text matches", newPage.text, newPageBody.text);
  TestValidator.predicate(
    "created_at field is ISO string",
    typeof newPage.created_at === "string" && newPage.created_at.length > 0,
  );
  TestValidator.predicate(
    "updated_at field is ISO string",
    typeof newPage.updated_at === "string" && newPage.updated_at.length > 0,
  );

  // 5. Edge case: attempt to add a duplicate page number
  await TestValidator.error(
    "should not allow duplicate page_number for same story",
    async () => {
      await api.functional.storyfieldAi.systemAdmin.stories.pages.create(
        connection,
        {
          storyId: story.id,
          body: newPageBody,
        },
      );
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
