import jwt from "jsonwebtoken";
import { MyGlobal } from "../MyGlobal";
import typia, { tags } from "typia";
import { Prisma } from "@prisma/client";
import { v4 } from "uuid";
import { toISOStringSafe } from "../util/toISOStringSafe";
import { IStoryfieldAiAuthenticatedUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiAuthenticatedUser";

/**
 * Register a new StoryField AI authenticatedUser
 * (storyfield_ai_authenticatedusers) given a verified external_user_id and
 * email.
 *
 * This operation creates a new user with unique external_user_id and email, and
 * fixed actor_type ('authenticatedUser'). It enforces uniqueness of both fields
 * and returns user info and an authentication token contract.
 *
 * @param props - Registration payload { body: {external_user_id, email,
 *   actor_type: 'authenticatedUser'} }
 * @returns IStoryfieldAiAuthenticatedUser.IAuthorized (authorized session
 *   envelope)
 * @throws {Error} - If external_user_id or email already exists, or actor_type
 *   is invalid
 */
export async function postauthAuthenticatedUserJoin(props: {
  body: IStoryfieldAiAuthenticatedUser.ICreate;
}): Promise<IStoryfieldAiAuthenticatedUser.IAuthorized> {
  const { external_user_id, email, actor_type } = props.body;
  if (actor_type !== "authenticatedUser") throw new Error("Invalid actor_type");

  // Enforce duplicate checking at application level
  const exists =
    await MyGlobal.prisma.storyfield_ai_authenticatedusers.findFirst({
      where: { OR: [{ external_user_id }, { email }] },
    });
  if (exists) {
    if (exists.external_user_id === external_user_id)
      throw new Error("Duplicate external_user_id");
    if (exists.email === email) throw new Error("Duplicate email");
    throw new Error("Duplicate registration");
  }

  // Generate new user fields
  const id = v4();
  const now = toISOStringSafe(new Date());

  // Insert new authenticated user
  let created;
  try {
    created = await MyGlobal.prisma.storyfield_ai_authenticatedusers.create({
      data: {
        id: id,
        external_user_id: external_user_id,
        email: email,
        actor_type: "authenticatedUser",
        created_at: now,
        updated_at: now,
      },
    });
  } catch (err: unknown) {
    // Catch concurrent unique error
    if (
      typeof err === "object" &&
      err !== null &&
      "code" in err &&
      (err as { code: string }).code === "P2002"
    ) {
      // Prisma unique violation
      throw new Error(
        "Duplicate registration (external_user_id or email already exists)",
      );
    }
    throw err;
  }

  // JWT Token logic
  // Access token expires in 1h, refresh in 7d
  const accessExp = Math.floor((Date.now() + 60 * 60 * 1000) / 1000);
  const refreshExp = Math.floor((Date.now() + 7 * 24 * 60 * 60 * 1000) / 1000);

  // Brand expiration as ISO 8601
  const expired_at = toISOStringSafe(new Date(accessExp * 1000));
  const refreshable_until = toISOStringSafe(new Date(refreshExp * 1000));

  // Create tokens (per contract, minimal claims)
  const access = jwt.sign(
    {
      id: id,
      type: "authenticatedUser",
    },
    MyGlobal.env.JWT_SECRET_KEY,
    {
      expiresIn: "1h",
      issuer: "autobe",
    },
  );
  const refresh = jwt.sign(
    {
      id: id,
      tokenType: "refresh",
    },
    MyGlobal.env.JWT_SECRET_KEY,
    {
      expiresIn: "7d",
      issuer: "autobe",
    },
  );

  return {
    id: id,
    external_user_id: external_user_id,
    email: email,
    actor_type: "authenticatedUser",
    created_at: now,
    updated_at: now,
    token: {
      access,
      refresh,
      expired_at,
      refreshable_until,
    },
  };
}

import jwt from "jsonwebtoken";
import { MyGlobal } from "../MyGlobal";
import typia, { tags } from "typia";
import { Prisma } from "@prisma/client";
import { v4 } from "uuid";
import { toISOStringSafe } from "../util/toISOStringSafe";
import { IStoryfieldAiAuthenticatedUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiAuthenticatedUser";

/**
 * Authenticate an existing authenticatedUser using external_user_id and email
 * as mapped from the Spring backend. Issues a session token (JWT) if the user
 * exists, is not soft deleted, and the credentials match.
 *
 * - Only users present in `storyfield_ai_authenticatedusers` with a null
 *   `deleted_at` (active) can log in.
 * - Issues access and refresh JWT tokens with proper claims, expiration, and
 *   issuer 'autobe'.
 * - All audit, trace, and session state is strictly determined by table content;
 *   no password or challenge/response is performed.
 *
 * @param props - Login request props
 * @param props.body - Contains the external_user_id and email for login mapping
 * @returns IStoryfieldAiAuthenticatedUser.IAuthorized session object with
 *   tokens if successful
 * @throws {Error} If credentials are invalid or user is soft deleted
 */
export async function postauthAuthenticatedUserLogin(props: {
  body: IStoryfieldAiAuthenticatedUser.ILogin;
}): Promise<IStoryfieldAiAuthenticatedUser.IAuthorized> {
  const { external_user_id, email } = props.body;
  // Query for user with exact credentials and soft-delete check
  const user = await MyGlobal.prisma.storyfield_ai_authenticatedusers.findFirst(
    {
      where: {
        external_user_id,
        email,
        deleted_at: null,
      },
      select: {
        id: true,
        external_user_id: true,
        email: true,
        actor_type: true,
        created_at: true,
        updated_at: true,
      },
    },
  );
  if (!user)
    throw new Error(
      "Invalid login: user not found or credentials are incorrect, or user is soft deleted.",
    );

  // Build payload for JWT access/refresh tokens
  const jwtPayload = {
    id: user.id,
    type: "authenticatedUser",
  };

  // Access: 1 hour; Refresh: 7 days
  // Calculate future expiration as ISO string without using Date directly in type
  const nowValue = toISOStringSafe(new Date());

  // 1 hour = 3600*1000ms; 7 days = 7*24*3600*1000ms
  // Compute future expires
  const expiresAtValue = toISOStringSafe(new Date(Date.now() + 3600 * 1000));
  const refreshExpiresAtValue = toISOStringSafe(
    new Date(Date.now() + 7 * 24 * 3600 * 1000),
  );

  // Generate JWT tokens
  const accessToken = await new Promise<string>((resolve, reject) => {
    jwt.sign(
      jwtPayload,
      MyGlobal.env.JWT_SECRET_KEY,
      {
        expiresIn: "1h",
        issuer: "autobe",
      },
      (err, token) => {
        if (err || typeof token !== "string")
          reject(err ?? new Error("Failed to sign token"));
        else resolve(token);
      },
    );
  });
  const refreshToken = await new Promise<string>((resolve, reject) => {
    jwt.sign(
      { ...jwtPayload, tokenType: "refresh" },
      MyGlobal.env.JWT_SECRET_KEY,
      {
        expiresIn: "7d",
        issuer: "autobe",
      },
      (err, token) => {
        if (err || typeof token !== "string")
          reject(err ?? new Error("Failed to sign refresh token"));
        else resolve(token);
      },
    );
  });

  return {
    id: user.id,
    external_user_id: user.external_user_id,
    email: user.email,
    actor_type: "authenticatedUser",
    created_at: toISOStringSafe(user.created_at),
    updated_at: toISOStringSafe(user.updated_at),
    token: {
      access: accessToken,
      refresh: refreshToken,
      expired_at: expiresAtValue,
      refreshable_until: refreshExpiresAtValue,
    },
  };
}

import jwt from "jsonwebtoken";
import { MyGlobal } from "../MyGlobal";
import typia, { tags } from "typia";
import { Prisma } from "@prisma/client";
import { v4 } from "uuid";
import { toISOStringSafe } from "../util/toISOStringSafe";
import { IStoryfieldAiAuthenticatedUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiAuthenticatedUser";

/**
 * Refresh session and access tokens for authenticatedUser
 * (storyfield_ai_token_sessions) given existing, valid session/refresh token.
 *
 * This operation verifies the current refresh token from the Authorization
 * header, validates that the associated session and user are active and not
 * revoked, then issues a new access and refresh token pair with updated
 * expiration. Session and audit data are updated for compliance.
 *
 * @param props - Props containing body (empty for refresh)
 * @returns IStoryfieldAiAuthenticatedUser.IAuthorized response (user profile +
 *   new token contract)
 * @throws {Error} When authorization is missing, token is invalid, session is
 *   expired, revoked, or user is inactive.
 */
export async function postauthAuthenticatedUserRefresh(props: {
  body: IStoryfieldAiAuthenticatedUser.IRefresh;
}): Promise<IStoryfieldAiAuthenticatedUser.IAuthorized> {
  // Framework should inject header/cookie/token as needed.
  // Here, expect the refresh token in Authorization header: Bearer <token>
  // Try reading from MyGlobal.request.headers (if implemented), else mock error
  // If no request context, throw error with clear message.
  const req = (MyGlobal as any).request;
  if (
    !req ||
    !req.headers ||
    typeof req.headers["authorization"] !== "string"
  ) {
    throw new Error("Authorization header with refresh token is required");
  }
  const authHeader: string = req.headers["authorization"];
  const [scheme, token] = authHeader.split(" ");
  if (scheme.toLowerCase() !== "bearer" || !token) {
    throw new Error("Malformed Authorization header - expected Bearer token");
  }
  const refreshToken = token;

  // Decode/verify JWT refresh token
  let decoded: unknown;
  try {
    decoded = jwt.verify(refreshToken, MyGlobal.env.JWT_SECRET_KEY, {
      issuer: "autobe",
    });
  } catch {
    throw new Error("Invalid or expired refresh token");
  }

  // Validate JWT payload shape
  if (
    typeof decoded !== "object" ||
    decoded === null ||
    !("id" in decoded) ||
    typeof (decoded as any).id !== "string" ||
    !("type" in decoded) ||
    (decoded as any).type !== "authenticatedUser"
  ) {
    throw new Error("Malformed refresh token payload");
  }
  const userId = (decoded as { id: string; type: string }).id;

  // Compute token hash
  const tokenHash = await MyGlobal.password.hash(refreshToken);
  const session = await MyGlobal.prisma.storyfield_ai_token_sessions.findFirst({
    where: {
      token_hash: tokenHash,
      authenticated_user_id: userId,
      deleted_at: null,
    },
  });
  if (!session) {
    throw new Error("Session not found or expired");
  }

  // Validate session is not expired (session.expires_at is string, must parse to Date)
  const now = new Date();
  if (now > new Date(session.expires_at)) {
    throw new Error("Session expired");
  }

  // Check for revocation
  const revoked =
    await MyGlobal.prisma.storyfield_ai_token_revocations.findFirst({
      where: { token_hash: tokenHash },
    });
  if (revoked) {
    throw new Error("Refresh token has been revoked");
  }

  // User account must be active
  const user = await MyGlobal.prisma.storyfield_ai_authenticatedusers.findFirst(
    {
      where: {
        id: userId,
        deleted_at: null,
      },
    },
  );
  if (!user) {
    throw new Error("User not found or deactivated");
  }

  // Compute new token expiration times (access: 1h, refresh: 30d)
  const accessExp = new Date(now.getTime() + 60 * 60 * 1000);
  const refreshExp = new Date(now.getTime() + 30 * 24 * 60 * 60 * 1000);

  // Generate new access and refresh tokens
  const newAccessToken = jwt.sign(
    {
      id: user.id,
      type: "authenticatedUser",
    },
    MyGlobal.env.JWT_SECRET_KEY,
    {
      issuer: "autobe",
      expiresIn: "1h",
    },
  );
  const newRefreshToken = jwt.sign(
    {
      id: user.id,
      type: "authenticatedUser",
    },
    MyGlobal.env.JWT_SECRET_KEY,
    {
      issuer: "autobe",
      expiresIn: "30d",
    },
  );

  // Update session timestamps
  const updateTimestamp = toISOStringSafe(now);
  await MyGlobal.prisma.storyfield_ai_token_sessions.update({
    where: { id: session.id },
    data: {
      refreshed_at: updateTimestamp,
      last_activity_at: updateTimestamp,
      updated_at: updateTimestamp,
    },
  });

  // Write audit log (ignore errors)
  try {
    await MyGlobal.prisma.storyfield_ai_auth_audit_logs.create({
      data: {
        id: v4(),
        token_session_id: session.id,
        authenticated_user_id: user.id,
        event_type: "refreshed",
        event_outcome: "success",
        event_message: "Token refreshed via /auth/authenticatedUser/refresh",
        created_at: updateTimestamp,
      },
    });
  } catch {}

  // Return refreshed user and token structure
  return {
    id: user.id,
    external_user_id: user.external_user_id,
    email: user.email,
    actor_type: "authenticatedUser",
    created_at: toISOStringSafe(user.created_at),
    updated_at: toISOStringSafe(user.updated_at),
    token: {
      access: newAccessToken,
      refresh: newRefreshToken,
      expired_at: toISOStringSafe(accessExp),
      refreshable_until: toISOStringSafe(refreshExp),
    },
  };
}

import jwt from "jsonwebtoken";
import { MyGlobal } from "../MyGlobal";
import typia, { tags } from "typia";
import { Prisma } from "@prisma/client";
import { v4 } from "uuid";
import { toISOStringSafe } from "../util/toISOStringSafe";
import { IStoryfieldAiSystemAdmin } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiSystemAdmin";

/**
 * Register new systemAdmin account in storyfield_ai_systemadmins table with JWT
 * token return.
 *
 * This operation securely onboards a new system administrator by inserting a
 * new record into the 'storyfield_ai_systemadmins' table using the provided
 * external_admin_id and email. Uniqueness is enforced on both fields. Returns a
 * JWT-based authorized session structure and creates an initial token session
 * record for the admin.
 *
 * No password is stored or involved; credential management is handled
 * externally (SSO/JWT).
 *
 * @param props - Registration details for the new system admin
 * @param props.body - Registration payload containing external_admin_id, email,
 *   and optional actor_type
 * @returns The authorized session representation for the newly registered
 *   system admin, including tokens and audit metadata
 * @throws {Error} When the external_admin_id or email is already registered as
 *   a system admin
 */
export async function postauthSystemAdminJoin(props: {
  body: IStoryfieldAiSystemAdmin.IJoin;
}): Promise<IStoryfieldAiSystemAdmin.IAuthorized> {
  const { body } = props;
  const now: string & tags.Format<"date-time"> = toISOStringSafe(new Date());
  // Always generate a new UUID for primary key
  const adminId: string & tags.Format<"uuid"> = v4();
  // If actor_type is omitted, default to 'systemAdmin'
  const actor_type = body.actor_type ?? "systemAdmin";

  // Enforce uniqueness: external_admin_id and email must not exist
  const duplicate = await MyGlobal.prisma.storyfield_ai_systemadmins.findFirst({
    where: {
      OR: [
        { external_admin_id: body.external_admin_id },
        { email: body.email },
      ],
    },
  });
  if (duplicate) {
    throw new Error(
      "A system admin with this external_admin_id or email already exists.",
    );
  }

  // Create the system admin account
  const created = await MyGlobal.prisma.storyfield_ai_systemadmins.create({
    data: {
      id: adminId,
      external_admin_id: body.external_admin_id,
      email: body.email,
      actor_type: actor_type,
      created_at: now,
      updated_at: now,
    },
  });

  // JWT token (1 hour expiry for access, 7 days for refresh)
  const accessTokenExpiresInMs = 1000 * 60 * 60; // 1 hour
  const refreshTokenExpiresInMs = 1000 * 60 * 60 * 24 * 7; // 7 days
  const accessExpiredAt: string & tags.Format<"date-time"> = toISOStringSafe(
    new Date(Date.now() + accessTokenExpiresInMs),
  );
  const refreshableUntil: string & tags.Format<"date-time"> = toISOStringSafe(
    new Date(Date.now() + refreshTokenExpiresInMs),
  );

  // JWT payload per contract
  const accessToken = jwt.sign(
    {
      id: adminId,
      type: actor_type,
    },
    MyGlobal.env.JWT_SECRET_KEY,
    {
      expiresIn: "1h",
      issuer: "autobe",
    },
  );
  const refreshToken = jwt.sign(
    {
      id: adminId,
      type: actor_type,
      tokenType: "refresh",
    },
    MyGlobal.env.JWT_SECRET_KEY,
    {
      expiresIn: "7d",
      issuer: "autobe",
    },
  );

  // Create token session for admin (required for all JWT usage)
  // Token hash is stored using secure hash (never plain token)
  await MyGlobal.prisma.storyfield_ai_token_sessions.create({
    data: {
      id: v4(),
      system_admin_id: adminId,
      token_hash: await MyGlobal.password.hash(accessToken),
      fingerprint: v4(),
      issued_at: now,
      expires_at: accessExpiredAt,
      refreshed_at: undefined,
      last_activity_at: now,
      created_at: now,
      updated_at: now,
      deleted_at: undefined,
    },
  });

  // Return full admin authorized DTO
  return {
    id: created.id,
    external_admin_id: created.external_admin_id,
    email: created.email,
    actor_type: created.actor_type,
    last_login_at: undefined,
    admin_notes: undefined,
    created_at: created.created_at,
    updated_at: created.updated_at,
    deleted_at: undefined,
    token: {
      access: accessToken,
      refresh: refreshToken,
      expired_at: accessExpiredAt,
      refreshable_until: refreshableUntil,
    },
  };
}

import jwt from "jsonwebtoken";
import { MyGlobal } from "../MyGlobal";
import typia, { tags } from "typia";
import { Prisma } from "@prisma/client";
import { v4 } from "uuid";
import { toISOStringSafe } from "../util/toISOStringSafe";
import { IStoryfieldAiSystemAdmin } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiSystemAdmin";

/**
 * Authenticate systemAdmin using external_admin_id and issue JWT access token
 * if active (storyfield_ai_systemadmins).
 *
 * This function implements admin login using external SSO/JWT-mapped identity,
 * verifying existence and non-deactivated status. It issues new access/refresh
 * tokens, records a session in 'storyfield_ai_token_sessions', updates
 * last_login_at on the admin, and logs to 'storyfield_ai_auth_audit_logs'.
 *
 * @param props - Object containing system admin login info
 * @param props.body - The login data: external_admin_id and email (SSO mapped)
 * @returns Authorized system admin session details, JWT tokens, and admin
 *   identity fields
 * @throws {Error} If login credentials are invalid or admin is soft-deleted
 */
export async function postauthSystemAdminLogin(props: {
  body: IStoryfieldAiSystemAdmin.ILogin;
}): Promise<IStoryfieldAiSystemAdmin.IAuthorized> {
  const { external_admin_id, email } = props.body;

  // 1. Find the admin by credentials, enforcing active status only
  const admin = await MyGlobal.prisma.storyfield_ai_systemadmins.findFirst({
    where: {
      external_admin_id,
      email,
      deleted_at: null,
    },
  });
  if (!admin) {
    throw new Error("Invalid credentials or administrator deactivated.");
  }

  // 2. Set last_login_at to now (ISO string)
  const nowDate = toISOStringSafe(new Date());
  await MyGlobal.prisma.storyfield_ai_systemadmins.update({
    where: { id: admin.id },
    data: { last_login_at: nowDate },
  });

  // 3. Generate JWT tokens
  const accessTTL = 60 * 60; // 1 hour (in seconds)
  const refreshTTL = 60 * 60 * 24 * 7; // 7 days (in seconds)
  const nowEpochSec = Math.floor(Date.now() / 1000);
  const accessExpSec = nowEpochSec + accessTTL;
  const refreshExpSec = nowEpochSec + refreshTTL;

  // Compose payload
  const payload = { id: admin.id, type: "systemAdmin" };
  const accessToken = jwt.sign(payload, MyGlobal.env.JWT_SECRET_KEY, {
    expiresIn: accessTTL,
    issuer: "autobe",
  });
  const refreshPayload = {
    id: admin.id,
    type: "systemAdmin",
    tokenType: "refresh",
  };
  const refreshToken = jwt.sign(refreshPayload, MyGlobal.env.JWT_SECRET_KEY, {
    expiresIn: refreshTTL,
    issuer: "autobe",
  });

  // Calculate expiry strings
  const accessExpiresAt = toISOStringSafe(new Date(accessExpSec * 1000));
  const refreshExpiresAt = toISOStringSafe(new Date(refreshExpSec * 1000));

  // 4. Register session in token_sessions
  const sessionId = v4();
  const sessionFingerprint = v4();
  const tokenHash = await MyGlobal.password.hash(accessToken);

  await MyGlobal.prisma.storyfield_ai_token_sessions.create({
    data: {
      id: sessionId,
      system_admin_id: admin.id,
      token_hash: tokenHash,
      fingerprint: sessionFingerprint,
      issued_at: accessExpiresAt,
      expires_at: accessExpiresAt,
      refreshed_at: null,
      last_activity_at: accessExpiresAt,
      created_at: accessExpiresAt,
      updated_at: accessExpiresAt,
      deleted_at: null,
      authenticated_user_id: undefined,
    },
  });

  // 5. Audit log: admin login event
  await MyGlobal.prisma.storyfield_ai_auth_audit_logs.create({
    data: {
      id: v4(),
      token_session_id: sessionId,
      authenticated_user_id: undefined,
      system_admin_id: admin.id,
      event_type: "issued",
      event_outcome: "success",
      event_message: "systemAdmin login",
      source_ip: undefined,
      user_agent: undefined,
      created_at: accessExpiresAt,
    },
  });

  // 6. Return authorized session info. Null/undefined handled for optional fields.
  return {
    id: admin.id,
    external_admin_id: admin.external_admin_id,
    email: admin.email,
    actor_type: "systemAdmin",
    last_login_at: nowDate ?? undefined,
    admin_notes: admin.admin_notes ?? undefined,
    created_at: toISOStringSafe(admin.created_at),
    updated_at: toISOStringSafe(admin.updated_at),
    deleted_at: admin.deleted_at
      ? toISOStringSafe(admin.deleted_at)
      : undefined,
    token: {
      access: accessToken,
      refresh: refreshToken,
      expired_at: accessExpiresAt,
      refreshable_until: refreshExpiresAt,
    },
  };
}

import jwt from "jsonwebtoken";
import { MyGlobal } from "../MyGlobal";
import typia, { tags } from "typia";
import { Prisma } from "@prisma/client";
import { v4 } from "uuid";
import { toISOStringSafe } from "../util/toISOStringSafe";
import { IStoryfieldAiSystemAdmin } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiSystemAdmin";

/**
 * Refresh JWT access token for systemAdmin using valid active refresh
 * token/session.
 *
 * This endpoint validates the refresh token for an active, non-revoked token
 * session tied to a system administrator. It issues a new JWT access and
 * refresh token, updates session lifecycle fields, and writes an audit event.
 * It strictly enforces session, soft-deletion, and revocation checks, ensuring
 * only active, privileged sessions are refreshed.
 *
 * @param props - Request props containing the empty IRefresh body (actual token
 *   provided in HTTP context)
 * @returns Fully authorized system admin DTO with renewed tokens
 * @throws {Error} If the refresh token is missing, expired, revoked, or not
 *   linked to an active system admin.
 */
export async function postauthSystemAdminRefresh(props: {
  body: IStoryfieldAiSystemAdmin.IRefresh;
}): Promise<IStoryfieldAiSystemAdmin.IAuthorized> {
  // Extract refresh token from context (must be present in request headers)
  const refreshToken: string = MyGlobal.getRefreshTokenFromContext();
  if (!refreshToken) {
    await MyGlobal.prisma.storyfield_ai_auth_audit_logs.create({
      data: {
        id: v4(),
        token_session_id: null,
        system_admin_id: null,
        event_type: "refresh_denied",
        event_outcome: "failure",
        event_message: "Missing refresh token in request",
        source_ip: MyGlobal.getSourceIP(),
        user_agent: MyGlobal.getUserAgent(),
        created_at: toISOStringSafe(new Date()),
      },
    });
    throw new Error("Missing refresh token");
  }

  // Attempt to decode and verify the JWT
  let decoded: unknown;
  try {
    decoded = jwt.verify(refreshToken, MyGlobal.env.JWT_SECRET_KEY, {
      issuer: "autobe",
    });
  } catch (err) {
    await MyGlobal.prisma.storyfield_ai_auth_audit_logs.create({
      data: {
        id: v4(),
        token_session_id: null,
        system_admin_id: null,
        event_type: "refresh_denied",
        event_outcome: "failure",
        event_message: `Invalid refresh token: ${err instanceof Error ? err.message : String(err)}`,
        source_ip: MyGlobal.getSourceIP(),
        user_agent: MyGlobal.getUserAgent(),
        created_at: toISOStringSafe(new Date()),
      },
    });
    throw new Error("Invalid or expired refresh token");
  }

  // Hash the presented refresh token for DB lookup
  const tokenHash: string = await MyGlobal.password.hash(refreshToken);
  // Find the active session (non-deleted, not expired, systemAdmin-linked)
  const nowIso = toISOStringSafe(new Date());
  const session = await MyGlobal.prisma.storyfield_ai_token_sessions.findFirst({
    where: {
      token_hash: tokenHash,
      deleted_at: null,
      system_admin_id: { not: null },
      expires_at: { gt: nowIso },
    },
  });
  if (!session) {
    await MyGlobal.prisma.storyfield_ai_auth_audit_logs.create({
      data: {
        id: v4(),
        token_session_id: null,
        system_admin_id: null,
        event_type: "refresh_denied",
        event_outcome: "failure",
        event_message:
          "Token session not found, expired, deleted, not admin-linked, or already ended.",
        source_ip: MyGlobal.getSourceIP(),
        user_agent: MyGlobal.getUserAgent(),
        created_at: nowIso,
      },
    });
    throw new Error("Token session not active");
  }

  // Check for token revocation (deny-list)
  const revocation =
    await MyGlobal.prisma.storyfield_ai_token_revocations.findUnique({
      where: { token_hash: tokenHash },
    });
  if (revocation) {
    await MyGlobal.prisma.storyfield_ai_auth_audit_logs.create({
      data: {
        id: v4(),
        token_session_id: session.id,
        system_admin_id: session.system_admin_id,
        event_type: "refresh_denied",
        event_outcome: "failure",
        event_message: "Refresh token was revoked (deny-list)",
        source_ip: MyGlobal.getSourceIP(),
        user_agent: MyGlobal.getUserAgent(),
        created_at: nowIso,
      },
    });
    throw new Error("Refresh token has been revoked");
  }

  // Lookup the current system admin, ensure not deleted
  const adminRow = await MyGlobal.prisma.storyfield_ai_systemadmins.findFirst({
    where: {
      id: session.system_admin_id,
      deleted_at: null,
    },
  });
  if (!adminRow) {
    await MyGlobal.prisma.storyfield_ai_auth_audit_logs.create({
      data: {
        id: v4(),
        token_session_id: session.id,
        system_admin_id: session.system_admin_id,
        event_type: "refresh_denied",
        event_outcome: "failure",
        event_message: "System admin for session not found or was deleted",
        source_ip: MyGlobal.getSourceIP(),
        user_agent: MyGlobal.getUserAgent(),
        created_at: nowIso,
      },
    });
    throw new Error("Admin account not active");
  }

  // Generate new tokens
  const issuedAt = toISOStringSafe(new Date());
  const accessTokenExpiryDate = new Date(Date.now() + 60 * 60 * 1000);
  const refreshExpiryDate = new Date(Date.now() + 7 * 24 * 60 * 60 * 1000);
  const expiresAtIso: string & tags.Format<"date-time"> = toISOStringSafe(
    accessTokenExpiryDate,
  );
  const refreshableUntilIso: string & tags.Format<"date-time"> =
    toISOStringSafe(refreshExpiryDate);
  const accessPayload = {
    id: adminRow.id,
    type: "systemAdmin",
  };
  const newAccessToken = jwt.sign(accessPayload, MyGlobal.env.JWT_SECRET_KEY, {
    expiresIn: 60 * 60, // 1 hour in seconds
    issuer: "autobe",
  });
  const newRefreshToken = jwt.sign(
    { sessionId: session.id, type: "refresh" },
    MyGlobal.env.JWT_SECRET_KEY,
    { expiresIn: 7 * 24 * 60 * 60, issuer: "autobe" }, // 7 days in seconds
  );
  // Update session: refreshed_at/last_activity_at
  await MyGlobal.prisma.storyfield_ai_token_sessions.update({
    where: { id: session.id },
    data: {
      refreshed_at: issuedAt,
      last_activity_at: issuedAt,
    },
  });
  // Write audit log for refresh event
  await MyGlobal.prisma.storyfield_ai_auth_audit_logs.create({
    data: {
      id: v4(),
      token_session_id: session.id,
      system_admin_id: session.system_admin_id,
      event_type: "refreshed",
      event_outcome: "success",
      event_message: "System admin token refreshed",
      source_ip: MyGlobal.getSourceIP(),
      user_agent: MyGlobal.getUserAgent(),
      created_at: issuedAt,
    },
  });
  // Compose response
  return {
    id: adminRow.id,
    external_admin_id: adminRow.external_admin_id,
    email: adminRow.email,
    actor_type: "systemAdmin",
    last_login_at: adminRow.last_login_at
      ? toISOStringSafe(adminRow.last_login_at)
      : undefined,
    admin_notes: adminRow.admin_notes ?? undefined,
    created_at: toISOStringSafe(adminRow.created_at),
    updated_at: toISOStringSafe(adminRow.updated_at),
    deleted_at: adminRow.deleted_at
      ? toISOStringSafe(adminRow.deleted_at)
      : undefined,
    token: {
      access: newAccessToken,
      refresh: newRefreshToken,
      expired_at: expiresAtIso,
      refreshable_until: refreshableUntilIso,
    },
  };
}

import jwt from "jsonwebtoken";
import { MyGlobal } from "../MyGlobal";
import typia, { tags } from "typia";
import { Prisma } from "@prisma/client";
import { v4 } from "uuid";
import { toISOStringSafe } from "../util/toISOStringSafe";
import { IStoryfieldAiAuthenticatedusers } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiAuthenticatedusers";
import { IPageIStoryfieldAiAuthenticatedusers } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIStoryfieldAiAuthenticatedusers";
import { SystemadminPayload } from "../decorators/payload/SystemadminPayload";

/**
 * Retrieve and search a paginated list of authenticated users
 * (storyfield_ai_authenticatedusers).
 *
 * This provider lists registered, verified authenticated users in the
 * StoryField AI service, allowing filtering, searching, and paging on all
 * essential onboarding and compliance data. Supports filtering by email,
 * external user ID, actor_type, creation and update date, and soft-deletion
 * status. Only system admins may access this operation. All result dates are
 * returned as ISO 8601 strings with correct format branding. Sorting supports
 * allowed fields only.
 *
 * @param props - Properties for call
 * @param props.systemAdmin - The authenticated system admin making this request
 * @param props.body - Search, filter, sort, and pagination options (see
 *   IStoryfieldAiAuthenticatedusers.IRequest)
 * @returns Paginated summary listing of authenticated users (ISummary)
 * @throws {Error} If not called by a valid system admin, or if sort/order field
 *   is not allowed
 */
export async function patchstoryfieldAiSystemAdminAuthenticatedUsers(props: {
  systemAdmin: SystemadminPayload;
  body: IStoryfieldAiAuthenticatedusers.IRequest;
}): Promise<IPageIStoryfieldAiAuthenticatedusers.ISummary> {
  const { systemAdmin, body } = props;

  // Authorization check: only systemAdmin.type === 'systemAdmin' allowed
  if (!systemAdmin || systemAdmin.type !== "systemAdmin") {
    throw new Error(
      "Unauthorized: Only system admin may list authenticated users",
    );
  }

  // Allowed sort fields for defensive sorting (all ISummary keys except deleted_at, which is optional)
  const ALLOWED_SORT_FIELDS = [
    "id",
    "external_user_id",
    "email",
    "created_at",
    "updated_at",
  ];

  const DEFAULT_SORT_FIELD = "created_at";
  const DEFAULT_SORT_ORDER = "desc";

  // Parse pagination params with defaults enforced, using Number to strip typia tags
  const page = Number(body.page ?? 1);
  const limit = Math.min(Number(body.limit ?? 20), 100);
  const skip = (page - 1) * limit;

  // Defensive: If sort given, only allow whitelisted fields, else default
  const sortField =
    body.sort !== undefined && ALLOWED_SORT_FIELDS.includes(body.sort)
      ? body.sort
      : DEFAULT_SORT_FIELD;
  // Defensive: Only allow 'asc'/'desc', else default
  const sortOrder =
    body.order === "asc" || body.order === "desc"
      ? body.order
      : DEFAULT_SORT_ORDER;

  // Build Prisma where clause (inline, immutable)
  const where = {
    ...(body.email !== undefined && { email: body.email }),
    ...(body.external_user_id !== undefined && {
      external_user_id: body.external_user_id,
    }),
    ...(body.actor_type !== undefined && { actor_type: body.actor_type }),
    // Date ranges: created_at
    ...(body.created_from !== undefined || body.created_to !== undefined
      ? {
          created_at: {
            ...(body.created_from !== undefined && { gte: body.created_from }),
            ...(body.created_to !== undefined && { lte: body.created_to }),
          },
        }
      : {}),
    // Date ranges: updated_at
    ...(body.updated_from !== undefined || body.updated_to !== undefined
      ? {
          updated_at: {
            ...(body.updated_from !== undefined && { gte: body.updated_from }),
            ...(body.updated_to !== undefined && { lte: body.updated_to }),
          },
        }
      : {}),
    // Soft delete filter
    ...(typeof body.deleted === "boolean"
      ? body.deleted
        ? { deleted_at: { not: null } }
        : { deleted_at: null }
      : { deleted_at: null }),
  };

  // Query rows and count in parallel (immutable, no intermediate variables)
  const [rows, total] = await Promise.all([
    MyGlobal.prisma.storyfield_ai_authenticatedusers.findMany({
      where,
      orderBy: { [sortField]: sortOrder },
      skip,
      take: limit,
    }),
    MyGlobal.prisma.storyfield_ai_authenticatedusers.count({ where }),
  ]);

  // Map to ISummary, converting dates to branded strings
  const data = rows.map((row) => ({
    id: row.id,
    external_user_id: row.external_user_id,
    email: row.email,
    created_at: toISOStringSafe(row.created_at),
    updated_at: toISOStringSafe(row.updated_at),
    deleted_at:
      row.deleted_at !== null && row.deleted_at !== undefined
        ? toISOStringSafe(row.deleted_at)
        : undefined,
  }));

  // Build response per IPageIStoryfieldAiAuthenticatedusers.ISummary
  return {
    pagination: {
      current: page,
      limit: limit,
      records: total,
      pages: Math.ceil(total / limit),
    },
    data,
  };
}

import jwt from "jsonwebtoken";
import { MyGlobal } from "../MyGlobal";
import typia, { tags } from "typia";
import { Prisma } from "@prisma/client";
import { v4 } from "uuid";
import { toISOStringSafe } from "../util/toISOStringSafe";
import { IStoryfieldAiAuthenticatedusers } from "@ORGANIZATION/PROJECT-api/lib/structures/IStoryfieldAiAuthenticatedusers";
import { SystemadminPayload } from "../decorators/payload/SystemadminPayload";

/**
 * Retrieve full details for a single authenticated user
 * (storyfield_ai_authenticatedusers).
 *
 * Fetches all registered and audit fields for one authenticated user, as
 * verified via the Spring backend, by unique ID. Only accessible to system
 * administrators for compliance, investigation, and administrative review. The
 * returned object includes all identity, audit, and soft-deletion (deleted_at)
 * status fields.
 *
 * @param props - Function input properties
