export async function patchstoryfieldAiSystemAdminDeploymentLogs(props: {
  systemAdmin: SystemadminPayload;
  body: IStoryfieldAiDeploymentLog.IRequest;
}): Promise<IPageIStoryfieldAiDeploymentLog.ISummary> {
