  @@index([enduser_id, created_at], map: "telegram_file_downloader_download_jobs_enduser_id_crea_5d2337f2")
  @@index([developer_id, created_at], map: "telegram_file_downloader_download_jobs_developer_id_cr_208a2a3d")
