import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { ITelegramFileDownloaderEndUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITelegramFileDownloaderEndUser";

/**
 * Test the successful registration of a Telegram File Downloader end user via
 * the /auth/endUser/join endpoint. Validates account creation with a unique
 * email and hashed password, issuance of initial JWT tokens for authentication,
 * and proper rejection of duplicate email registrations.
 *
 * Workflow:
 *
 * 1. Register a new user with a unique email and password hash.
 * 2. Confirm the response contains authorized user info with tokens.
 * 3. Confirm returned email matches request.
 * 4. Verify tokens are present and well-formed.
 * 5. Attempt duplicate registration and expect failure.
 */
export async function test_api_enduser_join_successful_registration_and_token_issuance(
  connection: api.IConnection,
) {
  // Step 1: Generate a unique legitimate email and a random hashed password
  const email: string & tags.Format<"email"> = typia.random<
    string & tags.Format<"email">
  >();
  const passwordHash: string = RandomGenerator.alphaNumeric(20);

  // Request body for registration
  const createBody = {
    email: email,
    password_hash: passwordHash,
  } satisfies ITelegramFileDownloaderEndUser.ICreate;

  // Step 2: Call the join endpoint to register new user
  const authorizedUser: ITelegramFileDownloaderEndUser.IAuthorized =
    await api.functional.auth.endUser.join(connection, {
      body: createBody,
    });

  // Step 3: Assert the returned user data
  typia.assert(authorizedUser);

  // Step 4: Validate that email matches
  TestValidator.equals(
    "Returned user email should match requested email",
    authorizedUser.email,
    email,
  );

  // Step 5: Validate token presence and structure
  TestValidator.predicate(
    "Token access token should be a non-empty string",
    typeof authorizedUser.token.access === "string" &&
      authorizedUser.token.access.length > 0,
  );
  TestValidator.predicate(
    "Token refresh token should be a non-empty string",
    typeof authorizedUser.token.refresh === "string" &&
      authorizedUser.token.refresh.length > 0,
  );
  TestValidator.predicate(
    "Token expired_at should be a valid date-time string",
    typeof authorizedUser.token.expired_at === "string" &&
      authorizedUser.token.expired_at.length > 0,
  );
  TestValidator.predicate(
    "Token refreshable_until should be a valid date-time string",
    typeof authorizedUser.token.refreshable_until === "string" &&
      authorizedUser.token.refreshable_until.length > 0,
  );

  // Step 6: Attempt duplicate registration with same email and expect failure
  await TestValidator.error(
    "Duplicate email registration should fail",
    async () => {
      await api.functional.auth.endUser.join(connection, {
        body: createBody,
      });
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { ITelegramFileDownloaderEndUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITelegramFileDownloaderEndUser";

export async function test_api_enduser_login_with_valid_and_invalid_credentials(
  connection: api.IConnection,
) {
  // Step 1: Generate plaintext password and register a new end user
  const plaintextPassword = RandomGenerator.alphaNumeric(16);

  const createBody = {
    email: typia.random<string & tags.Format<"email">>(),
    password_hash: plaintextPassword,
  } satisfies ITelegramFileDownloaderEndUser.ICreate;

  const created: ITelegramFileDownloaderEndUser.IAuthorized =
    await api.functional.auth.endUser.join(connection, {
      body: createBody,
    });
  typia.assert(created);

  // Step 2: Successful login attempt with correct plaintext password
  const loginBody = {
    email: createBody.email,
    password: plaintextPassword, // Use plaintext password for login
  } satisfies ITelegramFileDownloaderEndUser.ILogin;

  const loggedIn: ITelegramFileDownloaderEndUser.IAuthorized =
    await api.functional.auth.endUser.login(connection, { body: loginBody });
  typia.assert(loggedIn);

  TestValidator.equals(
    "Logged in user email matches registered email",
    loggedIn.email,
    createBody.email,
  );

  TestValidator.predicate(
    "JWT access token exists",
    typeof loggedIn.token.access === "string" &&
      loggedIn.token.access.length > 0,
  );

  TestValidator.predicate(
    "JWT refresh token exists",
    typeof loggedIn.token.refresh === "string" &&
      loggedIn.token.refresh.length > 0,
  );

  // Step 3: Failed login attempt with incorrect password
  const wrongPasswordBody = {
    email: createBody.email,
    password: RandomGenerator.alphaNumeric(12), // wrong password
  } satisfies ITelegramFileDownloaderEndUser.ILogin;

  await TestValidator.error("Invalid login with wrong password", async () => {
    await api.functional.auth.endUser.login(connection, {
      body: wrongPasswordBody,
    });
  });

  // Step 4: Failed login attempt with non-existent email
  const nonExistentEmailBody = {
    email: `nonexistent_${RandomGenerator.alphaNumeric(8)}@example.com`,
    password: RandomGenerator.alphaNumeric(16),
  } satisfies ITelegramFileDownloaderEndUser.ILogin;

  await TestValidator.error(
    "Invalid login with non-existent email",
    async () => {
      await api.functional.auth.endUser.login(connection, {
        body: nonExistentEmailBody,
      });
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { ITelegramFileDownloaderEndUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITelegramFileDownloaderEndUser";

/**
 * This E2E test function validates the token refresh flow for an end user via
 * the /auth/endUser/refresh endpoint.
 *
 * It covers the lifecycle of token management starting from user registration,
 * where an end user is created and initial JWT tokens (access and refresh) are
 * issued. The test then performs a valid refresh token request and validates
 * that new JWT tokens are issued. It also attempts a refresh with an invalid
 * token and checks that an error is correctly thrown.
 *
 * This ensures secure management of JWT tokens and proper error handling for
 * invalid refresh attempts.
 */
export async function test_api_enduser_token_refresh_with_valid_and_invalid_tokens(
  connection: api.IConnection,
) {
  // Step 1: Register a new end user and obtain tokens
  const email = typia.random<string & tags.Format<"email">>();
  const passwordHash = RandomGenerator.alphaNumeric(16);
  const createBody = {
    email: email,
    password_hash: passwordHash,
  } satisfies ITelegramFileDownloaderEndUser.ICreate;

  const authorized: ITelegramFileDownloaderEndUser.IAuthorized =
    await api.functional.auth.endUser.join(connection, { body: createBody });
  typia.assert(authorized);

  // Step 2: Refresh tokens with valid refresh token
  const refreshBody = {
    refresh_token: authorized.token.refresh,
  } satisfies ITelegramFileDownloaderEndUser.IRefresh;

  const refreshed: ITelegramFileDownloaderEndUser.IAuthorized =
    await api.functional.auth.endUser.refresh(connection, {
      body: refreshBody,
    });
  typia.assert(refreshed);

  // Verify that new tokens are different from original
  TestValidator.notEquals(
    "refresh token should be updated",
    authorized.token.refresh,
    refreshed.token.refresh,
  );

  TestValidator.notEquals(
    "access token should be updated",
    authorized.token.access,
    refreshed.token.access,
  );

  // Step 3: Attempt refresh with invalid refresh token and expect failure
  const invalidRefreshBody = {
    refresh_token: RandomGenerator.alphaNumeric(64),
  } satisfies ITelegramFileDownloaderEndUser.IRefresh;

  await TestValidator.error(
    "refresh should fail with invalid refresh token",
    async () => {
      await api.functional.auth.endUser.refresh(connection, {
        body: invalidRefreshBody,
      });
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { ITelegramFileDownloaderEndUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITelegramFileDownloaderEndUser";

/**
 * End user password reset full flow test.
 *
 * Validates the password reset API including happy path and common failure
 * cases.
 *
 * 1. Registers an end user.
 * 2. Performs a successful password reset for the existing user.
 * 3. Attempts password reset with non-existent email (expect failure).
 * 4. Attempts password reset with weak password for existing user (expect
 *    failure).
 */
export async function test_api_enduser_password_reset_flow_valid_and_invalid_requests(
  connection: api.IConnection,
) {
  // Step 1: Register a new end user
  const userCreateBody = {
    email: typia.random<string & tags.Format<"email">>(),
    password_hash: RandomGenerator.alphaNumeric(32),
  } satisfies ITelegramFileDownloaderEndUser.ICreate;

  const authorizedUser: ITelegramFileDownloaderEndUser.IAuthorized =
    await api.functional.auth.endUser.join(connection, {
      body: userCreateBody,
    });
  typia.assert(authorizedUser);

  // Step 2: Successful password reset for existing user
  const newValidPassword = RandomGenerator.alphaNumeric(16); // assume complex enough

  const resetRequest: ITelegramFileDownloaderEndUser.IResetPassword = {
    email: authorizedUser.email,
    new_password: newValidPassword,
  } satisfies ITelegramFileDownloaderEndUser.IResetPassword;

  const resetResponse: ITelegramFileDownloaderEndUser.IResetPasswordResponse =
    await api.functional.auth.endUser.password.reset.resetPassword(connection, {
      body: resetRequest,
    });
  typia.assert(resetResponse);
  TestValidator.predicate(
    "password reset success flag should be true for valid reset",
    resetResponse.success === true,
  );

  // Step 3: Attempt password reset with non-existent email
  const resetRequestNonExistent: ITelegramFileDownloaderEndUser.IResetPassword =
    {
      email: `not.exist.${RandomGenerator.alphaNumeric(8)}@example.com`,
      new_password: RandomGenerator.alphaNumeric(16),
    } satisfies ITelegramFileDownloaderEndUser.IResetPassword;

  await TestValidator.error(
    "password reset attempt with non-existent email should fail",
    async () => {
      await api.functional.auth.endUser.password.reset.resetPassword(
        connection,
        {
          body: resetRequestNonExistent,
        },
      );
    },
  );

  // Step 4: Attempt password reset with weak/invalid password for existing user
  // Assuming password policy requires length > 8 and at least alphnum
  // We'll attempt with an invalid password (too short)

  const resetRequestWeakPassword: ITelegramFileDownloaderEndUser.IResetPassword =
