import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { ITelegramFileDownloaderAdministrator } from "@ORGANIZATION/PROJECT-api/lib/structures/ITelegramFileDownloaderAdministrator";
import type { ITelegramFileDownloaderSubscriptionPlans } from "@ORGANIZATION/PROJECT-api/lib/structures/ITelegramFileDownloaderSubscriptionPlans";

/**
 * Test the creation of a new subscription plan by an administrator through the
 * following steps. First, register an administrator account by providing a
 * unique email and hashed password through POST /auth/administrator/join,
 * acquiring an administrator authorization response including a JWT token.
 * Next, login using the registered email and raw password via POST
 * /auth/administrator/login to obtain a fresh JWT token for authorization.
 * Using this authenticated context, create a new subscription plan by POST
 * /telegramFileDownloader/administrator/subscription/plans, providing all
 * mandatory fields such as unique code, plan name, monthly price in USD,
 * maximum files allowed per day (integer), maximum file size in MB (integer),
 * total storage quota in MB (integer), and current status string
 * (active/inactive). Confirm that the created plan response matches the
 * submitted data and valid UUID is assigned in the id property. Then, validate
 * error handling by attempting to create a plan with missing mandatory fields
 * and creating a plan with a duplicate code to ensure these cases are rejected
 * with appropriate errors. This test verifies proper administrator
 * authentication, authorization enforcement, data persistence accuracy, and
 * error validation.
 */
export async function test_api_subscription_plan_creation_by_administrator(
  connection: api.IConnection,
) {
  // 1. Administrator registration
  const adminEmail: string = typia.random<string & tags.Format<"email">>();
  const adminPassword: string = "a_strong_password";
  const passwordHash = adminPassword; // For testing, plain password as hash (usually a hashed value)

  const adminCreateBody = {
    email: adminEmail,
    password_hash: passwordHash,
  } satisfies ITelegramFileDownloaderAdministrator.ICreate;

  const adminAuthorized = await api.functional.auth.administrator.join(
    connection,
    {
      body: adminCreateBody,
    },
  );
  typia.assert(adminAuthorized);

  // 2. Administrator login
  const adminLoginBody = {
    email: adminEmail,
    password: adminPassword,
  } satisfies ITelegramFileDownloaderAdministrator.ILogin;

  const adminLoginAuthorized = await api.functional.auth.administrator.login(
    connection,
    {
      body: adminLoginBody,
    },
  );
  typia.assert(adminLoginAuthorized);

  // 3. Creation of subscription plan
  const uniquePlanCode = `PLAN-${RandomGenerator.alphaNumeric(6)}`;

  const subscriptionPlanCreateBody = {
    code: uniquePlanCode,
    name: `Subscription Plan ${RandomGenerator.alphabets(5)}`,
    price: Math.floor(Math.random() * 100) + 1, // Monthly price between 1 and 100 USD
    max_files_per_day: Math.floor(Math.random() * 50) + 1, // Max files per day 1-50
    max_file_size_mb: Math.floor(Math.random() * 1024) + 1, // Max file size MB 1-1024
    total_storage_mb: Math.floor(Math.random() * 10240) + 1, // Storage 1-10240 MB
    status: "active",
  } satisfies ITelegramFileDownloaderSubscriptionPlans.ICreate;

  const createdPlan =
    await api.functional.telegramFileDownloader.administrator.subscription.plans.create(
      connection,
      {
        body: subscriptionPlanCreateBody,
      },
    );
  typia.assert(createdPlan);

  TestValidator.equals(
    "Plan code matches",
    createdPlan.code,
    subscriptionPlanCreateBody.code,
  );
  TestValidator.equals(
    "Plan name matches",
    createdPlan.name,
    subscriptionPlanCreateBody.name,
  );
  TestValidator.equals(
    "Plan price matches",
    createdPlan.price,
    subscriptionPlanCreateBody.price,
  );
  TestValidator.equals(
    "Max files per day matches",
    createdPlan.max_files_per_day,
    subscriptionPlanCreateBody.max_files_per_day,
  );
  TestValidator.equals(
    "Max file size MB matches",
    createdPlan.max_file_size_mb,
    subscriptionPlanCreateBody.max_file_size_mb,
  );
  TestValidator.equals(
    "Total storage MB matches",
    createdPlan.total_storage_mb,
    subscriptionPlanCreateBody.total_storage_mb,
  );
  TestValidator.equals(
    "Plan status matches",
    createdPlan.status,
    subscriptionPlanCreateBody.status,
  );

  // 4. Error testing: duplicate plan code
  await TestValidator.error(
    "subscription plan creation with duplicate code should fail",
    async () => {
      await api.functional.telegramFileDownloader.administrator.subscription.plans.create(
        connection,
        {
          body: {
            ...subscriptionPlanCreateBody, // use the same code to induce duplication error
          },
        },
      );
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { ITelegramFileDownloaderAdministrator } from "@ORGANIZATION/PROJECT-api/lib/structures/ITelegramFileDownloaderAdministrator";
import type { ITelegramFileDownloaderSubscriptionPlans } from "@ORGANIZATION/PROJECT-api/lib/structures/ITelegramFileDownloaderSubscriptionPlans";

/**
 * End-to-end test for updating a subscription plan by an administrator.
 *
 * This test covers the full workflow:
 *
 * 1. Register an administrator user.
 * 2. Log in as the administrator to obtain authorization.
 * 3. Update a subscription plan with new properties including price, maximum
 *    files per day, maximum file size (MB), total storage (MB), status, and
 *    optionally code and name.
 *
 * Validation includes:
 *
 * - Confirming the update response reflects changes.
 * - Verifying correct error handling for invalid UUID updates (404).
 * - Verifying unauthorized access is rejected (401).
 *
 * All API calls use valid DTO according to the schema. JWT tokens are
 * handled automatically by the SDK.
 */
export async function test_api_subscription_plan_update_by_administrator(
  connection: api.IConnection,
) {
  // 1. Administrator registration
  const adminCreateBody = {
    email: `admin_${RandomGenerator.alphaNumeric(6)}@example.com`,
    password_hash: RandomGenerator.alphaNumeric(16),
  } satisfies ITelegramFileDownloaderAdministrator.ICreate;
  const adminAuthorized = await api.functional.auth.administrator.join(
    connection,
    {
      body: adminCreateBody,
    },
  );
  typia.assert(adminAuthorized);

  // 2. Administrator login
  const adminLoginBody = {
    email: adminCreateBody.email,
    password: adminCreateBody.password_hash,
  } satisfies ITelegramFileDownloaderAdministrator.ILogin;
  const adminLoginAuthorized = await api.functional.auth.administrator.login(
    connection,
    {
      body: adminLoginBody,
    },
  );
  typia.assert(adminLoginAuthorized);

  // Helper function to create a realistic subscription plan update body
  function createSubscriptionPlanUpdateBody(): ITelegramFileDownloaderSubscriptionPlans.IUpdate {
    return {
      price: Math.floor(RandomGenerator.pick([999, 1999, 2999, 3999])),
      max_files_per_day: typia.random<number & tags.Type<"int32">>(),
      max_file_size_mb: typia.random<number & tags.Type<"int32">>(),
      total_storage_mb: typia.random<number & tags.Type<"int32">>(),
      status: RandomGenerator.pick([
        "active",
        "inactive",
        "deprecated",
      ] as const),
      code: `code_${RandomGenerator.alphaNumeric(6)}`,
      name: RandomGenerator.name(3),
    };
  }

  // 3. Update a subscription plan (using a fresh random UUID)
  const randomPlanId: string & tags.Format<"uuid"> = typia.random<
    string & tags.Format<"uuid">
  >();
  const updateBody = createSubscriptionPlanUpdateBody();
  const updatedPlan =
    await api.functional.telegramFileDownloader.administrator.subscription.plans.update(
      connection,
      {
        id: randomPlanId,
        body: updateBody,
      },
    );
  typia.assert(updatedPlan);

  // Validate updated properties
  TestValidator.equals(
    "updated price matches",
    updatedPlan.price,
    updateBody.price ?? updatedPlan.price,
  );
  TestValidator.equals(
    "updated max_files_per_day matches",
    updatedPlan.max_files_per_day,
    updateBody.max_files_per_day ?? updatedPlan.max_files_per_day,
  );
  TestValidator.equals(
    "updated max_file_size_mb matches",
    updatedPlan.max_file_size_mb,
    updateBody.max_file_size_mb ?? updatedPlan.max_file_size_mb,
  );
  TestValidator.equals(
    "updated total_storage_mb matches",
    updatedPlan.total_storage_mb,
    updateBody.total_storage_mb ?? updatedPlan.total_storage_mb,
  );
  TestValidator.equals(
    "updated status matches",
    updatedPlan.status,
    updateBody.status ?? updatedPlan.status,
  );
  if (updateBody.code !== undefined) {
    TestValidator.equals(
      "updated code matches",
      updatedPlan.code,
      updateBody.code,
    );
  }
  if (updateBody.name !== undefined) {
    TestValidator.equals(
      "updated name matches",
      updatedPlan.name,
      updateBody.name,
    );
  }

  // 4. Negative test: Update with invalid UUID (expect 404 Not Found error)
  await TestValidator.error(
    "update with invalid UUID should fail with 404",
    async () => {
      await api.functional.telegramFileDownloader.administrator.subscription.plans.update(
        connection,
        {
          id: "00000000-0000-0000-0000-000000000000",
          body: createSubscriptionPlanUpdateBody(),
        },
      );
    },
  );

  // 5. Negative test: Unauthorized update attempt (no authentication)
  // Create a new connection object without any authentication headers
  const unauthenticatedConnection: api.IConnection = {
    ...connection,
    headers: {},
  };
  await TestValidator.error(
    "unauthorized update attempts should fail with 401 error",
    async () => {
      await api.functional.telegramFileDownloader.administrator.subscription.plans.update(
        unauthenticatedConnection,
        {
          id: typia.random<string & tags.Format<"uuid">>(),
          body: createSubscriptionPlanUpdateBody(),
        },
      );
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { ITelegramFileDownloaderAdministrator } from "@ORGANIZATION/PROJECT-api/lib/structures/ITelegramFileDownloaderAdministrator";

/**
 * Test deletion of a subscription plan by an administrator user.
 *
 * This scenario covers:
 *
 * 1. Administrator registration via /auth/administrator/join with email and
 *    password_hash.
 * 2. Administrator login via /auth/administrator/login with email and
 *    password.
 * 3. [Optional] Creation of a subscription plan if required (not explicitly
 *    covered due to lack of create API).
 * 4. Deletion of a subscription plan by ID using DELETE
 *    /telegramFileDownloader/administrator/subscription/plans/{id}.
 * 5. Validation that deletion is successful and the plan no longer exists (by
 *    error expectation).
 * 6. Negative cases: deleting non-existent IDs should error, unauthorized
 *    deletion attempts should error with 401.
 *
 * The test enforces that only authenticated administrators can delete plans
 * and validates both happy and error flows.
 *
 * Steps:
 *
 * - Register administrator with random email and password_hash.
 * - Login with same credentials.
 * - Attempt to delete a subscription plan using a valid UUID. (In absence of
 *   create plan API, we simulate with the UUID used or any UUID.)
 * - Validate deletion completes without error.
 * - Attempt unauthorized deletion with empty headers/invalid session.
 * - Attempt deletion of non-existent subscription plan UUID, expect error.
 */
export async function test_api_subscription_plan_deletion_by_administrator(
  connection: api.IConnection,
) {
  // 1. Administrator registration
  const email = `${RandomGenerator.alphabets(8)}@example.com`;
  const passwordHash = RandomGenerator.alphaNumeric(32);
  const createBody = {
    email,
    password_hash: passwordHash,
  } satisfies ITelegramFileDownloaderAdministrator.ICreate;

  const authorized: ITelegramFileDownloaderAdministrator.IAuthorized =
    await api.functional.auth.administrator.join(connection, {
      body: createBody,
    });
  typia.assert(authorized);

  // 2. Administrator login
  const loginBody = {
    email,
    password: passwordHash,
  } satisfies ITelegramFileDownloaderAdministrator.ILogin;

  const loginAuthorized: ITelegramFileDownloaderAdministrator.IAuthorized =
    await api.functional.auth.administrator.login(connection, {
      body: loginBody,
    });
  typia.assert(loginAuthorized);

  // 3. Deletion of a subscription plan (using random valid UUID)
  const planId = typia.random<string & tags.Format<"uuid">>();

  await api.functional.telegramFileDownloader.administrator.subscription.plans.erase(
    connection,
    { id: planId },
  );

  // 4. Unauthorized deletion attempt (empty headers connection)
  const unauthConn: api.IConnection = { ...connection, headers: {} };
  await TestValidator.error("unauthorized deletion should throw", async () => {
    await api.functional.telegramFileDownloader.administrator.subscription.plans.erase(
      unauthConn,
      { id: planId },
    );
  });

  // 5. Attempt to delete non-existent plan
  const nonExistentId = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.error(
    "deleting non-existent plan should throw",
    async () => {
      await api.functional.telegramFileDownloader.administrator.subscription.plans.erase(
        connection,
        { id: nonExistentId },
      );
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { ITelegramFileDownloaderAdministrator } from "@ORGANIZATION/PROJECT-api/lib/structures/ITelegramFileDownloaderAdministrator";
