import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";
import type { IPageITelegramFileDownloaderAuditLog } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageITelegramFileDownloaderAuditLog";
import type { ITelegramFileDownloaderAdministrator } from "@ORGANIZATION/PROJECT-api/lib/structures/ITelegramFileDownloaderAdministrator";
import type { ITelegramFileDownloaderAuditLog } from "@ORGANIZATION/PROJECT-api/lib/structures/ITelegramFileDownloaderAuditLog";

/**
 * This end-to-end test function validates the audit log listing and filtering
 * functionality for administrators in the Telegram File Downloader system.
 *
 * The comprehensive test flow is as follows:
 *
 * 1. Create an administrator account using /auth/administrator/join with valid
 *    email and password hash.
 * 2. Authenticate the administrator via /auth/administrator/login to obtain JWT
 *    tokens for authorized requests.
 * 3. Use the authenticated administrator context to request a paginated list of
 *    audit logs with filters on action types, entity types, user IDs, or date
 *    ranges using PATCH /telegramFileDownloader/administrator/auditLogs.
 * 4. Validate that the response contains a paginated list matching the filters
 *    with correct schema, timestamps, and data integrity while not exposing
 *    sensitive info.
 * 5. Test edge case with filters returning no results, confirming empty data array
 *    and valid pagination metadata.
 * 6. Verify unauthorized access results in appropriate HTTP 401 or 403 errors when
 *    attempting the same audit log request without authentication or
 *    insufficient role.
 *
 * All requests use valid schema-defined DTOs and respect format, required
 * fields, and value constraints. The test asserts all response types with
 * typia.assert() for thorough validation. Authentication tokens are managed
 * automatically by API SDK on each login/join. No direct manipulation of
 * headers is performed.
 *
 * This test ensures audit log data is securely accessible, properly filtered,
 * and paginated only for authorized administrators, while gracefully handling
 * error and edge cases.
 */
export async function test_api_audit_log_list_and_filter_paginated(
  connection: api.IConnection,
) {
  // 1. Create admin account
  const adminEmail = `admin+${RandomGenerator.alphaNumeric(6)}@example.com`;
  const adminPassword = RandomGenerator.alphaNumeric(16);
  const adminPasswordHash = RandomGenerator.alphaNumeric(32);

  const admin: ITelegramFileDownloaderAdministrator.IAuthorized =
    await api.functional.auth.administrator.join(connection, {
      body: {
        email: adminEmail,
        password_hash: adminPasswordHash,
      },
    });
  typia.assert(admin);

  // 2. Authenticate admin (login) with correct plaintext password
  const loggedInAdmin: ITelegramFileDownloaderAdministrator.IAuthorized =
    await api.functional.auth.administrator.login(connection, {
      body: {
        email: adminEmail,
        password: adminPassword,
      },
    });
  typia.assert(loggedInAdmin);

  // 3. Request audit logs with filters
  const filterBody: ITelegramFileDownloaderAuditLog.IRequest = {
    error_code: null,
    resolved: null,
    occurred_at_start: "2000-01-01T00:00:00.000Z" satisfies string &
      tags.Format<"date-time">,
    occurred_at_end: new Date().toISOString() satisfies string &
      tags.Format<"date-time">,
    page: 1,
    limit: 10,
    sort_by: "occurred_at",
    sort_order: "desc",
  };

  const auditPage: IPageITelegramFileDownloaderAuditLog =
    await api.functional.telegramFileDownloader.administrator.auditLogs.index(
      connection,
      {
        body: filterBody,
      },
    );
  typia.assert(auditPage);

  // Validate pagination details
  TestValidator.equals(
    "pagination current page equals 1",
    auditPage.pagination.current,
    1,
  );
  TestValidator.equals(
    "pagination limit equals 10",
    auditPage.pagination.limit,
    10,
  );
  TestValidator.predicate(
    "pagination pages non-negative",
    auditPage.pagination.pages >= 0,
  );
  TestValidator.predicate(
    "pagination records non-negative",
    auditPage.pagination.records >= 0,
  );

  // Validate audit log entries
  for (const entry of auditPage.data) {
    typia.assert(entry);
    TestValidator.predicate(
      "each audit log has uuid id",
      typeof entry.id === "string" && entry.id.length > 0,
    );
    TestValidator.predicate(
      "audit log has error_code",
      typeof entry.error_code === "string" && entry.error_code.length > 0,
    );
    TestValidator.predicate(
      "audit log has occurred_at ISO date",
      typeof entry.occurred_at === "string",
    );
  }

  // 4. Test edge case: filters that return no logs
  const emptyFilterBody: ITelegramFileDownloaderAuditLog.IRequest = {
    error_code: "nonexistent-error-code",
    resolved: false,
    occurred_at_start: "2100-01-01T00:00:00.000Z" satisfies string &
      tags.Format<"date-time">,
    occurred_at_end: "2100-12-31T23:59:59.999Z" satisfies string &
      tags.Format<"date-time">,
    page: 1,
    limit: 5,
    sort_by: "occurred_at",
    sort_order: "asc",
  };

  const emptyPage: IPageITelegramFileDownloaderAuditLog =
    await api.functional.telegramFileDownloader.administrator.auditLogs.index(
      connection,
      {
        body: emptyFilterBody,
      },
    );
  typia.assert(emptyPage);

  TestValidator.equals(
    "empty page has zero records",
    emptyPage.pagination.records,
    0,
  );
  TestValidator.equals(
    "empty page has zero data items",
    emptyPage.data.length,
    0,
  );

  // 5. Test unauthorized access - without login
  const unauthConnection: api.IConnection = { ...connection, headers: {} };
  await TestValidator.error("unauthorized access without login", async () => {
    await api.functional.telegramFileDownloader.administrator.auditLogs.index(
      unauthConnection,
      {
        body: filterBody,
      },
    );
  });
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { ITelegramFileDownloaderAdministrator } from "@ORGANIZATION/PROJECT-api/lib/structures/ITelegramFileDownloaderAdministrator";
import type { ITelegramFileDownloaderAuditLog } from "@ORGANIZATION/PROJECT-api/lib/structures/ITelegramFileDownloaderAuditLog";

/**
 * End-to-end test validating administrator authentication and audit log
 * detail retrieval.
 *
 * This test executes a comprehensive scenario where an administrator is
 * registered and logged in via authentication endpoints before successfully
 * retrieving an audit log detail by its unique ID. It asserts the
 * correctness of authentication tokens, audit log data integrity, and error
 * responses on invalid audit log IDs or unauthorized access.
 *
 * The test covers:
 *
 * 1. Administrator account creation with valid email and a hashed password.
 * 2. Administrator login using email and plaintext password to obtain JWT
 *    token.
 * 3. Successful retrieval of audit log details given a valid UUID.
 * 4. Validation that the audit log response matches the expected DTO schema.
 * 5. Negative test case for requesting a non-existent audit log ID expecting
 *    404 error.
 * 6. Negative test case for access without authentication expecting 401/403
 *    error.
 *
 * The API client internally handles JWT token management; test code does
 * not modify headers.
 *
 * The test ensures that only authorized administrators can access audit log
 * details, validating security constraints and response correctness.
 */
export async function test_api_audit_log_detail_retrieval_authorized(
  connection: api.IConnection,
) {
  // 1. Recreate admin account with known password hash for consistent login
  const fixedPassword = "test-password-1234";
  const adminCreateBody = {
    email:
      RandomGenerator.name(1) +
      `-${typia.random<string & tags.Format<"uuid">>().substring(0, 8)}@example.com`,
    password_hash: fixedPassword,
  } satisfies ITelegramFileDownloaderAdministrator.ICreate;

  const adminAuthorized = await api.functional.auth.administrator.join(
    connection,
    { body: adminCreateBody },
  );
  typia.assert(adminAuthorized);

  // 2. Login as the administrator with the fixed plaintext password
  const adminLoginBody = {
    email: adminCreateBody.email,
    password: fixedPassword,
  } satisfies ITelegramFileDownloaderAdministrator.ILogin;

  const adminLoginAuthorized = await api.functional.auth.administrator.login(
    connection,
    { body: adminLoginBody },
  );
  typia.assert(adminLoginAuthorized);

  // 3. Use valid audit log ID
  // Positive test: fetch a valid audit log by simulating one
  const validAuditLog: ITelegramFileDownloaderAuditLog =
    typia.random<ITelegramFileDownloaderAuditLog>();
  typia.assert(validAuditLog);

  // Fetch audit log detail successfully with valid id
  const auditLog =
    await api.functional.telegramFileDownloader.administrator.auditLogs.at(
      connection,
      {
        id: validAuditLog.id,
      },
    );
  typia.assert(auditLog);

  // Validate key properties of the audit log
  TestValidator.equals("audit log id matches", auditLog.id, validAuditLog.id);
  TestValidator.predicate(
    "audit log error_code is non-empty",
    auditLog.error_code.length > 0,
  );
  TestValidator.predicate(
    "audit log error_message is non-empty",
    auditLog.error_message.length > 0,
  );
  TestValidator.predicate(
    "audit log source_component is non-empty",
    auditLog.source_component.length > 0,
  );
  TestValidator.predicate(
    "audit log occurred_at is valid datetime",
    typeof auditLog.occurred_at === "string",
  );
  TestValidator.predicate(
    "audit log resolved flag is boolean",
    typeof auditLog.resolved === "boolean",
  );
  TestValidator.predicate(
    "audit log created_at is valid datetime",
    typeof auditLog.created_at === "string",
  );
  TestValidator.predicate(
    "audit log updated_at is valid datetime",
    typeof auditLog.updated_at === "string",
  );
  // deleted_at can be null or string or undefined (optional)

  // 4. Negative test: Attempt to retrieve audit log with non-existent UUID
  const invalidId = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.error(
    "fetch audit log with non-existent id should fail",
    async () => {
      await api.functional.telegramFileDownloader.administrator.auditLogs.at(
        connection,
        {
          id: invalidId,
        },
      );
    },
  );

  // 5. Negative test: Attempt to retrieve audit log without authentication
  // For this, create a new connection with empty headers to simulate unauthenticated request
  const unauthenticatedConnection: api.IConnection = {
    ...connection,
    headers: {},
  };
  await TestValidator.error(
    "unauthorized audit log access should fail",
    async () => {
      await api.functional.telegramFileDownloader.administrator.auditLogs.at(
        unauthenticatedConnection,
        {
          id: validAuditLog.id,
        },
      );
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";
import type { IPageITelegramFileDownloaderBillingLog } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageITelegramFileDownloaderBillingLog";
import type { ITelegramFileDownloaderAdministrator } from "@ORGANIZATION/PROJECT-api/lib/structures/ITelegramFileDownloaderAdministrator";
import type { ITelegramFileDownloaderBillingLog } from "@ORGANIZATION/PROJECT-api/lib/structures/ITelegramFileDownloaderBillingLog";

/**
 * Validate administrative billing log listing with filtering and
 * pagination.
 *
 * This test registers an administrator, logs in, then performs various
 * queries on the billing logs resource with accurate filtering, pagination,
 * and authorization.
 *
 * Business steps:
 *
 * 1. Create administrator user by joining with random email and password hash.
 * 2. Login administrator user with same credentials.
 * 3. Query billing logs with valid filters including payment ID, event type,
 *    time range, page and limit.
 * 4. Validate that results contain expected pagination details and billing
 *    logs consistent with filters.
 * 5. Query with non-existent payment ID and validate empty response handled
 *    properly.
 * 6. Attempt querying billing logs without valid JWT authorization token and
 *    expect a failure.
 */
export async function test_api_billing_log_list_and_filter_paginated(
  connection: api.IConnection,
): Promise<void> {
  // Step 1: Administrator account creation
  const adminEmail: string = `${RandomGenerator.alphaNumeric(8)}@example.com`;
  const adminPasswordHash: string = RandomGenerator.alphaNumeric(32);

  const adminCreated: ITelegramFileDownloaderAdministrator.IAuthorized =
    await api.functional.auth.administrator.join(connection, {
      body: {
        email: adminEmail,
        password_hash: adminPasswordHash,
      } satisfies ITelegramFileDownloaderAdministrator.ICreate,
    });
  typia.assert(adminCreated);

  // Step 2: Administrator login
  const adminLoggedIn: ITelegramFileDownloaderAdministrator.IAuthorized =
    await api.functional.auth.administrator.login(connection, {
      body: {
        email: adminEmail,
        password: adminPasswordHash,
      } satisfies ITelegramFileDownloaderAdministrator.ILogin,
    });
  typia.assert(adminLoggedIn);

  // Step 3: Query billing logs with valid filters
  const validFilterBody: ITelegramFileDownloaderBillingLog.IRequest = {
    telegram_file_downloader_payment_id: adminCreated.id,
    event_type: "payment_success",
    event_timestamp_start: new Date(
      new Date().getTime() - 7 * 24 * 3600 * 1000,
    ).toISOString(),
    event_timestamp_end: new Date().toISOString(),
    page: 1,
    limit: 10,
    sort_by: "event_timestamp",
    sort_order: "desc",
  };
  const paginatedLogs: IPageITelegramFileDownloaderBillingLog =
    await api.functional.telegramFileDownloader.administrator.billingLogs.index(
      connection,
      { body: validFilterBody },
    );
  typia.assert(paginatedLogs);

  TestValidator.predicate(
    "pagination current page is correct",
    paginatedLogs.pagination.current === validFilterBody.page,
  );

  TestValidator.predicate(
    "pagination page limit is correct",
    paginatedLogs.pagination.limit === validFilterBody.limit,
  );

  TestValidator.predicate(
    "all returned logs match payment id filter",
    paginatedLogs.data.every(
      (log) =>
        log.telegram_file_downloader_payment_id ===
        validFilterBody.telegram_file_downloader_payment_id,
    ),
  );

  TestValidator.predicate(
    "all returned logs match event type filter",
    paginatedLogs.data.every(
      (log) => log.event_type === validFilterBody.event_type,
    ),
  );

  // Step 4: Query billing logs with non-existent payment ID
  const nonExistentPaymentId = typia.random<string & tags.Format<"uuid">>();
  const emptyResult: IPageITelegramFileDownloaderBillingLog =
    await api.functional.telegramFileDownloader.administrator.billingLogs.index(
      connection,
      {
        body: {
          telegram_file_downloader_payment_id: nonExistentPaymentId,
          page: 1,
          limit: 10,
        } satisfies ITelegramFileDownloaderBillingLog.IRequest,
      },
    );
  typia.assert(emptyResult);

  TestValidator.equals(
    "empty data for non-existent payment ID",
    emptyResult.data.length,
    0,
  );

  // Step 5: Attempt unauthorized request
  const unauthorizedConnection: api.IConnection = {
    ...connection,
    headers: {},
  };
  await TestValidator.error("unauthorized request should fail", async () => {
    await api.functional.telegramFileDownloader.administrator.billingLogs.index(
      unauthorizedConnection,
      { body: validFilterBody },
    );
  });
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
