import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { ITelegramFileDownloaderAdministrator } from "@ORGANIZATION/PROJECT-api/lib/structures/ITelegramFileDownloaderAdministrator";
import type { ITelegramFileDownloaderAdministrators } from "@ORGANIZATION/PROJECT-api/lib/structures/ITelegramFileDownloaderAdministrators";

/**
 * End-to-end test for the administrator update API in
 * telegramFileDownloader.
 *
 * This test covers creating and authenticating administrator users,
 * updating an administrator's email and password hash, and validating both
 * successful updates and failure scenarios:
 *
 * - Duplicate email error (expects HTTP 409 Conflict)
 * - Updating non-existent administrator (expects HTTP 404 Not Found)
 *
 * The scenario ensures that all authentication tokens and credentials are
 * handled securely and updates persist correctly.
 *
 * Workflow:
 *
 * 1. Register initial administrator
 * 2. Authenticate initial administrator
 * 3. Register second administrator for conflict testing
 * 4. Update initial administrator with new email and password hash
 * 5. Confirm updated administrator details
 * 6. Authenticate updated administrator
 * 7. Test duplicate email error
 * 8. Test updating non-existent administrator error
 */
export async function test_api_administrator_update_success(
  connection: api.IConnection,
) {
  // Step 1: Register initial administrator
  const initialEmail = `${RandomGenerator.alphaNumeric(6)}@example.com`;
  const initialPasswordHash = RandomGenerator.alphaNumeric(32);
  const initialAdmin = await api.functional.auth.administrator.join(
    connection,
    {
      body: {
        email: initialEmail,
        password_hash: initialPasswordHash,
      } satisfies ITelegramFileDownloaderAdministrator.ICreate,
    },
  );
  typia.assert(initialAdmin);

  // Step 2: Authenticate initial administrator
  const authInitialAdmin = await api.functional.auth.administrator.login(
    connection,
    {
      body: {
        email: initialEmail,
        password: initialPasswordHash,
      } satisfies ITelegramFileDownloaderAdministrator.ILogin,
    },
  );
  typia.assert(authInitialAdmin);
  TestValidator.equals(
    "initial admin id matches",
    authInitialAdmin.id,
    initialAdmin.id,
  );
  TestValidator.equals(
    "initial admin email matches",
    authInitialAdmin.email,
    initialEmail,
  );

  // Step 3: Register second administrator to use for conflict testing
  const secondEmail = `${RandomGenerator.alphaNumeric(6)}@example.com`;
  const secondPasswordHash = RandomGenerator.alphaNumeric(32);
  const secondAdmin = await api.functional.auth.administrator.join(connection, {
    body: {
      email: secondEmail,
      password_hash: secondPasswordHash,
    } satisfies ITelegramFileDownloaderAdministrator.ICreate,
  });
  typia.assert(secondAdmin);

  // Step 4: Update initial administrator with new email and password hash
  const updatedEmail = `${RandomGenerator.alphaNumeric(6)}@example.org`;
  const updatedPasswordHash = RandomGenerator.alphaNumeric(40);
  const updatedAdmin =
    await api.functional.telegramFileDownloader.administrator.administrators.update(
      connection,
      {
        administratorId: initialAdmin.id,
        body: {
          email: updatedEmail,
          password_hash: updatedPasswordHash,
        } satisfies ITelegramFileDownloaderAdministrators.IUpdate,
      },
    );
  typia.assert(updatedAdmin);

  // Validate updated values
  TestValidator.equals(
    "updated admin id matches original",
    updatedAdmin.id,
    initialAdmin.id,
  );
  TestValidator.equals(
    "updated admin email matches new email",
    updatedAdmin.email,
    updatedEmail,
  );
  TestValidator.notEquals(
    "updated password_hash differs from original",
    updatedAdmin.password_hash,
    initialAdmin.password_hash,
  );

  // Step 5: Authenticate updated administrator with new credentials
  const authUpdatedAdmin = await api.functional.auth.administrator.login(
    connection,
    {
      body: {
        email: updatedEmail,
        password: updatedPasswordHash,
      } satisfies ITelegramFileDownloaderAdministrator.ILogin,
    },
  );
  typia.assert(authUpdatedAdmin);
  TestValidator.equals(
    "auth updated admin id matches",
    authUpdatedAdmin.id,
    initialAdmin.id,
  );
  TestValidator.equals(
    "auth updated admin email matches",
    authUpdatedAdmin.email,
    updatedEmail,
  );

  // Step 6: Test updating with duplicate email from second administrator (should fail with 409)
  await TestValidator.error(
    "update with duplicate email should fail",
    async () => {
      await api.functional.telegramFileDownloader.administrator.administrators.update(
        connection,
        {
          administratorId: initialAdmin.id,
          body: {
            email: secondEmail, // duplicate
          } satisfies ITelegramFileDownloaderAdministrators.IUpdate,
        },
      );
    },
  );

  // Step 7: Test updating non-existent administrator (should fail with 404)
  // Generate fake UUID for testing
  const fakeId = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.error(
    "update non-existent administrator should fail",
    async () => {
      await api.functional.telegramFileDownloader.administrator.administrators.update(
        connection,
        {
          administratorId: fakeId,
          body: {
            email: `${RandomGenerator.alphaNumeric(6)}@example.com`,
          } satisfies ITelegramFileDownloaderAdministrators.IUpdate,
        },
      );
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { ITelegramFileDownloaderAdministrator } from "@ORGANIZATION/PROJECT-api/lib/structures/ITelegramFileDownloaderAdministrator";

/**
 * Validate the successful deletion of an administrator user in the Telegram
 * File Downloader system.
 *
 * This test performs the full user lifecycle by:
 *
 * 1. Creating a new administrator account through POST
 *    /auth/administrator/join with valid email and password hash.
 * 2. Logging in as the newly created administrator to establish authentication
 *    context.
 * 3. Deleting the administrator via DELETE
 *    /telegramFileDownloader/administrator/administrators/{administratorId},
 *    expecting HTTP 204 no content response.
 * 4. Verifying the deletion by asserting the administrator cannot log in
 *    anymore, expecting an error.
 *
 * It ensures that only authorized administrators can delete users and
 * verifies the system properly removes the administrator record. Business
 * rules such as status codes and authentication are rigorously validated.
 * All DTO properties are used exactly as specified, including required
 * email, password_hash fields for creation and login, and UUID formatted
 * administratorId for deletion.
 */
export async function test_api_administrator_delete_success(
  connection: api.IConnection,
) {
  // 1. Register a new administrator user with join API
  const createBody = {
    email: RandomGenerator.alphaNumeric(6) + "@example.com",
    password_hash: RandomGenerator.alphaNumeric(32),
  } satisfies ITelegramFileDownloaderAdministrator.ICreate;

  const administrator: ITelegramFileDownloaderAdministrator.IAuthorized =
    await api.functional.auth.administrator.join(connection, {
      body: createBody,
    });
  typia.assert(administrator);

  // 2. Login as this administrator to authenticate
  const loginBody = {
    email: createBody.email,
    password: createBody.password_hash,
  } satisfies ITelegramFileDownloaderAdministrator.ILogin;

  const login = await api.functional.auth.administrator.login(connection, {
    body: loginBody,
  });
  typia.assert(login);

  // Confirm that the login id matches the created administrator id
  TestValidator.equals(
    "administrator id should match after login",
    login.id,
    administrator.id,
  );

  // 3. Delete the administrator using erase API
  await api.functional.telegramFileDownloader.administrator.administrators.erase(
    connection,
    {
      administratorId: administrator.id,
    },
  );

  // 4. Verify deletion by attempting to login again and expect an error
  await TestValidator.error("login after deletion should fail", async () => {
    await api.functional.auth.administrator.login(connection, {
      body: loginBody,
    });
  });
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { ITelegramFileDownloaderDownloadJob } from "@ORGANIZATION/PROJECT-api/lib/structures/ITelegramFileDownloaderDownloadJob";
import type { ITelegramFileDownloaderEndUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITelegramFileDownloaderEndUser";

export async function test_api_enduser_download_job_retrieval_success(
  connection: api.IConnection,
) {
  // 1. Register a new endUser
  const email = typia.random<string & tags.Format<"email">>();
  const password = RandomGenerator.alphaNumeric(12);
  const createBody = {
    email,
    password_hash: password,
  } satisfies ITelegramFileDownloaderEndUser.ICreate;
  const authorized: ITelegramFileDownloaderEndUser.IAuthorized =
    await api.functional.auth.endUser.join(connection, { body: createBody });
  typia.assert(authorized);

  // 2. Login with the created endUser
  const loginBody = {
    email,
    password,
  } satisfies ITelegramFileDownloaderEndUser.ILogin;
  const loggedIn: ITelegramFileDownloaderEndUser.IAuthorized =
    await api.functional.auth.endUser.login(connection, { body: loginBody });
  typia.assert(loggedIn);

  // 3. Create a new download job
  const fileTypesValue = ["mp4", "zip", "pdf", "jpg", "png"] as const;
  const uniqueFileTypes = ArrayUtil.repeat(3, () =>
    RandomGenerator.pick(fileTypesValue),
  );
  // Remove duplicates
  const fileTypesSet = new Set(uniqueFileTypes);
  const selectedFileTypes = Array.from(fileTypesSet).join(",");

  const now = new Date();
  const dateStart = new Date(now.getTime() - 86400000 * 7).toISOString(); // 7 days ago
  const dateEnd = now.toISOString();

  const createJobBody = {
    channel_id: RandomGenerator.alphaNumeric(16),
    file_types: selectedFileTypes,
    date_start: dateStart,
    date_end: dateEnd,
  } satisfies ITelegramFileDownloaderDownloadJob.ICreate;
  const createdJob: ITelegramFileDownloaderDownloadJob =
    await api.functional.telegramFileDownloader.endUser.download_jobs.create(
      connection,
      { body: createJobBody },
    );
  typia.assert(createdJob);

  // 4. Retrieve the created download job by its ID
  const retrievedJob: ITelegramFileDownloaderDownloadJob =
    await api.functional.telegramFileDownloader.endUser.download_jobs.at(
      connection,
      { id: createdJob.id },
    );
  typia.assert(retrievedJob);

  // 5. Validate download job fields
  TestValidator.equals(
    "retrieved job id matches created job id",
    retrievedJob.id,
    createdJob.id,
  );
  TestValidator.equals(
    "retrieved job channel id matches",
    retrievedJob.channel_id,
    createdJob.channel_id,
  );
  TestValidator.equals(
    "retrieved job file types match",
    retrievedJob.file_types,
    createdJob.file_types,
  );
  TestValidator.equals(
    "retrieved job date_start matches",
    retrievedJob.date_start,
    createdJob.date_start,
  );
  TestValidator.equals(
    "retrieved job date_end matches",
    retrievedJob.date_end,
    createdJob.date_end,
  );
  TestValidator.predicate(
    "retrieved job enduser_id exists (not null or undefined)",
    retrievedJob.enduser_id !== null && retrievedJob.enduser_id !== undefined,
  );
  TestValidator.predicate(
    "retrieved job status is of type string",
    typeof retrievedJob.status === "string",
  );
  TestValidator.predicate(
    "retrieved job created_at is valid ISO date",
    !isNaN(Date.parse(retrievedJob.created_at)),
  );
  TestValidator.predicate(
    "retrieved job updated_at is valid ISO date",
    !isNaN(Date.parse(retrievedJob.updated_at)),
  );

  // 6. Test retrieval with non-existent UUID
  await TestValidator.error("non-existent UUID retrieval fails", async () => {
    await api.functional.telegramFileDownloader.endUser.download_jobs.at(
      connection,
      { id: typia.random<string & tags.Format<"uuid">>() },
    );
  });

  // 7. Authentication as different endUser for unauthorized retrieval
  const otherEmail = typia.random<string & tags.Format<"email">>();
  const otherPassword = RandomGenerator.alphaNumeric(12);
  const otherCreateBody = {
    email: otherEmail,
    password_hash: otherPassword,
  } satisfies ITelegramFileDownloaderEndUser.ICreate;
  const otherAuthorized: ITelegramFileDownloaderEndUser.IAuthorized =
    await api.functional.auth.endUser.join(connection, {
      body: otherCreateBody,
    });
  typia.assert(otherAuthorized);

  const otherLoginBody = {
    email: otherEmail,
    password: otherPassword,
  } satisfies ITelegramFileDownloaderEndUser.ILogin;
  const otherLoggedIn: ITelegramFileDownloaderEndUser.IAuthorized =
    await api.functional.auth.endUser.login(connection, {
      body: otherLoginBody,
    });
  typia.assert(otherLoggedIn);

  await TestValidator.error(
    "unauthorized user cannot retrieve other's download job",
    async () => {
      await api.functional.telegramFileDownloader.endUser.download_jobs.at(
        connection,
        { id: createdJob.id },
      );
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";
import type { IPageITelegramFileDownloaderStorageUsage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageITelegramFileDownloaderStorageUsage";
import type { ITelegramFileDownloaderAdministrator } from "@ORGANIZATION/PROJECT-api/lib/structures/ITelegramFileDownloaderAdministrator";
import type { ITelegramFileDownloaderStorageUsage } from "@ORGANIZATION/PROJECT-api/lib/structures/ITelegramFileDownloaderStorageUsage";

/**
 * Verify administrator filtered storage usage pagination.
 *
 * This test covers the administrator user registration and authentication,
 * followed by querying storage usage records with filters for enduser and
 * developer IDs. It confirms valid results, pagination metadata, and error
 * responses for unauthorized and invalid inputs.
 *
 * Workflow:
 *
 * 1. Register new administrator account for authentication.
 * 2. Confirm administrator account authenticated with JWT token.
 * 3. Use authenticated connection to fetch filtered storage usages with
 *    enduser_id and developer_id pagination.
 * 4. Validate each returned record matches filters and pagination metadata is
 *    correct.
 * 5. Test unauthorized access to storage usage endpoint yields error.
 * 6. Test invalid filter UUIDs cause validation errors.
 */
export async function test_api_storage_usage_filtered_pagination_with_admin_authentication(
  connection: api.IConnection,
) {
  // 1. Administrator signup and authentication
  const adminCreateBody = {
    email: `admin_${RandomGenerator.alphaNumeric(8)}@example.com`,
    // Note password_hash must be a hashed password, so use a non-empty string
    password_hash: "hashed_password_123456",
  } satisfies ITelegramFileDownloaderAdministrator.ICreate;

  const adminAuthorized: ITelegramFileDownloaderAdministrator.IAuthorized =
    await api.functional.auth.administrator.join(connection, {
      body: adminCreateBody,
    });
  typia.assert(adminAuthorized);

  // 2. Prepare filter criteria using valid UUIDs from authorized admin (simulate realistic values)
  // For demonstrative purpose, use adminAuthorized.id for both filters
  const filterParams = {
    enduser_id: adminAuthorized.id,
    developer_id: adminAuthorized.id,
    page: 1,
    limit: 10,
    orderBy: "id",
    orderDirection: "asc",
  } satisfies ITelegramFileDownloaderStorageUsage.IRequest;

  // 3. Fetch storage usage records with filtering and pagination
  const storageUsagePage: IPageITelegramFileDownloaderStorageUsage.ISummary =
    await api.functional.telegramFileDownloader.administrator.storage_usages.index(
      connection,
      { body: filterParams },
    );
  typia.assert(storageUsagePage);

  // 4. Validate pagination metadata correctness
  const pagination = storageUsagePage.pagination;
  TestValidator.predicate(
    "pagination current page is correct",
    pagination.current === 1,
  );
  TestValidator.predicate(
    "pagination limit is correct",
    pagination.limit === 10,
  );
  TestValidator.predicate(
    "pagination pages is non-negative",
    pagination.pages >= 0,
  );
  TestValidator.predicate(
    "pagination records is non-negative",
    pagination.records >= 0,
  );

  // 5. Validate that all returned storage usages are valid
  for (const record of storageUsagePage.data) {
    typia.assert(record);
    TestValidator.predicate(
      "storage usage record id exists",
      typeof record.id === "string" && record.id.length > 0,
    );
    TestValidator.predicate(
      "storage bytes used non-negative",
      record.storage_bytes_used >= 0,
    );
    TestValidator.predicate("file count non-negative", record.file_count >= 0);
    TestValidator.predicate(
      "quota max bytes non-negative",
      record.quota_max_bytes >= 0,
    );
  }

  // 6. Verify unauthorized access fails
  const unauthorizedConn: api.IConnection = { ...connection, headers: {} };
  await TestValidator.error("unauthorized access throws error", async () => {
    await api.functional.telegramFileDownloader.administrator.storage_usages.index(
      unauthorizedConn,
      { body: filterParams },
    );
  });

  // 7. Verify API validation errors when invalid UUIDs are provided
  const invalidUuidParams1 = {
    enduser_id: "invalid-uuid-string",
    developer_id: adminAuthorized.id,
    page: 1,
    limit: 5,
    orderBy: null,
    orderDirection: null,
  } satisfies ITelegramFileDownloaderStorageUsage.IRequest;

  await TestValidator.error(
    "invalid enduser_id uuid throws validation error",
    async () => {
      await api.functional.telegramFileDownloader.administrator.storage_usages.index(
        connection,
        { body: invalidUuidParams1 },
      );
    },
  );

  const invalidUuidParams2 = {
    enduser_id: adminAuthorized.id,
    developer_id: "not-a-valid-uuid",
    page: 1,
    limit: 5,
    orderBy: "id",
    orderDirection: "asc",
  } satisfies ITelegramFileDownloaderStorageUsage.IRequest;

  await TestValidator.error(
    "invalid developer_id uuid throws validation error",
    async () => {
      await api.functional.telegramFileDownloader.administrator.storage_usages.index(
        connection,
        { body: invalidUuidParams2 },
      );
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { ITelegramFileDownloaderAdministrator } from "@ORGANIZATION/PROJECT-api/lib/structures/ITelegramFileDownloaderAdministrator";
import type { ITelegramFileDownloaderStorageUsage } from "@ORGANIZATION/PROJECT-api/lib/structures/ITelegramFileDownloaderStorageUsage";

export async function test_api_storage_usage_retrieve_detail_with_admin_authentication(
  connection: api.IConnection,
) {
  // Step 1: Administrator account creation and authentication
  const adminEmail = typia.random<string & tags.Format<"email">>();
  const adminPasswordHash = RandomGenerator.alphaNumeric(64); // Simulated secure hash

  const admin: ITelegramFileDownloaderAdministrator.IAuthorized =
    await api.functional.auth.administrator.join(connection, {
      body: {
        email: adminEmail,
        password_hash: adminPasswordHash,
      } satisfies ITelegramFileDownloaderAdministrator.ICreate,
    });
  typia.assert(admin);

  // Step 2: Create a valid UUID and retrieve storage usage detail
  const validStorageUsageId = typia.random<string & tags.Format<"uuid">>();
  const storageUsage: ITelegramFileDownloaderStorageUsage =
    await api.functional.telegramFileDownloader.administrator.storage_usages.at(
      connection,
      { id: validStorageUsageId },
    );
  typia.assert(storageUsage);

  TestValidator.predicate(
    "storage usage id matches requested id",
    storageUsage.id === validStorageUsageId,
  );

  TestValidator.predicate(
    "storage usage storage_bytes_used is non-negative",
    storageUsage.storage_bytes_used >= 0,
  );

  TestValidator.predicate(
    "storage usage file_count is non-negative",
    storageUsage.file_count >= 0,
  );

  TestValidator.predicate(
    "storage usage quota_max_bytes is positive",
    storageUsage.quota_max_bytes > 0,
  );

  TestValidator.predicate(
    "storage usage enduser_id is uuid",
    typia.is<string & tags.Format<"uuid">>(storageUsage.enduser_id),
  );

  // developer_id can be null or uuid
  if (
    storageUsage.developer_id !== null &&
    storageUsage.developer_id !== undefined
  ) {
    TestValidator.predicate(
      "storage usage developer_id is uuid",
      typia.is<string & tags.Format<"uuid">>(storageUsage.developer_id),
    );
  }

  // Step 3: Unauthorized access attempt
  const unauthenticatedConnection: api.IConnection = {
    ...connection,
    headers: {},
  };
  await TestValidator.error(
    "unauthorized access to storage usage detail should fail",
    async () => {
      await api.functional.telegramFileDownloader.administrator.storage_usages.at(
        unauthenticatedConnection,
        { id: validStorageUsageId },
      );
    },
  );

  // Step 4: Non-existent UUID should result in error (likely 404)
  const nonExistentId = "00000000-0000-0000-0000-000000000000" as string &
    tags.Format<"uuid">;
  await TestValidator.error(
    "non-existent UUID should fail with error",
    async () => {
      await api.functional.telegramFileDownloader.administrator.storage_usages.at(
        connection,
        { id: nonExistentId },
      );
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { ITelegramFileDownloaderAdministrator } from "@ORGANIZATION/PROJECT-api/lib/structures/ITelegramFileDownloaderAdministrator";
import type { ITelegramFileDownloaderStorageUsage } from "@ORGANIZATION/PROJECT-api/lib/structures/ITelegramFileDownloaderStorageUsage";

/**
 * Test scenario to verify that an administrator can create new storage usage
 * records with proper authorization and validation.
 *
 * This test performs the following steps:
 *
 * 1. Administrator user account is created and authenticated via the join
 *    endpoint. This returns an authorized administrator with valid JWT token.
 * 2. Using the administrator authorization, a storage usage creation request is
 *    sent with proper payload containing required fields:
 *
 *    - Enduser_id (valid uuid)
 *    - Developer_id (optional valid uuid or null)
 *    - Storage_bytes_used (valid int32 number)
 *    - File_count (valid int32 number)
 *    - Quota_max_bytes (valid int32 number)
 * 3. Validate the response is a storage usage record matching the sent data with
 *    proper fields.
 *
 * Additional tests include:
 *
 * - Attempting to create with invalid UUID formats for enduser_id or developer_id
 *   and expecting errors.
 * - Attempting to create without administrator authentication and expecting
 *   authorization error.
 *
 * The validation points include:
 *
 * - Authorization enforcement: only administrators can create storage usage
 *   records.
 * - Required fields presence and valid UUID format for identifiers.
 * - Integer storage values within valid int32 range.
 * - Proper handling of uniqueness and validation error cases.
 *
 * Business logic:
 *
 * - Only administrator role can create storage usage.
 * - UUIDs must be valid format.
 *
 * Success criteria:
 *
 * - Administrator authentication succeeds.
 * - Storage usage creation succeeds with accurate data.
 * - Invalid or unauthorized operations fail correctly.
 *
 * Error handling:
 *
 * - Invalid UUIDs or missing mandatory properties lead to validation errors.
 * - Unauthorized access leads to auth error.
 *
 * This test demonstrates end-to-end secure storage usage creation with admin
 * role.
 */
export async function test_api_storage_usage_creation_with_admin_authentication(
  connection: api.IConnection,
) {
  // 1. Administrator joins and authenticates
  const adminEmail: string = typia.random<string & tags.Format<"email">>();
  const administrator: ITelegramFileDownloaderAdministrator.IAuthorized =
    await api.functional.auth.administrator.join(connection, {
      body: {
        email: adminEmail,
        password_hash: "hashed_password_example123!",
      } satisfies ITelegramFileDownloaderAdministrator.ICreate,
    });
  typia.assert(administrator);

  // 2. Storage usage creation with valid data
  const createBody1 = {
    enduser_id: typia.random<string & tags.Format<"uuid">>(),
    developer_id: typia.random<string & tags.Format<"uuid">>(),
    storage_bytes_used: 10000,
    file_count: typia.random<
      number & tags.Type<"int32"> & tags.Minimum<0> & tags.Maximum<10000>
    >(),
    quota_max_bytes: typia.random<
      number & tags.Type<"int32"> & tags.Minimum<1000> & tags.Maximum<1000000>
    >(),
  } satisfies ITelegramFileDownloaderStorageUsage.ICreate;

  const storageUsage1: ITelegramFileDownloaderStorageUsage =
    await api.functional.telegramFileDownloader.administrator.storage_usages.create(
      connection,
      {
        body: createBody1,
      },
    );
  typia.assert(storageUsage1);

  TestValidator.equals(
    "Storage usage enduser_id matches",
    storageUsage1.enduser_id,
    createBody1.enduser_id,
  );
  TestValidator.equals(
    "Storage usage developer_id matches",
    storageUsage1.developer_id,
    createBody1.developer_id,
  );
  TestValidator.equals(
