import jwt from "jsonwebtoken";
import { MyGlobal } from "../MyGlobal";
import typia, { tags } from "typia";
import { Prisma } from "@prisma/client";
import { v4 } from "uuid";
import { toISOStringSafe } from "../util/toISOStringSafe";
import { ITelegramFileDownloaderEndUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITelegramFileDownloaderEndUser";
import { EnduserPayload } from "../decorators/payload/EnduserPayload";

/**
 * Register a new endUser account in telegram_file_downloader_endusers with a
 * unique email and hashed password. Automatically issues JWT tokens for initial
 * authentication.
 *
 * This endpoint ensures email uniqueness and securely stores passwords.
 *
 * @param props - Request props containing endUser payload and body with email
 *   and password_hash
 * @returns Authorized endUser information with tokens
 * @throws {Error} When the email is already registered
 */
export async function postauthEndUserJoin(props: {
  endUser: EnduserPayload;
  body: ITelegramFileDownloaderEndUser.ICreate;
}): Promise<ITelegramFileDownloaderEndUser.IAuthorized> {
  const { body } = props;

  const existingUser =
    await MyGlobal.prisma.telegram_file_downloader_endusers.findFirst({
      where: { email: body.email },
    });
  if (existingUser) throw new Error("Email already registered");

  const hashedPassword = await MyGlobal.password.hash(body.password_hash);
  const now = toISOStringSafe(new Date());
  const id = v4() as string & tags.Format<"uuid">;

  const created =
    await MyGlobal.prisma.telegram_file_downloader_endusers.create({
      data: {
        id,
        email: body.email,
        password_hash: hashedPassword,
        created_at: now,
        updated_at: now,
      },
    });

  const accessToken = jwt.sign(
    { id: created.id, email: created.email, type: "enduser" },
    MyGlobal.env.JWT_SECRET_KEY,
    { expiresIn: "1h", issuer: "autobe" },
  );

  const refreshToken = jwt.sign(
    { id: created.id, tokenType: "refresh" },
    MyGlobal.env.JWT_SECRET_KEY,
    { expiresIn: "7d", issuer: "autobe" },
  );

  const expiredAt = toISOStringSafe(new Date(Date.now() + 3600 * 1000));
  const refreshableUntil = toISOStringSafe(
    new Date(Date.now() + 7 * 24 * 3600 * 1000),
  );

  return {
    id: created.id,
    email: created.email as string & tags.Format<"email">,
    password_hash: created.password_hash,
    created_at: toISOStringSafe(created.created_at),
    updated_at: toISOStringSafe(created.updated_at),
    deleted_at: created.deleted_at ? toISOStringSafe(created.deleted_at) : null,
    token: {
      access: accessToken,
      refresh: refreshToken,
      expired_at: expiredAt,
      refreshable_until: refreshableUntil,
    },
  };
}

import jwt from "jsonwebtoken";
import { MyGlobal } from "../MyGlobal";
import typia, { tags } from "typia";
import { Prisma } from "@prisma/client";
import { v4 } from "uuid";
import { toISOStringSafe } from "../util/toISOStringSafe";
import { ITelegramFileDownloaderEndUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITelegramFileDownloaderEndUser";
import { EnduserPayload } from "../decorators/payload/EnduserPayload";

/**
 * User login API for end users enabling authentication with email and password.
 * Validates credentials against telegram_file_downloader_endusers data and
 * issues JWT tokens for session management.
 *
 * This endpoint is publicly accessible and allows members to obtain access
 * tokens necessary for subsequent API calls and dashboard interactions.
 *
 * @param props - Object containing the authenticated endUser payload and login
 *   body
 * @param props.endUser - The authenticated end user making the request (ignored
 *   here)
 * @param props.body - The login request body containing email and password
 * @returns Promise resolving to authorized user data with issued tokens
 * @throws {Error} Throws if credentials are invalid
 */
export async function postauthEndUserLogin(props: {
  endUser: EnduserPayload;
  body: ITelegramFileDownloaderEndUser.ILogin;
}): Promise<ITelegramFileDownloaderEndUser.IAuthorized> {
  const { body } = props;

  let user;
  try {
    user =
      await MyGlobal.prisma.telegram_file_downloader_endusers.findFirstOrThrow({
        where: {
          email: body.email,
          deleted_at: null,
        },
      });
  } catch {
    throw new Error("Invalid credentials");
  }

  const isPasswordValid = await MyGlobal.password.verify(
    body.password,
    user.password_hash,
  );
  if (!isPasswordValid) throw new Error("Invalid credentials");

  typia.assertGuard<string & tags.Format<"email">>(user.email);

  const accessToken = jwt.sign(
    {
      id: user.id,
      type: "enduser",
    },
    MyGlobal.env.JWT_SECRET_KEY,
    {
      expiresIn: "1h",
      issuer: "autobe",
    },
  );

  const refreshToken = jwt.sign(
    {
      id: user.id,
      type: "enduser",
      tokenType: "refresh",
    },
    MyGlobal.env.JWT_SECRET_KEY,
    {
      expiresIn: "7d",
      issuer: "autobe",
    },
  );

  return {
    id: user.id,
    email: user.email,
    password_hash: user.password_hash,
    created_at: toISOStringSafe(user.created_at),
    updated_at: toISOStringSafe(user.updated_at),
    deleted_at: user.deleted_at ? toISOStringSafe(user.deleted_at) : null,
    token: {
      access: accessToken,
      refresh: refreshToken,
      expired_at: toISOStringSafe(new Date(Date.now() + 60 * 60 * 1000)),
      refreshable_until: toISOStringSafe(
        new Date(Date.now() + 7 * 24 * 60 * 60 * 1000),
      ),
    },
  };
}

import jwt from "jsonwebtoken";
import { MyGlobal } from "../MyGlobal";
import typia, { tags } from "typia";
import { Prisma } from "@prisma/client";
import { v4 } from "uuid";
import { toISOStringSafe } from "../util/toISOStringSafe";
import { ITelegramFileDownloaderEndUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITelegramFileDownloaderEndUser";
import { EnduserPayload } from "../decorators/payload/EnduserPayload";

/**
 * Refresh JWT tokens for authenticated endUser sessions.
 *
 * This function validates the provided refresh token, ensures the associated
 * end user exists and is active, then issues new access and refresh tokens. The
 * new tokens preserve the original payload structure used during login,
 * allowing seamless session continuation without re-authentication.
 *
 * @param props - Object containing the authenticated end user payload and the
 *   refresh token body.
 * @param props.endUser - Authenticated end user payload (not used directly for
 *   authorization in this function as refresh token is primary).
 * @param props.body - Contains the refresh token string for renewal.
 * @returns Newly authorized end user information including updated JWT tokens.
 * @throws {Error} When the refresh token is invalid or expired.
 * @throws {Error} When the associated user record is not found.
 */
export async function postauthEndUserRefresh(props: {
  endUser: EnduserPayload;
  body: ITelegramFileDownloaderEndUser.IRefresh;
}): Promise<ITelegramFileDownloaderEndUser.IAuthorized> {
  const { body } = props;
  // Step 1: Verify refresh token and decode
  const decoded = jwt.verify(body.refresh_token, MyGlobal.env.JWT_SECRET_KEY, {
    issuer: "autobe",
  }) as {
    userId: string;
  };

  // Step 2: Fetch user from DB
  const user =
    await MyGlobal.prisma.telegram_file_downloader_endusers.findUnique({
      where: { id: decoded.userId },
    });

  if (!user) {
    throw new Error("User not found");
  }

  // Step 3: Generate new tokens and expirations
  const now = Date.now();
  const accessTokenExpiryMs = 1 * 60 * 60 * 1000; // 1 hour
  const refreshTokenExpiryMs = 7 * 24 * 60 * 60 * 1000; // 7 days
  const accessTokenExpiresAt = toISOStringSafe(
    new Date(now + accessTokenExpiryMs),
  );
  const refreshTokenExpiresAt = toISOStringSafe(
    new Date(now + refreshTokenExpiryMs),
  );

  const accessToken = jwt.sign(
    {
      userId: user.id,
      email: user.email,
    },
    MyGlobal.env.JWT_SECRET_KEY,
    {
      expiresIn: "1h",
      issuer: "autobe",
    },
  );

  const refreshToken = jwt.sign(
    {
      userId: user.id,
      tokenType: "refresh",
    },
    MyGlobal.env.JWT_SECRET_KEY,
    {
      expiresIn: "7d",
      issuer: "autobe",
    },
  );

  // Step 4: Construct and return authorized response
  return {
    id: user.id,
    email: user.email as string & tags.Format<"email">,
    password_hash: user.password_hash,
    created_at: toISOStringSafe(user.created_at),
    updated_at: toISOStringSafe(user.updated_at),
    deleted_at: user.deleted_at ? toISOStringSafe(user.deleted_at) : null,
    token: {
      access: accessToken,
      refresh: refreshToken,
      expired_at: accessTokenExpiresAt,
      refreshable_until: refreshTokenExpiresAt,
    },
  };
}

import jwt from "jsonwebtoken";
import { MyGlobal } from "../MyGlobal";
import typia, { tags } from "typia";
import { Prisma } from "@prisma/client";
import { v4 } from "uuid";
import { toISOStringSafe } from "../util/toISOStringSafe";
import { ITelegramFileDownloaderEndUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITelegramFileDownloaderEndUser";
import { EnduserPayload } from "../decorators/payload/EnduserPayload";

/**
 * Reset password for endUser account securely
 *
 * Password reset operation for end users to securely change their password
 * after verification. Relies on telegram_file_downloader_endusers password_hash
 * field for updating credentials. Supports member-kind users in maintaining
 * account security via password recovery.
 *
 * @param props - Object containing endUser authentication info and body with
 *   reset data
 * @param props.endUser - The authenticated endUser payload (not used directly
 *   in this operation, but required for authentication context)
 * @param props.body - The reset password request body containing email and
 *   new_password
 * @returns Confirmation of password reset success or throws error if user not
 *   found
 * @throws {Error} When endUser with specified email does not exist
 */
export async function postauthEndUserPasswordReset(props: {
  endUser: EnduserPayload;
  body: ITelegramFileDownloaderEndUser.IResetPassword;
}): Promise<ITelegramFileDownloaderEndUser.IResetPasswordResponse> {
  const { body } = props;

  // Find the user by email and ensure not soft deleted
  const user =
    await MyGlobal.prisma.telegram_file_downloader_endusers.findFirst({
      where: { email: body.email, deleted_at: null },
    });

  if (!user) {
    throw new Error(`EndUser with email ${body.email} not found`);
  }

  // Hash the new password before storing
  const hashedPassword = await MyGlobal.password.hash(body.new_password);

  // Update the user's password_hash field
  await MyGlobal.prisma.telegram_file_downloader_endusers.update({
    where: { id: user.id },
    data: { password_hash: hashedPassword },
  });

  return {
    success: true,
    message: "Password has been reset successfully.",
  };
}

import jwt from "jsonwebtoken";
import { MyGlobal } from "../MyGlobal";
import typia, { tags } from "typia";
import { Prisma } from "@prisma/client";
import { v4 } from "uuid";
import { toISOStringSafe } from "../util/toISOStringSafe";
import { ITelegramFileDownloaderEndUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITelegramFileDownloaderEndUser";
import { EnduserPayload } from "../decorators/payload/EnduserPayload";

/**
 * Change password for authenticated end user securely.
 *
 * This provider function handles verification of the current password and
 * updates the stored password hash. It ensures the user is active and
 * authenticated, then performs a safe password hash update.
 *
 * @param props - Object containing authenticated end user and password change
 *   body
 * @param props.endUser - The authenticated end user payload identifying the
 *   user
 * @param props.body - Object containing current_password and new_password
 *   fields
 * @returns Result indicating success or failure of the password change
 *   operation
 * @throws {Error} When the user does not exist or is soft deleted
 * @throws {Error} When the current password provided is incorrect
 */
export async function postauthEndUserPasswordChange(props: {
  endUser: EnduserPayload;
  body: ITelegramFileDownloaderEndUser.IChangePassword;
}): Promise<ITelegramFileDownloaderEndUser.IChangePasswordResponse> {
  const { endUser, body } = props;

  // Locate the active end user record
  const user =
    await MyGlobal.prisma.telegram_file_downloader_endusers.findFirst({
      where: {
        id: endUser.id,
        deleted_at: null,
      },
    });

  if (!user) throw new Error("User not found");

  // Validate the current password against stored hash
  const isValidCurrent = await MyGlobal.password.verify(
    body.current_password,
    user.password_hash,
  );
  if (!isValidCurrent) throw new Error("Current password incorrect");

  // Compute the new password hash
  const newHash = await MyGlobal.password.hash(body.new_password);

  // Record current timestamp for updated_at
  const now = toISOStringSafe(new Date());

  // Update the user's password hash and updated timestamp
  await MyGlobal.prisma.telegram_file_downloader_endusers.update({
    where: { id: endUser.id },
    data: {
      password_hash: newHash,
      updated_at: now,
    },
  });

  // Return success response
  return {
    success: true,
    message: "Password changed successfully",
  };
}

import jwt from "jsonwebtoken";
import { MyGlobal } from "../MyGlobal";
import typia, { tags } from "typia";
import { Prisma } from "@prisma/client";
import { v4 } from "uuid";
import { toISOStringSafe } from "../util/toISOStringSafe";
import { ITelegramFileDownloaderDeveloper } from "@ORGANIZATION/PROJECT-api/lib/structures/ITelegramFileDownloaderDeveloper";

/**
 * Registers a new developer user for the Telegram File Downloader service.
 *
 * This public API endpoint creates a new developer account by accepting an
 * email and password hash. It enforces unique email addresses and returns a
 * fully-authorized developer user object including JWT access and refresh
 * tokens.
 *
 * @param props - Object containing the request body with developer registration
 *   details
 * @param props.body.email - Unique email address for the developer
 * @param props.body.password_hash - Hashed password for authentication
 * @returns The authorized developer user including JWT tokens
 * @throws {Error} When the email is already registered
 */
export async function postauthDeveloperJoin(props: {
  body: ITelegramFileDownloaderDeveloper.ICreate;
}): Promise<ITelegramFileDownloaderDeveloper.IAuthorized> {
  const { body } = props;
  const now = toISOStringSafe(new Date());

  // Check for duplicate email
  const existing =
    await MyGlobal.prisma.telegram_file_downloader_developers.findUnique({
      where: { email: body.email },
    });
  if (existing) throw new Error("Email already registered");

  // Create new developer user
  const created =
    await MyGlobal.prisma.telegram_file_downloader_developers.create({
      data: {
        id: v4() as string & tags.Format<"uuid">,
        email: body.email,
        password_hash: body.password_hash,
        created_at: now,
        updated_at: now,
      },
    });

  // Generate JWT tokens
  const access_token = jwt.sign(
    {
      id: created.id,
      type: "developer",
      email: created.email,
    },
    MyGlobal.env.JWT_SECRET_KEY,
    { expiresIn: "1h", issuer: "autobe" },
  );

  const refresh_token = jwt.sign(
    {
      id: created.id,
      token_type: "refresh",
    },
    MyGlobal.env.JWT_SECRET_KEY,
    { expiresIn: "7d", issuer: "autobe" },
  );

  // Tokens expiration timestamps
  const expired_at = toISOStringSafe(new Date(Date.now() + 3600 * 1000));
  const refreshable_until = toISOStringSafe(
    new Date(Date.now() + 7 * 24 * 3600 * 1000),
  );

  return {
    id: created.id,
    email: created.email,
    password_hash: created.password_hash,
    created_at: created.created_at,
    updated_at: created.updated_at,
    deleted_at: created.deleted_at ?? null,
    token: {
      access: access_token,
      refresh: refresh_token,
      expired_at,
      refreshable_until,
    },
  };
}

import jwt from "jsonwebtoken";
import { MyGlobal } from "../MyGlobal";
