import { tags } from "typia";

/**
 * Audit logs capturing detailed records of system actions including user,
 * admin, moderator, and system events for compliance and forensic analysis.
 *
 * Each log entry is immutable and includes metadata such as IP address and user
 * agent for traceability.
 *
 * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
 */
export type ICommunityAiAuditHistory = {
  /** Primary Key. */
  id: string & tags.Format<"uuid">;

  /**
   * Acting member user.
   *
   * Optional foreign key referencing community_ai_members.id indicating the
   * user performing the action.
   */
  user_id?: (string & tags.Format<"uuid">) | null | undefined;

  /**
   * Admin user.
   *
   * Optional foreign key referencing community_ai_admins.id indicating the
   * admin performing the action.
   */
  admin_id?: (string & tags.Format<"uuid">) | null | undefined;

  /**
   * Moderator user.
   *
   * Optional foreign key referencing community_ai_moderators.id indicating
   * the moderator performing the action.
   */
  moderator_id?: (string & tags.Format<"uuid">) | null | undefined;

  /**
   * Related system setting.
   *
   * Optional foreign key referencing community_ai_system_settings.id
   * indicating the system setting involved in the action.
   */
  system_setting_id?: (string & tags.Format<"uuid">) | null | undefined;

  /** Type of action performed, e.g., 'login', 'post_create', 'comment_delete'. */
  action_type: string;

  /** Detailed description of the action performed. */
  action_description?: string | null | undefined;

  /** IP address from which the action originated. */
  ip_address?: string | null | undefined;

  /** User agent string of the client performing the action. */
  user_agent?: string | null | undefined;

  /** Timestamp when the audit log entry was created. */
  created_at: string & tags.Format<"date-time">;
};
