  id String @id @db.Uuid

  /// Unique code representing the evaluation cycle.
  cycle_code String

  /// Descriptive name of the evaluation cycle.
  cycle_name String

  /// Starting datetime of the evaluation cycle period.
  start_date DateTime @db.Timestamptz

  /// Ending datetime of the evaluation cycle period.
  end_date DateTime @db.Timestamptz

  /// Flag indicating whether this evaluation cycle is currently active.
  is_active Boolean

  /// Record creation timestamp.
  created_at DateTime @db.Timestamptz

