    start_date: now.toISOString(),
    end_date: oneMonthLater.toISOString(),
    is_active: true,
  } satisfies IJobPerformanceEvalEvaluationCycle.ICreate;

  const cycle: IJobPerformanceEvalEvaluationCycle =
