import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IJobPerformanceEvalEvaluationScore } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalEvaluationScore";
import type { IJobPerformanceEvalManager } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalManager";
import type { IJobPerformanceEvalManagerEvaluation } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalManagerEvaluation";
import type { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";
import type { IPageIJobPerformanceEvalEvaluationScore } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIJobPerformanceEvalEvaluationScore";

/**
 * This scenario tests the successful retrieval of a paginated and filtered list
 * of job performance evaluation scores by a manager user. It involves the
 * following steps:
 *
 * 1. Create a new manager account with proper email, password, and name.
 * 2. Login as the created manager to establish authentication context and receive
 *    JWT tokens.
 * 3. Using the manager's id, create at least one manager evaluation record that
 *    associates a specific evaluation cycle and employee with scoring details.
 * 4. Use the evaluation_id from the created manager evaluation to request a
 *    paginated list of evaluation scores, filtered by evaluation_id, one or two
 *    categories (e.g., 'work_performance' or 'knowledge_skill'), and by score
 *    ranges (e.g., minimum and maximum scores).
 * 5. Request pagination settings with page and limit, and include order_by and
 *    order_direction for sorting the resulting score list.
 * 6. Verify the paginated response structure includes valid pagination info and a
 *    list of evaluation score data matching the filtering criteria.
 * 7. Use typia.assert to check response types and TestValidator functions to
 *    validate correctness of the returned data.
 *
 * All API endpoints are accessed with authentication context handled by the
 * SDK's login process. The test excludes attempts to send invalid data or omit
 * required fields, focusing exclusively on a successful, valid request and
 * response cycle with accurate filtering and pagination.
 *
 * The scenario uses correct DTOs for each operation:
 * IJobPerformanceEvalManager.ICreate for manager join,
 * IJobPerformanceEvalManager.ILogin for login,
 * IJobPerformanceEvalManagerEvaluation.ICreate for manager evaluation creation,
 * and IJobPerformanceEvalEvaluationScore.IRequest for the evaluation scores
 * search request.
 */
export async function test_api_evaluation_scores_manager_search_success(
  connection: api.IConnection,
) {
  // 1. Create a manager user
  const createManagerBody = {
    email: `manager${Date.now()}@example.com`,
    password: "password1234",
    name: RandomGenerator.name(),
  } satisfies IJobPerformanceEvalManager.ICreate;
  const managerAuthorized: IJobPerformanceEvalManager.IAuthorized =
    await api.functional.auth.manager.join(connection, {
      body: createManagerBody,
    });
  typia.assert(managerAuthorized);

  // 2. Login manager
  const loginBody = {
    email: createManagerBody.email,
    password: createManagerBody.password,
  } satisfies IJobPerformanceEvalManager.ILogin;
  const managerLoggedIn: IJobPerformanceEvalManager.IAuthorized =
    await api.functional.auth.manager.login(connection, {
      body: loginBody,
    });
  typia.assert(managerLoggedIn);

  // 3. Create a manager evaluation
  const managerEvaluationBody = {
    manager_id: managerAuthorized.id,
    employee_id: typia.random<string & tags.Format<"uuid">>(),
    evaluation_cycle_id: typia.random<string & tags.Format<"uuid">>(),
    evaluation_date: new Date().toISOString(),
    work_performance_score: RandomGenerator.pick([1, 2, 3, 4, 5]),
    knowledge_skill_score: RandomGenerator.pick([1, 2, 3, 4, 5]),
    problem_solving_collab_score: RandomGenerator.pick([1, 2, 3, 4, 5]),
    innovation_score: RandomGenerator.pick([1, 2, 3, 4, 5]),
    overall_comment: RandomGenerator.paragraph({ sentences: 3 }),
  } satisfies IJobPerformanceEvalManagerEvaluation.ICreate;
  const managerEvaluation: IJobPerformanceEvalManagerEvaluation =
    await api.functional.jobPerformanceEval.manager.managerEvaluations.create(
      connection,
      {
        body: managerEvaluationBody,
      },
    );
  typia.assert(managerEvaluation);

  // 4. Prepare evaluation scores search body
  const evaluationScoresSearchBody = {
    evaluation_id: managerEvaluation.id,
    category: RandomGenerator.pick([
      "work_performance",
      "knowledge_skill",
      "problem_solving_collab",
      "innovation",
    ]),
    min_score: 1,
    max_score: 5,
    page: 1,
    limit: 10,
    order_by: "score",
    order_direction: "desc" as "asc" | "desc",
  } satisfies IJobPerformanceEvalEvaluationScore.IRequest;

  // 5. Call evaluationScores.index with filter and pagination
  const evaluationScoresResponse: IPageIJobPerformanceEvalEvaluationScore =
    await api.functional.jobPerformanceEval.manager.evaluationScores.index(
      connection,
      {
        body: evaluationScoresSearchBody,
      },
    );
  typia.assert(evaluationScoresResponse);

  // 6. Validation of response
  TestValidator.predicate(
    "pagination current page should equal requested page",
    evaluationScoresResponse.pagination.current ===
      evaluationScoresSearchBody.page,
  );
  TestValidator.predicate(
    "pagination limit should equal requested limit",
    evaluationScoresResponse.pagination.limit ===
      evaluationScoresSearchBody.limit,
  );
  TestValidator.predicate(
    "all evaluation scores should have matching evaluation_id",
    evaluationScoresResponse.data.every(
      (score) =>
        score.evaluation_id === evaluationScoresSearchBody.evaluation_id,
    ),
  );
  TestValidator.predicate(
    "all evaluation scores should have category matching requested category",
    evaluationScoresResponse.data.every(
      (score) => score.category === evaluationScoresSearchBody.category,
    ),
  );
  TestValidator.predicate(
    "all evaluation scores scores should be between min_score and max_score",
    evaluationScoresResponse.data.every(
      (score) =>
        score.score >= (evaluationScoresSearchBody.min_score ?? 1) &&
        score.score <= (evaluationScoresSearchBody.max_score ?? 5),
    ),
  );

  // Check ordering desc by score
  for (let i = 1; i < evaluationScoresResponse.data.length; i++) {
    TestValidator.predicate(
      `score at position ${i - 1} >= score at position ${i}`,
      evaluationScoresResponse.data[i - 1].score >=
        evaluationScoresResponse.data[i].score,
    );
  }
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IJobPerformanceEvalEmployee } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalEmployee";
import type { IJobPerformanceEvalTask } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalTask";
import type { IJobPerformanceEvalTaskActivity } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalTaskActivity";

/**
 * Test for retrieving detailed task activity information for an employee user.
 *
 * This test covers the following workflow:
 *
 * 1. Employee user registration and authentication.
 * 2. Creation of a task group is assumed; since taskGroup creation API is not
 *    provided, we simulate a valid UUID for taskGroupId.
 * 3. Creation of a task under a valid task group.
 * 4. Creation of a task activity under that task.
 * 5. Retrieval of detailed information for the created task activity using the GET
 *    endpoint.
 * 6. Verification of retrieved data consistency and conformance.
 *
 * Negative tests include:
 *
 * 1. Attempt to retrieve a task activity with a non-existent taskActivityId,
 *    expecting an error.
 * 2. Attempt to retrieve a task activity without authentication, expecting an
 *    unauthorized error.
 */
export async function test_api_employee_task_activity_get_detail_success(
  connection: api.IConnection,
) {
  // 1. Employee user registration
  const employeeCreateBody = {
    email: `${RandomGenerator.alphaNumeric(10)}@example.com`,
    password_hash: RandomGenerator.alphaNumeric(32),
    name: RandomGenerator.name(),
  } satisfies IJobPerformanceEvalEmployee.ICreate;

  const employee = await api.functional.auth.employee.join.joinEmployee(
    connection,
    { body: employeeCreateBody },
  );
  typia.assert(employee);

  // We must have a taskGroupId for task creation; no creation API, so simulate UUID
  const taskGroupId = typia.random<string & tags.Format<"uuid">>();

  // 2. Create a task under the task group
  const taskCreateBody = {
    task_group_id: taskGroupId,
    code: RandomGenerator.alphaNumeric(8),
    name: RandomGenerator.name(),
    description: RandomGenerator.paragraph({ sentences: 5 }),
    knowledge_area_id: null,
  } satisfies IJobPerformanceEvalTask.ICreate;

  const task =
    await api.functional.jobPerformanceEval.employee.taskGroups.tasks.create(
      connection,
      {
        taskGroupId: taskGroupId,
        body: taskCreateBody,
      },
    );
  typia.assert(task);

  // 3. Create a task activity under the task
  const taskActivityCreateBody = {
    task_id: task.id,
    code: RandomGenerator.alphaNumeric(6),
    name: RandomGenerator.name(),
    description: RandomGenerator.content({ paragraphs: 2 }),
  } satisfies IJobPerformanceEvalTaskActivity.ICreate;

  const taskActivity =
    await api.functional.jobPerformanceEval.employee.tasks.taskActivities.create(
      connection,
      {
        taskId: task.id,
        body: taskActivityCreateBody,
      },
    );
  typia.assert(taskActivity);

  // 4. Retrieve task activity details
  const retrieved =
    await api.functional.jobPerformanceEval.employee.tasks.taskActivities.at(
      connection,
      {
        taskId: task.id,
        taskActivityId: taskActivity.id,
      },
    );

  typia.assert(retrieved);

  // Validate important fields are correct
  TestValidator.equals(
    "Task activity ID matches",
    retrieved.id,
    taskActivity.id,
  );
  TestValidator.equals(
    "Task activity taskId matches",
    retrieved.task_id,
    task.id,
  );
  TestValidator.equals(
    "Task activity code matches",
    retrieved.code,
    taskActivity.code,
  );
  TestValidator.equals(
    "Task activity name matches",
    retrieved.name,
    taskActivity.name,
  );
  TestValidator.equals(
    "Task activity description matches",
    retrieved.description,
    taskActivity.description,
  );
  TestValidator.equals(
    "Task activity created_at presence",
    typeof retrieved.created_at,
    "string",
  );
  TestValidator.equals(
    "Task activity updated_at presence",
    typeof retrieved.updated_at,
    "string",
  );

  // deleted_at may be undefined or null - verify type accordingly
  if (retrieved.deleted_at !== undefined && retrieved.deleted_at !== null) {
    TestValidator.equals(
      "Task activity deleted_at presence",
      typeof retrieved.deleted_at,
      "string",
    );
  }

  // 5. Negative test: attempt retrieving a non-existing task activity ID
  await TestValidator.error(
    "retrieving non-existent task activity should fail",
    async () => {
      await api.functional.jobPerformanceEval.employee.tasks.taskActivities.at(
        connection,
        {
          taskId: task.id,
          taskActivityId: typia.random<string & tags.Format<"uuid">>(),
        },
      );
    },
  );

  // 6. Negative test: attempt retrieving task activity without authentication
  // Create a new connection with empty headers (no auth)
  const unauthenticatedConnection: api.IConnection = {
    ...connection,
    headers: {},
  };

  await TestValidator.error(
    "retrieving task activity without auth should fail",
    async () => {
      await api.functional.jobPerformanceEval.employee.tasks.taskActivities.at(
        unauthenticatedConnection,
        {
          taskId: task.id,
          taskActivityId: taskActivity.id,
        },
      );
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IJobPerformanceEvalEmployee } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalEmployee";
import type { IJobPerformanceEvalTaskActivity } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalTaskActivity";

export async function test_api_employee_task_activity_create_success(
  connection: api.IConnection,
) {
  // 1. Create and authenticate employee user
  const employeeEmail = typia.random<string & tags.Format<"email">>();
  const passwordHash = RandomGenerator.alphaNumeric(64);

  const newEmployee: IJobPerformanceEvalEmployee.ICreate = {
    email: employeeEmail,
    password_hash: passwordHash,
    name: RandomGenerator.name(),
  };

  const authorizedEmployee =
    await api.functional.auth.employee.join.joinEmployee(connection, {
      body: newEmployee,
    });
  typia.assert(authorizedEmployee);

  // 2. Prepare task activity creation data
  const taskId = typia.random<string & tags.Format<"uuid">>();
  const code = RandomGenerator.alphaNumeric(10);
  const name = RandomGenerator.name();

  const useDescription = RandomGenerator.pick([true, false]);
  const description = useDescription
    ? RandomGenerator.paragraph({ sentences: 3, wordMin: 5, wordMax: 10 })
    : null;

  const createBody = {
    task_id: taskId,
    code,
    name,
    description,
  } satisfies IJobPerformanceEvalTaskActivity.ICreate;

  // 3. Call create task activity API with authenticated connection
  const createdActivity =
    await api.functional.jobPerformanceEval.employee.tasks.taskActivities.create(
      connection,
      {
        taskId: taskId,
        body: createBody,
      },
    );
  typia.assert(createdActivity);

  // 4. Validate response fields
  TestValidator.predicate(
    "createdActivity has non-empty id",
    createdActivity.id.length > 0,
  );
  TestValidator.equals("task_id matches", createdActivity.task_id, taskId);
  TestValidator.equals("code matches", createdActivity.code, code);
  TestValidator.equals("name matches", createdActivity.name, name);

  if (
    createdActivity.description === null ||
    createdActivity.description === undefined
  ) {
    TestValidator.equals(
      "description is null",
      createdActivity.description,
      null,
    );
  } else {
    TestValidator.predicate(
      "description is non-empty string",
      typeof createdActivity.description === "string" &&
        createdActivity.description.length > 0,
    );
  }

  TestValidator.predicate(
    "created_at is valid ISO 8601 date-time string",
    !isNaN(Date.parse(createdActivity.created_at)),
  );
  TestValidator.predicate(
    "updated_at is valid ISO 8601 date-time string",
    !isNaN(Date.parse(createdActivity.updated_at)),
  );

  TestValidator.predicate(
    "deleted_at is null or undefined or valid ISO string",
    createdActivity.deleted_at === null ||
      createdActivity.deleted_at === undefined ||
      (typeof createdActivity.deleted_at === "string" &&
        !isNaN(Date.parse(createdActivity.deleted_at))),
  );

  // 5. Negative test: unauthenticated call must throw error
  const unauthConn: api.IConnection = { ...connection, headers: {} };

  await TestValidator.error("unauthenticated create throws error", async () => {
    await api.functional.jobPerformanceEval.employee.tasks.taskActivities.create(
      unauthConn,
      {
        taskId: taskId,
        body: createBody,
      },
    );
  });
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IJobPerformanceEvalManager } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalManager";
import type { IJobPerformanceEvalTaskActivity } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalTaskActivity";

/**
 * Test the successful creation of a task activity by an authenticated manager
 * user.
 *
 * This involves first creating a manager user account via the joinManager
 * authentication endpoint, establishing authentication. Then, creating a task
 * activity associated with a valid task UUID. The response is validated to
 * confirm the task activity was properly created with matching properties and
 * timestamps.
 *
 * Steps:
 *
 * 1. Create and authenticate a manager user.
 * 2. Generate a valid taskId for the task activity.
 * 3. Create a task activity with valid code, name, and optional description.
 * 4. Validate response type and contents against the request.
 */
export async function test_api_manager_task_activity_create_success(
  connection: api.IConnection,
) {
  // 1. Create a manager user and authenticate
  const email = typia.random<string & tags.Format<"email">>();
  const password = "Password123!";
  const name = RandomGenerator.name();
  const manager: IJobPerformanceEvalManager.IAuthorized =
    await api.functional.auth.manager.join(connection, {
      body: {
        email: email,
        password: password,
        name: name,
      } satisfies IJobPerformanceEvalManager.ICreate,
    });
  typia.assert(manager);

  // 2. Generate a valid taskId as UUID
  const taskId = typia.random<string & tags.Format<"uuid">>();

  // 3. Compose a new task activity creation request
  const code = RandomGenerator.alphaNumeric(10);
  const taskActivityCreate = {
    task_id: taskId,
    code: code,
    name: RandomGenerator.name(),
    description: RandomGenerator.paragraph({ sentences: 3 }),
  } satisfies IJobPerformanceEvalTaskActivity.ICreate;

  // 4. Call the create task activity endpoint
  const response: IJobPerformanceEvalTaskActivity =
    await api.functional.jobPerformanceEval.manager.tasks.taskActivities.create(
      connection,
      {
        taskId: taskId,
        body: taskActivityCreate,
      },
    );
  typia.assert(response);

  // 5. Validate response data conforms to request
  TestValidator.predicate(
    "response contains id",
    typeof response.id === "string" && response.id.length > 0,
  );
  TestValidator.equals(
    "response task_id matches request",
    response.task_id,
    taskActivityCreate.task_id,
  );
  TestValidator.equals("response code matches request", response.code, code);
  TestValidator.equals(
    "response name matches request",
    response.name,
    taskActivityCreate.name,
  );
  TestValidator.equals(
    "response description matches request",
    response.description ?? null,
    taskActivityCreate.description ?? null,
  );
  TestValidator.predicate(
    "response has created_at",
    typeof response.created_at === "string" && response.created_at.length > 0,
  );
  TestValidator.predicate(
    "response has updated_at",
    typeof response.updated_at === "string" && response.updated_at.length > 0,
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IJobPerformanceEvalEmployee } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalEmployee";
import type { IJobPerformanceEvalTask } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalTask";
import type { IJobPerformanceEvalTaskActivity } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalTaskActivity";

/**
 * Validate the update operation for an employee's task activity.
 *
 * This test covers the full happy path workflow of:
 *
 * 1. Creating a new employee user via joinEmployee.
 * 2. Creating a task within a task group to obtain a valid taskId.
 * 3. Creating a task activity under the created task.
 * 4. Updating the created task activity with new code, name, and description.
 * 5. Validating that the update is persisted correctly by checking the
 *    response.
 * 6. Testing that updates fail without authentication or with invalid IDs.
 *
 * The test ensures:
 *
 * - Correct use of role-based authentication.
 * - Proper parameter usage for path variables and request bodies.
 * - Compliance with DTO schemas and business rules.
 * - Robustness with both success and failure paths.
 */
export async function test_api_employee_task_activity_update_success(
  connection: api.IConnection,
) {
  // Step 1: Employee registration and authentication
  const employeeCreateBody = {
    email: `${RandomGenerator.name(1)}@example.com`,
    password_hash: RandomGenerator.alphaNumeric(32),
    name: RandomGenerator.name(),
  } satisfies IJobPerformanceEvalEmployee.ICreate;
  const employee: IJobPerformanceEvalEmployee.IAuthorized =
    await api.functional.auth.employee.join.joinEmployee(connection, {
      body: employeeCreateBody,
    });
  typia.assert(employee);

  // Step 2: Create a task group ID for creating a task (simulate UUID)
  const taskGroupId = typia.random<string & tags.Format<"uuid">>();

  // Step 3: Create a task under the task group
  const taskCreateBody = {
    task_group_id: taskGroupId,
    code: RandomGenerator.alphaNumeric(8),
    name: RandomGenerator.name(),
    description: RandomGenerator.paragraph({ sentences: 5 }),
    knowledge_area_id: null,
  } satisfies IJobPerformanceEvalTask.ICreate;

  const task: IJobPerformanceEvalTask =
    await api.functional.jobPerformanceEval.employee.taskGroups.tasks.create(
      connection,
      {
        taskGroupId,
        body: taskCreateBody,
      },
    );
  typia.assert(task);

  // Step 4: Create a task activity under the created task
  const taskActivityCreateBody = {
    task_id: task.id,
    code: RandomGenerator.alphaNumeric(6),
    name: RandomGenerator.name(),
    description: RandomGenerator.paragraph({ sentences: 3 }),
  } satisfies IJobPerformanceEvalTaskActivity.ICreate;

  const taskActivity: IJobPerformanceEvalTaskActivity =
    await api.functional.jobPerformanceEval.employee.tasks.taskActivities.create(
      connection,
      {
        taskId: task.id,
        body: taskActivityCreateBody,
      },
    );
  typia.assert(taskActivity);

  // Step 5: Update the task activity with new values
  const taskActivityUpdateBody = {
    code: RandomGenerator.alphaNumeric(6),
    name: RandomGenerator.name(),
    description: RandomGenerator.paragraph({ sentences: 4 }),
    task_id: task.id,
  } satisfies IJobPerformanceEvalTaskActivity.IUpdate;

  const updatedTaskActivity: IJobPerformanceEvalTaskActivity =
    await api.functional.jobPerformanceEval.employee.tasks.taskActivities.update(
      connection,
      {
        taskId: task.id,
        taskActivityId: taskActivity.id,
        body: taskActivityUpdateBody,
      },
    );
  typia.assert(updatedTaskActivity);

  // Step 6: Validate that the returned update matches the update request
  TestValidator.equals(
    "task activity code updated",
    updatedTaskActivity.code,
    taskActivityUpdateBody.code,
  );
  TestValidator.equals(
    "task activity name updated",
    updatedTaskActivity.name,
    taskActivityUpdateBody.name,
  );
  TestValidator.equals(
    "task activity description updated",
    updatedTaskActivity.description,
    taskActivityUpdateBody.description,
  );

  // Step 7: Negative tests to ensure update fails without authentication or with invalid ID

  // Emulate unauthenticated connection by cloning with empty headers
  const unauthenticatedConnection: api.IConnection = {
    ...connection,
    headers: {},
  };

  // Expect failure when calling update without authentication
  await TestValidator.error("update fails without authentication", async () => {
    await api.functional.jobPerformanceEval.employee.tasks.taskActivities.update(
      unauthenticatedConnection,
      {
        taskId: task.id,
        taskActivityId: taskActivity.id,
        body: taskActivityUpdateBody,
      },
    );
  });

  // Expect failure with invalid taskActivityId
  await TestValidator.error(
    "update fails with invalid taskActivityId",
    async () => {
      await api.functional.jobPerformanceEval.employee.tasks.taskActivities.update(
        connection,
        {
          taskId: task.id,
          taskActivityId: typia.random<string & tags.Format<"uuid">>(),
          body: taskActivityUpdateBody,
        },
      );
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IJobPerformanceEvalEmployee } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalEmployee";
import type { IJobPerformanceEvalJobGroup } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalJobGroup";
import type { IJobPerformanceEvalJobRole } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalJobRole";
import type { IJobPerformanceEvalJobSeries } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalJobSeries";
import type { IJobPerformanceEvalManager } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalManager";

/**
 * Test successful deletion of a job role by an employee user.
 *
 * This test performs a full workflow including:
 *
 * 1. Creating and authenticating an employee user
 * 2. Creating and authenticating a manager user
 * 3. Manager creates a job group
 * 4. Manager creates a job series under the job group
 * 5. Manager creates a job role under the job series
 * 6. Switching authentication context back to employee user
 * 7. Employee user deletes the job role
 *
 * This verifies role-based authorization, proper resource management, and
 * successful API operation for deletion.
 *
 * All API responses are asserted with typia.assert. Authentication tokens are
 * handled automatically by SDK. The deletion is successful if no error is
 * thrown during the erase call.
 */
export async function test_api_job_performance_eval_employee_job_series_job_roles_erase_success(
  connection: api.IConnection,
) {
  // 1. Create and authenticate employee user
  const employeePassword = RandomGenerator.alphaNumeric(16);
  const employeeCreateBody = {
    email: `employee_${RandomGenerator.alphaNumeric(8)}@example.com`,
    password_hash: employeePassword, // For test, use as plain password string but pass as hash field
    name: RandomGenerator.name(),
  } satisfies IJobPerformanceEvalEmployee.ICreate;

  const employeeAuth: IJobPerformanceEvalEmployee.IAuthorized =
    await api.functional.auth.employee.join.joinEmployee(connection, {
      body: employeeCreateBody,
    });
  typia.assert(employeeAuth);

  // 2. Create and authenticate manager user
  const managerPassword = RandomGenerator.alphaNumeric(16);
  const managerCreateBody = {
    email: `manager_${RandomGenerator.alphaNumeric(8)}@example.com`,
    password: managerPassword,
    name: RandomGenerator.name(),
  } satisfies IJobPerformanceEvalManager.ICreate;

  const managerAuth: IJobPerformanceEvalManager.IAuthorized =
    await api.functional.auth.manager.join(connection, {
      body: managerCreateBody,
    });
  typia.assert(managerAuth);

  // 3. Manager creates job group
  const jobGroupCreateBody = {
    code: RandomGenerator.alphaNumeric(6).toUpperCase(),
    name: RandomGenerator.name(2),
    description: RandomGenerator.paragraph({ sentences: 3 }),
  } satisfies IJobPerformanceEvalJobGroup.ICreate;

  const jobGroup: IJobPerformanceEvalJobGroup =
    await api.functional.jobPerformanceEval.manager.jobGroups.create(
      connection,
      {
        body: jobGroupCreateBody,
      },
    );
  typia.assert(jobGroup);

  // Generate a UUID string for jobGroupId since IJobPerformanceEvalJobGroup has no id
  const jobGroupId = typia.random<string & tags.Format<"uuid">>();

  // 4. Manager creates job series under job group
  const jobSeriesCreateBody = {
    job_group_id: jobGroupId,
    code: RandomGenerator.alphaNumeric(6).toUpperCase(),
    name: RandomGenerator.name(2),
    description: RandomGenerator.paragraph({ sentences: 3 }),
  } satisfies IJobPerformanceEvalJobSeries.ICreate;

  const jobSeries: IJobPerformanceEvalJobSeries =
    await api.functional.jobPerformanceEval.manager.jobGroups.jobSeries.create(
      connection,
      {
        jobGroupId: jobGroupId,
        body: jobSeriesCreateBody,
      },
    );
  typia.assert(jobSeries);

  // 5. Manager creates job role under job series
  const jobRoleCreateBody = {
    job_series_id: jobSeries.id,
    code: RandomGenerator.alphaNumeric(6).toUpperCase(),
    name: RandomGenerator.name(2),
    description: RandomGenerator.paragraph({ sentences: 3 }),
    growth_level: null,
  } satisfies IJobPerformanceEvalJobRole.ICreate;

  const jobRole: IJobPerformanceEvalJobRole =
    await api.functional.jobPerformanceEval.manager.jobSeries.jobRoles.create(
      connection,
      {
        jobSeriesId: jobSeries.id,
        body: jobRoleCreateBody,
      },
    );
  typia.assert(jobRole);

  // 6. Switch authentication back to employee user
  await api.functional.auth.employee.login.loginEmployee(connection, {
    body: {
      email: employeeAuth.email,
      password: employeePassword,
    } satisfies IJobPerformanceEvalEmployee.ILogin,
  });

  // 7. Employee deletes the job role
  await api.functional.jobPerformanceEval.employee.jobSeries.jobRoles.erase(
    connection,
    {
      jobSeriesId: jobSeries.id,
      jobRoleId: jobRole.id,
    },
  );

  // If no error, deletion is successful
  TestValidator.predicate("job role successfully deleted", true);
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IJobPerformanceEvalEmployee } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalEmployee";
import type { IJobPerformanceEvalManager } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalManager";
import type { IJobPerformanceEvalTaskActivity } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalTaskActivity";

/**
 * Validate update of a task activity by a manager user including valid data
 * update and permission error when unauthorized.
 *
 * This test covers the following scenario:
 *
 * 1. Register a manager user and authenticate
 * 2. Register an employee user for permission testing
 * 3. Manager updates a task activity’s fields (task_id, code, name, description)
 *    with valid data and verifies response
 * 4. Employee user attempts the same update and is expected to receive an
 *    authorization error
 */
export async function test_api_task_activity_update_manager_valid_and_permission_error(
  connection: api.IConnection,
) {
  // Step 1: Manager registers and authenticates
  const managerEmail = typia.random<string & tags.Format<"email">>();
  const managerPassword = "1234";

  const manager: IJobPerformanceEvalManager.IAuthorized =
    await api.functional.auth.manager.join(connection, {
      body: {
        email: managerEmail,
        password: managerPassword,
        name: RandomGenerator.name(),
      } satisfies IJobPerformanceEvalManager.ICreate,
    });
  typia.assert(manager);

  // Step 2: Employee registers and authenticates
  const employeeEmail = typia.random<string & tags.Format<"email">>();
  const employeePassword = "1234";

  const employee: IJobPerformanceEvalEmployee.IAuthorized =
    await api.functional.auth.employee.join.joinEmployee(connection, {
      body: {
        email: employeeEmail,
        password_hash: employeePassword,
        name: RandomGenerator.name(),
      } satisfies IJobPerformanceEvalEmployee.ICreate,
    });
  typia.assert(employee);

  // Generate valid UUIDs for the task and task activity
  const taskId = typia.random<string & tags.Format<"uuid">>();
  const taskActivityId = typia.random<string & tags.Format<"uuid">>();

  // Step 3: Manager updates a task activity with valid data
  const updateBody = {
    task_id: taskId,
    code: RandomGenerator.alphaNumeric(8),
    name: RandomGenerator.name(2),
    description: RandomGenerator.paragraph({
      sentences: 4,
      wordMin: 2,
      wordMax: 5,
    }),
  } satisfies IJobPerformanceEvalTaskActivity.IUpdate;

  const updatedTaskActivity: IJobPerformanceEvalTaskActivity =
    await api.functional.jobPerformanceEval.manager.tasks.taskActivities.update(
      connection,
      {
        taskId,
        taskActivityId,
        body: updateBody,
      },
    );
  typia.assert(updatedTaskActivity);

  TestValidator.equals(
    "updated task_id matches",
    updatedTaskActivity.task_id,
    updateBody.task_id,
  );
  TestValidator.equals(
    "updated code matches",
    updatedTaskActivity.code,
    updateBody.code,
  );
  TestValidator.equals(
    "updated name matches",
    updatedTaskActivity.name,
    updateBody.name,
  );
  TestValidator.equals(
    "updated description matches",
    updatedTaskActivity.description,
    updateBody.description,
  );

  // Step 4: Employee (non-manager) attempts the same update and expects error
  await TestValidator.error(
    "employee cannot update task activity - permission denied",
    async () => {
      await api.functional.jobPerformanceEval.manager.tasks.taskActivities.update(
        connection,
        {
          taskId,
          taskActivityId,
          body: updateBody,
        },
      );
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IJobPerformanceEvalManager } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalManager";
import type { IJobPerformanceEvalTask } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalTask";

export async function test_api_task_create_success_with_manager_auth(
  connection: api.IConnection,
) {
  // 1. Manager join and authenticate
  const managerEmail: string = typia.random<string & tags.Format<"email">>();
  const managerCreateBody = {
    email: managerEmail,
    password: "StrongP@ssw0rd!",
    name: RandomGenerator.name(),
  } satisfies IJobPerformanceEvalManager.ICreate;
  const authorizedManager = await api.functional.auth.manager.join(connection, {
    body: managerCreateBody,
  });
  typia.assert(authorizedManager);

  // 2. Prepare new task creation request
  // Generate a realistic UUID as taskGroupId
  const taskGroupId: string & tags.Format<"uuid"> = typia.random<
    string & tags.Format<"uuid">
  >();
  const newTaskBody = {
    task_group_id: taskGroupId,
    code: RandomGenerator.alphaNumeric(8),
    name: RandomGenerator.paragraph({
      sentences: 3,
      wordMin: 4,
      wordMax: 8,
    }).trim(),
    description: RandomGenerator.paragraph({
      sentences: 6,
      wordMin: 5,
      wordMax: 12,
    }),
  } satisfies IJobPerformanceEvalTask.ICreate;

  // 3. Create the task via API
  const createdTask =
    await api.functional.jobPerformanceEval.manager.taskGroups.tasks.create(
      connection,
      {
        taskGroupId,
        body: newTaskBody,
      },
    );

  // Assert the response matches expected type
  typia.assert(createdTask);

  // 4. Verify created task properties
  TestValidator.equals(
    "task_group_id matches input",
    createdTask.task_group_id,
    taskGroupId,
  );
  TestValidator.equals(
    "code matches input",
    createdTask.code,
    newTaskBody.code,
  );
  TestValidator.equals(
    "name matches input",
    createdTask.name,
    newTaskBody.name,
  );
  TestValidator.equals(
    "description matches input",
    createdTask.description ?? null,
    newTaskBody.description ?? null,
  );
  TestValidator.predicate(
    "id is uuid format",
    /^[0-9a-f]{8}-[0-9a-f]{4}-[1-5][0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i.test(
      createdTask.id,
    ),
  );
  TestValidator.predicate(
    "created_at is valid ISO 8601",
    !Number.isNaN(Date.parse(createdTask.created_at)),
  );
  TestValidator.predicate(
    "updated_at is valid ISO 8601",
    !Number.isNaN(Date.parse(createdTask.updated_at)),
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IJobPerformanceEvalEmployee } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalEmployee";
import type { IJobPerformanceEvalTask } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalTask";

export async function test_api_task_update_success_with_employee_auth(
  connection: api.IConnection,
) {
  // 1. Perform employee join to create and authenticate employee user
  const employeeBody: IJobPerformanceEvalEmployee.ICreate = {
    email: `employee.${RandomGenerator.alphaNumeric(5)}@company.com`,
    password_hash: RandomGenerator.alphaNumeric(32),
    name: RandomGenerator.name(),
  };
  const employee: IJobPerformanceEvalEmployee.IAuthorized =
    await api.functional.auth.employee.join.joinEmployee(connection, {
      body: employeeBody,
    });
  typia.assert(employee);

  // 2. Prepare update data for a task
  const updateBody: IJobPerformanceEvalTask.IUpdate = {
    code: `TASK-${RandomGenerator.alphaNumeric(6).toUpperCase()}`,
    name: RandomGenerator.paragraph({ sentences: 3 }),
    description: RandomGenerator.content({
      paragraphs: 1,
      sentenceMin: 4,
      sentenceMax: 8,
    }),
  };

  // 3. Provide existing valid UUIDs for taskGroupId and taskId
  const taskGroupId = typia.random<string & tags.Format<"uuid">>();
  const taskId = typia.random<string & tags.Format<"uuid">>();

  // 4. Perform task update under employee auth context
  const updatedTask: IJobPerformanceEvalTask =
    await api.functional.jobPerformanceEval.employee.taskGroups.tasks.update(
      connection,
      {
        taskGroupId,
        taskId,
        body: updateBody,
      },
    );
  typia.assert(updatedTask);

  // 5. Validate that updated values are correctly reflected
  TestValidator.equals("code matches", updatedTask.code, updateBody.code);
  TestValidator.equals("name matches", updatedTask.name, updateBody.name);
  TestValidator.equals(
    "description matches",
    updatedTask.description,
    updateBody.description,
  );

  // 6. Validate the task IDs match input
  TestValidator.equals(
    "taskGroupId matches",
    updatedTask.task_group_id,
    taskGroupId,
  );
  TestValidator.equals("taskId matches", updatedTask.id, taskId);

  // 7. Validate timestamps are proper ISO 8601 strings and updated_at is not older than created_at
  typia.assert<string & tags.Format<"date-time">>(updatedTask.created_at);
  typia.assert<string & tags.Format<"date-time">>(updatedTask.updated_at);
  const createdAt = new Date(updatedTask.created_at);
  const updatedAt = new Date(updatedTask.updated_at);
  TestValidator.predicate(
    "updated_at is same or after created_at",
    updatedAt.getTime() >= createdAt.getTime(),
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IJobPerformanceEvalManager } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalManager";
import type { IJobPerformanceEvalTask } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalTask";

/**
 * Verify the 'update' endpoint for tasks under a manager's task group.
 *
 * The test follows these steps:
 *
 * 1. Create and authenticate a manager user.
 * 2. Generate realistic update data for the task.
 * 3. Invoke the update API endpoint with task group and task identifiers.
 * 4. Validate the returned updated task object measurement with typia.assert.
 * 5. Verify that updated fields match the input data, including null handling.
 *
 * This test confirms that the update operation works as expected with valid
 * manager authentication and proper input.
 */
export async function test_api_task_update_success_with_manager_auth(
  connection: api.IConnection,
) {
  // Step 1: Manager registration and authentication
  const managerCreateBody = {
    email: typia.random<string & tags.Format<"email">>(),
    password: RandomGenerator.alphaNumeric(12),
    name: RandomGenerator.name(),
  } satisfies IJobPerformanceEvalManager.ICreate;
  const manager: IJobPerformanceEvalManager.IAuthorized =
    await api.functional.auth.manager.join(connection, {
      body: managerCreateBody,
    });
  typia.assert(manager);

  // For testing, generate realistic UUIDs for taskGroupId and taskId
  const taskGroupId: string & tags.Format<"uuid"> = typia.random<
    string & tags.Format<"uuid">
  >();
  const taskId: string & tags.Format<"uuid"> = typia.random<
    string & tags.Format<"uuid">
  >();

  // Step 2: Prepare update data for the task
  const updateBody = {
    code: RandomGenerator.alphaNumeric(8),
    name: RandomGenerator.name(),
    // Provide a descriptive paragraph for description
    description: RandomGenerator.paragraph({
      sentences: 4,
      wordMin: 5,
      wordMax: 10,
    }),
  } satisfies IJobPerformanceEvalTask.IUpdate;

  // Step 3: Call the update API
  const updatedTask: IJobPerformanceEvalTask =
    await api.functional.jobPerformanceEval.manager.taskGroups.tasks.update(
      connection,
      {
        taskGroupId,
        taskId,
        body: updateBody,
      },
    );

  // Step 4: Validate the response type
  typia.assert(updatedTask);

  // Step 5: Verify updated fields match input (description nullable, check null equivalence)
  TestValidator.equals("updated task code", updatedTask.code, updateBody.code!);
