  const employeeResponse: IJobPerformanceEvalEmployee.IAuthorized =
    await api.functional.auth.employee.join.joinEmployee(connection, {
      body: {
        email: employeeEmail,
        password_hash: "HashedPassword!",
        name: "Employee One",
      } satisfies IJobPerformanceEvalEmployee.ICreate,
    });
