  } satisfies IJobPerformanceEvalEmployee.ILogin;
  const employeeLogin: IJobPerformanceEvalEmployee.IAuthorized =
    await api.functional.auth.employee.login.loginEmployee(connection, {
      body: employeeLoginBody,
    });
