import jwt from "jsonwebtoken";
import { MyGlobal } from "../MyGlobal";
import typia, { tags } from "typia";
import { Prisma } from "@prisma/client";
import { v4 } from "uuid";
import { toISOStringSafe } from "../util/toISOStringSafe";
import { IJobPerformanceEvalEmployee } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalEmployee";

/**
 * Registers a new employee user in the Job Performance Evaluation system.
 *
 * This endpoint creates an employee account with a unique email and hashed
 * password, ensuring no duplicates exist. Upon successful creation, it issues
 * JWT access and refresh tokens to enable authenticated API access.
 *
 * @param props - Contains the request body with employee creation data.
 * @param props.body - Employee creation payload including email, hashed
 *   password, and name.
 * @returns The authorized employee data including tokens and timestamps.
 * @throws {Error} If the email is already registered.
 */
export async function postauthEmployeeJoin(props: {
  body: IJobPerformanceEvalEmployee.ICreate;
}): Promise<IJobPerformanceEvalEmployee.IAuthorized> {
  const { body } = props;

  // Check if email is already used
  const existing =
    await MyGlobal.prisma.job_performance_eval_employees.findUnique({
      where: { email: body.email },
    });
  if (existing) throw new Error(`Email '${body.email}' is already registered`);

  // Generate new UUID and timestamps
  const id = v4() as string & tags.Format<"uuid">;
  const now = toISOStringSafe(new Date());

  // Create new employee record
  const created = await MyGlobal.prisma.job_performance_eval_employees.create({
    data: {
      id,
      email: body.email,
      password_hash: body.password_hash,
      name: body.name,
      created_at: now,
      updated_at: now,
      deleted_at: null,
    },
  });

  // Generate JWT tokens expiry strings
  const tokenExpiresAt = toISOStringSafe(new Date(Date.now() + 1000 * 60 * 60)); // 1 hour from now
  const refreshExpiresAt = toISOStringSafe(
    new Date(Date.now() + 1000 * 60 * 60 * 24 * 7),
  ); // 7 days from now

  const accessToken = jwt.sign(
    {
      userId: created.id,
      email: created.email,
    },
    MyGlobal.env.JWT_SECRET_KEY,
    {
      expiresIn: "1h",
      issuer: "autobe",
    },
  );

  const refreshToken = jwt.sign(
    {
      userId: created.id,
      tokenType: "refresh",
    },
    MyGlobal.env.JWT_SECRET_KEY,
    {
      expiresIn: "7d",
      issuer: "autobe",
    },
  );

  return {
    id: created.id,
    email: created.email,
    password_hash: created.password_hash,
    name: created.name,
    created_at: created.created_at,
    updated_at: created.updated_at,
    deleted_at: created.deleted_at ?? null,
    access_token: accessToken,
    refresh_token: refreshToken,
    expires_at: tokenExpiresAt,
    token: {
      access: accessToken,
      refresh: refreshToken,
      expired_at: tokenExpiresAt,
      refreshable_until: refreshExpiresAt,
    },
  };
}

import jwt from "jsonwebtoken";
import { MyGlobal } from "../MyGlobal";
import typia, { tags } from "typia";
import { Prisma } from "@prisma/client";
import { v4 } from "uuid";
import { toISOStringSafe } from "../util/toISOStringSafe";
import { IJobPerformanceEvalEmployee } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalEmployee";

/**
 * Authenticate employee user via email and password, issuing JWT tokens upon
 * success.
 *
 * This function validates the provided credentials against the
 * job_performance_eval_employees table. It throws an error if the credentials
 * are invalid or the account is deleted. Upon successful authentication, it
 * generates JWT access and refresh tokens with appropriate expiry and returns
 * the full employee authorization data structure.
 *
 * @param props - Object containing the login request body.
 * @param props.body - Contains email and password for authentication.
 * @returns Employee's authorized information with JWT tokens.
 * @throws {Error} If email does not exist, is soft deleted, or password
 *   verification fails.
 */
export async function postauthEmployeeLogin(props: {
  body: IJobPerformanceEvalEmployee.ILogin;
}): Promise<IJobPerformanceEvalEmployee.IAuthorized> {
  const { body } = props;

  const employee =
    await MyGlobal.prisma.job_performance_eval_employees.findUnique({
      where: { email: body.email },
    });

  if (!employee || employee.deleted_at !== null) {
    throw new Error("Invalid email or password");
  }

  const valid = await MyGlobal.password.verify(
    body.password,
    employee.password_hash,
  );
  if (!valid) {
    throw new Error("Invalid email or password");
  }

  const now = toISOStringSafe(new Date());
  const expiresAt = toISOStringSafe(new Date(Date.now() + 3600000));
  const refreshableUntil = toISOStringSafe(new Date(Date.now() + 604800000));

  const accessToken = jwt.sign(
    { id: employee.id, type: "employee" },
    MyGlobal.env.JWT_SECRET_KEY,
    {
      expiresIn: "1h",
      issuer: "autobe",
    },
  );

  const refreshToken = jwt.sign(
    { id: employee.id, tokenType: "refresh" },
    MyGlobal.env.JWT_SECRET_KEY,
    {
      expiresIn: "7d",
      issuer: "autobe",
    },
  );

  return {
    id: employee.id,
    email: employee.email,
    password_hash: employee.password_hash,
    name: employee.name,
    created_at: toISOStringSafe(employee.created_at),
    updated_at: toISOStringSafe(employee.updated_at),
    deleted_at: employee.deleted_at
      ? toISOStringSafe(employee.deleted_at)
      : null,
    access_token: accessToken,
    refresh_token: refreshToken,
    expires_at: expiresAt,
    token: {
      access: accessToken,
      refresh: refreshToken,
      expired_at: expiresAt,
      refreshable_until: refreshableUntil,
    },
  };
}

import jwt from "jsonwebtoken";
import { MyGlobal } from "../MyGlobal";
import typia, { tags } from "typia";
import { Prisma } from "@prisma/client";
import { v4 } from "uuid";
import { toISOStringSafe } from "../util/toISOStringSafe";
import { IJobPerformanceEvalEmployee } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalEmployee";

export async function postauthEmployeeRefresh(props: {
  body: IJobPerformanceEvalEmployee.IRefresh;
}): Promise<IJobPerformanceEvalEmployee.IAuthorized> {
  const { refresh_token } = props.body;

  let decoded: { id: string; type: string };
  try {
    decoded = jwt.verify(refresh_token, MyGlobal.env.JWT_SECRET_KEY, {
      issuer: "autobe",
    }) as { id: string; type: string };
  } catch {
    throw new Error("Invalid or expired refresh token");
  }

  if (decoded.type !== "employee") {
    throw new Error("Invalid token type");
  }

  const employee =
    await MyGlobal.prisma.job_performance_eval_employees.findUnique({
      where: { id: decoded.id },
    });

  if (!employee || employee.deleted_at !== null) {
    throw new Error("Employee not found or deleted");
  }

  const accessTokenPayload = {
    id: employee.id,
    email: employee.email,
    name: employee.name,
    type: "employee" as const,
  };

  const access_token = jwt.sign(
    accessTokenPayload,
    MyGlobal.env.JWT_SECRET_KEY,
    {
      expiresIn: "1h",
      issuer: "autobe",
    },
  );

  const refreshTokenPayload = {
    id: employee.id,
    type: "employee" as const,
    token_type: "refresh",
  };

  const refresh_token_new = jwt.sign(
    refreshTokenPayload,
    MyGlobal.env.JWT_SECRET_KEY,
    {
      expiresIn: "7d",
      issuer: "autobe",
    },
  );

  // Calculate expiration timestamps for tokens as ISO strings
  const expires_at = toISOStringSafe(new Date(Date.now() + 3600 * 1000));
  const refreshable_until = toISOStringSafe(
    new Date(Date.now() + 7 * 24 * 3600 * 1000),
  );

  return {
    id: employee.id,
    email: employee.email,
    password_hash: employee.password_hash,
    name: employee.name,
    created_at: toISOStringSafe(employee.created_at),
    updated_at: toISOStringSafe(employee.updated_at),
    deleted_at: employee.deleted_at
      ? toISOStringSafe(employee.deleted_at)
      : null,
    access_token: access_token,
    refresh_token: refresh_token_new,
    expires_at: expires_at,
    token: {
      access: access_token,
      refresh: refresh_token_new,
      expired_at: expires_at,
      refreshable_until: refreshable_until,
    },
  };
}

import jwt from "jsonwebtoken";
import { MyGlobal } from "../MyGlobal";
import typia, { tags } from "typia";
import { Prisma } from "@prisma/client";
import { v4 } from "uuid";
import { toISOStringSafe } from "../util/toISOStringSafe";
import { IJobPerformanceEvalManager } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalManager";
import { ManagerPayload } from "../decorators/payload/ManagerPayload";

/**
 * Registers a new manager account in the Job Performance Evaluation system.
 *
 * This function creates a new manager user after ensuring the provided email is
 * unique among active managers. The user's password is securely hashed before
 * storage.
 *
 * After creation, JWT access and refresh tokens are generated with a 1-hour and
 * 7-day expiration respectively, both issued by 'autobe'.
 *
 * @param props - The input parameters containing manager registration data.
 * @param props.manager - The manager payload (not used in this public join
 *   operation but present).
 * @param props.body - The registration details including email, password, and
 *   name.
 * @returns The newly created manager's authorized information including JWT
 *   tokens.
 * @throws {Error} Throws an error if the email is already in use by an active
 *   manager.
 */
export async function postauthManagerJoin(props: {
  manager: ManagerPayload;
  body: IJobPerformanceEvalManager.ICreate;
}): Promise<IJobPerformanceEvalManager.IAuthorized> {
  const { body } = props;

  // Check if manager with the given email and not deleted already exists
  const existing =
    await MyGlobal.prisma.job_performance_eval_managers.findFirst({
      where: {
        email: body.email,
        deleted_at: null,
      },
    });
  if (existing) {
    throw new Error("DuplicateManagerEmail");
  }

  // Hash the password securely
  const hashedPassword = await MyGlobal.password.hash(body.password);

  // Generate a new UUID v4 for the manager ID
  const newId = v4() as string & tags.Format<"uuid">;

  // Current timestamp in ISO string format
  const now = toISOStringSafe(new Date());

  // Create the new manager record in the database
  const created = await MyGlobal.prisma.job_performance_eval_managers.create({
    data: {
      id: newId,
      email: body.email,
      password_hash: hashedPassword,
      name: body.name,
      created_at: now,
      updated_at: now,
      deleted_at: null,
    },
  });

  // Generate JWT access token with payload
  const accessToken = jwt.sign(
    {
      userId: created.id,
      email: created.email,
    },
    MyGlobal.env.JWT_SECRET_KEY,
    {
      expiresIn: "1h",
      issuer: "autobe",
    },
  );

  // Generate JWT refresh token with payload
  const refreshToken = jwt.sign(
    {
      userId: created.id,
      tokenType: "refresh",
    },
    MyGlobal.env.JWT_SECRET_KEY,
    {
      expiresIn: "7d",
      issuer: "autobe",
    },
  );

  // Calculate expiration timestamps for tokens
  const expiredAt = toISOStringSafe(new Date(Date.now() + 3600 * 1000));
  const refreshableUntil = toISOStringSafe(
    new Date(Date.now() + 7 * 24 * 3600 * 1000),
  );

  // Return the authorized manager data
  return {
    id: created.id as string & tags.Format<"uuid">,
    email: created.email,
    password_hash: created.password_hash,
    name: created.name,
    created_at: created.created_at as string & tags.Format<"date-time">,
    updated_at: created.updated_at as string & tags.Format<"date-time">,
    deleted_at: null,
    token: {
      access: accessToken,
      refresh: refreshToken,
      expired_at: expiredAt,
      refreshable_until: refreshableUntil,
    },
  };
}

import jwt from "jsonwebtoken";
import { MyGlobal } from "../MyGlobal";
import typia, { tags } from "typia";
import { Prisma } from "@prisma/client";
import { v4 } from "uuid";
import { toISOStringSafe } from "../util/toISOStringSafe";
import { IJobPerformanceEvalManager } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalManager";
import { ManagerPayload } from "../decorators/payload/ManagerPayload";

/**
 * Authenticate manager with email and password and issue JWT tokens.
 *
 * This operation validates credentials against the
 * job_performance_eval_managers table. It generates JWT access and refresh
 * tokens upon successful validation.
 *
 * @param props - Object containing manager payload and login credentials
 * @param props.manager - (Unused) Authenticated manager payload, included for
 *   signature consistency
 * @param props.body - Manager login credentials: email and password
 * @returns Authorized manager data including tokens
 * @throws {Error} When the email does not exist or credentials are invalid
 */
export async function postauthManagerLogin(props: {
  manager: ManagerPayload;
  body: IJobPerformanceEvalManager.ILogin;
}): Promise<IJobPerformanceEvalManager.IAuthorized> {
  const { body } = props;

  // Find manager by email and active status
  const manager = await MyGlobal.prisma.job_performance_eval_managers.findFirst(
    {
      where: {
        email: body.email,
        deleted_at: null,
      },
    },
  );

  if (!manager) {
    throw new Error("Invalid email or password");
  }

  // Verify password
  const passwordValid = await MyGlobal.password.verify(
    body.password,
    manager.password_hash,
  );
  if (!passwordValid) {
    throw new Error("Invalid email or password");
  }

  // Calculate token expirations
  const now = new Date();
  const accessExpiresInSeconds = 60 * 60; // 1 hour
  const refreshExpiresInSeconds = 7 * 24 * 60 * 60; // 7 days

  const accessExpiredAt = toISOStringSafe(
    new Date(Date.now() + accessExpiresInSeconds * 1000),
  );
  const refreshableUntil = toISOStringSafe(
    new Date(Date.now() + refreshExpiresInSeconds * 1000),
  );

  // Generate JWT Access Token
  const accessToken = jwt.sign(
    {
      id: manager.id,
      email: manager.email,
    },
    MyGlobal.env.JWT_SECRET_KEY,
    {
      expiresIn: accessExpiresInSeconds,
      issuer: "autobe",
    },
  );

  // Generate JWT Refresh Token
  const refreshToken = jwt.sign(
    {
      id: manager.id,
      tokenType: "refresh",
    },
    MyGlobal.env.JWT_SECRET_KEY,
    {
      expiresIn: refreshExpiresInSeconds,
      issuer: "autobe",
    },
  );

  return {
    id: manager.id,
    email: manager.email,
    password_hash: manager.password_hash,
    name: manager.name,
    created_at: toISOStringSafe(manager.created_at),
    updated_at: toISOStringSafe(manager.updated_at),
    deleted_at: manager.deleted_at
      ? toISOStringSafe(manager.deleted_at)
      : undefined,
    token: {
      access: accessToken,
      refresh: refreshToken,
      expired_at: accessExpiredAt,
      refreshable_until: refreshableUntil,
    },
  };
}

import jwt from "jsonwebtoken";
import { MyGlobal } from "../MyGlobal";
import typia, { tags } from "typia";
import { Prisma } from "@prisma/client";
import { v4 } from "uuid";
import { toISOStringSafe } from "../util/toISOStringSafe";
import { IJobPerformanceEvalManager } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalManager";
import { ManagerPayload } from "../decorators/payload/ManagerPayload";

/**
 * Refresh JWT tokens for manager role using a valid refresh token.
 *
 * This function verifies the provided refresh token, ensures it belongs to a
 * valid manager who is not soft-deleted, and issues new access and refresh
 * tokens. It returns the manager's authorization information including the
 * refreshed tokens.
 *
 * @param props - Object containing the manager identity and refresh token body.
 * @param props.manager - Authenticated manager payload (not used for auth
 *   here).
 * @param props.body - Body containing the refresh token string.
 * @returns The manager authorization data along with new JWT tokens.
 * @throws {Error} If the refresh token is invalid, expired, or does not belong
 *   to a manager.
 * @throws {Error} If the manager does not exist or is soft-deleted.
 */
export async function postauthManagerRefresh(props: {
  manager: ManagerPayload;
  body: IJobPerformanceEvalManager.IRefresh;
}): Promise<IJobPerformanceEvalManager.IAuthorized> {
  const { body } = props;

  // Step 1: Verify and decode the refresh token
  const decoded = jwt.verify(body.refresh_token, MyGlobal.env.JWT_SECRET_KEY, {
    issuer: "autobe",
  }) as { id: string; type: string };

  if (decoded.type !== "manager") {
    throw new Error("Invalid token: not a manager token");
  }

  // Step 2: Retrieve the manager record from the database
  const manager = await MyGlobal.prisma.job_performance_eval_managers.findFirst(
    {
      where: {
        id: decoded.id,
        deleted_at: null,
      },
    },
  );

  if (!manager) throw new Error("Manager not found or deleted");

  // Step 3: Generate new JWT tokens
  const accessTokenPayload = {
    id: manager.id,
    email: manager.email,
    name: manager.name,
    type: "manager",
  };

  const newAccessToken = jwt.sign(
    accessTokenPayload,
    MyGlobal.env.JWT_SECRET_KEY,
    {
      expiresIn: "1h",
      issuer: "autobe",
    },
  );

  const refreshTokenPayload = {
    id: manager.id,
    tokenType: "refresh",
  };

  const newRefreshToken = jwt.sign(
    refreshTokenPayload,
    MyGlobal.env.JWT_SECRET_KEY,
    {
      expiresIn: "7d",
      issuer: "autobe",
    },
  );

  // Step 4: Compute expiration timestamps as ISO strings
  const accessTokenExpiresAt: string & tags.Format<"date-time"> =
    toISOStringSafe(new Date(Date.now() + 3600 * 1000));

  const refreshTokenExpiresAt: string & tags.Format<"date-time"> =
    toISOStringSafe(new Date(Date.now() + 7 * 24 * 3600 * 1000));

  // Step 5: Return the full authorization response object
  return {
    id: manager.id,
    email: manager.email,
    password_hash: manager.password_hash,
    name: manager.name,
    created_at: toISOStringSafe(manager.created_at),
    updated_at: toISOStringSafe(manager.updated_at),
    deleted_at: manager.deleted_at ? toISOStringSafe(manager.deleted_at) : null,
    token: {
      access: newAccessToken,
      refresh: newRefreshToken,
      expired_at: accessTokenExpiresAt,
      refreshable_until: refreshTokenExpiresAt,
    },
  };
}

import jwt from "jsonwebtoken";
import { MyGlobal } from "../MyGlobal";
import typia, { tags } from "typia";
import { Prisma } from "@prisma/client";
import { v4 } from "uuid";
import { toISOStringSafe } from "../util/toISOStringSafe";
import { IJobPerformanceEvalEvaluationCycle } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalEvaluationCycle";
import { IPageIJobPerformanceEvalEvaluationCycle } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIJobPerformanceEvalEvaluationCycle";
import { ManagerPayload } from "../decorators/payload/ManagerPayload";

/**
 * Retrieve filtered, paginated list of job performance evaluation cycles.
 *
 * Enables clients to query evaluation cycles with filters like cycle code,
 * cycle name, date ranges, and active status.
 *
 * Supports soft deletion by excluding records with deleted_at.
 *
 * Pagination and filtering are applied as per request body parameters.
 *
 * Authorization: Requires authenticated manager role.
 *
 * @param props - The manager's authentication and request filter parameters.
 * @param props.manager - Authenticated manager payload.
 * @param props.body - Filtering and pagination criteria.
 * @returns Paginated summary of matched evaluation cycles.
 * @throws Error if database query fails.
 */
export async function patchjobPerformanceEvalManagerEvaluationCycles(props: {
  manager: ManagerPayload;
  body: IJobPerformanceEvalEvaluationCycle.IRequest;
}): Promise<IPageIJobPerformanceEvalEvaluationCycle.ISummary> {
  const { manager, body } = props;

  const page = body.page ?? 1;
  const limit = body.limit ?? 10;
  const skip = (page - 1) * limit;

  const where = {
    deleted_at: null as null,
    ...(body.cycle_code !== undefined ? { cycle_code: body.cycle_code } : {}),
    ...(body.cycle_name !== undefined
      ? { cycle_name: { contains: body.cycle_name } }
      : {}),
    ...(body.start_date_from !== undefined && body.start_date_from !== null
      ? { start_date: { gte: body.start_date_from } }
      : {}),
    ...(body.end_date_to !== undefined && body.end_date_to !== null
      ? { end_date: { lte: body.end_date_to } }
      : {}),
    ...(body.is_active !== undefined && body.is_active !== null
      ? { is_active: body.is_active }
      : {}),
  };

  const [rows, total] = await Promise.all([
    MyGlobal.prisma.job_performance_eval_evaluation_cycles.findMany({
      where,
      select: {
        id: true,
        cycle_code: true,
        cycle_name: true,
        is_active: true,
      },
      skip,
      take: limit,
    }),
    MyGlobal.prisma.job_performance_eval_evaluation_cycles.count({ where }),
  ]);

  return {
    pagination: {
      current: Number(page),
      limit: Number(limit),
      records: total,
      pages: Math.ceil(total / limit),
    },
    data: rows.map((row) => ({
      id: row.id,
      cycle_code: row.cycle_code,
      cycle_name: row.cycle_name,
      is_active: row.is_active,
    })),
  };
}

import jwt from "jsonwebtoken";
import { MyGlobal } from "../MyGlobal";
import typia, { tags } from "typia";
import { Prisma } from "@prisma/client";
import { v4 } from "uuid";
import { toISOStringSafe } from "../util/toISOStringSafe";
import { IJobPerformanceEvalEvaluationCycle } from "@ORGANIZATION/PROJECT-api/lib/structures/IJobPerformanceEvalEvaluationCycle";
import { ManagerPayload } from "../decorators/payload/ManagerPayload";

/**
 * Get evaluation cycle details by ID
 *
 * Fetch detailed information about a single job performance evaluation cycle by
 * its unique identifier.
 *
 * This enables administrative users to view and validate properties such as
 * start and end dates, cycle codes, and active status.
 *
 * Access restrictions apply to prevent unauthorized data exposure. Only
 * managers with valid authorization may access this data.
 *
 * @param props - Object containing the manager payload and the evaluation cycle
 *   ID to fetch
 * @param props.manager - The authenticated manager performing the request
 * @param props.id - Unique identifier of the evaluation cycle
 * @returns Detailed evaluation cycle information conforming to
 *   IJobPerformanceEvalEvaluationCycle
 * @throws {Error} Throws if the evaluation cycle is not found
 */
export async function getjobPerformanceEvalManagerEvaluationCyclesId(props: {
  manager: ManagerPayload;
  id: string & tags.Format<"uuid">;
}): Promise<IJobPerformanceEvalEvaluationCycle> {
  const record =
    await MyGlobal.prisma.job_performance_eval_evaluation_cycles.findUniqueOrThrow(
      {
        where: { id: props.id, deleted_at: null },
      },
    );

  return {
    id: record.id,
    cycle_code: record.cycle_code,
    cycle_name: record.cycle_name,
    start_date: toISOStringSafe(record.start_date),
